-- =============================================
-- Author:      Gartle LLC
-- Release:     3.0, 2022-07-05
-- Description: The view defines automatic handlers
-- =============================================

CREATE VIEW [xls27].[xl_automatic_handlers]
AS

SELECT
    t.TABLE_SCHEMA
    , t.TABLE_NAME
    , t.COLUMN_NAME
    , 'DoNotSave' AS EVENT_NAME
    , CAST(NULL AS nvarchar) AS HANDLER_SCHEMA
    , CAST(NULL AS nvarchar) AS HANDLER_NAME
    , 'ATTRIBUTE' AS HANDLER_TYPE
    , CAST(NULL AS nvarchar) AS HANDLER_CODE
    , CAST(NULL AS nvarchar) AS TARGET_WORKSHEET
    , CAST(NULL AS int) AS MENU_ORDER
    , CAST(NULL AS bit) AS EDIT_PARAMETERS
FROM
    INFORMATION_SCHEMA.COLUMNS t
WHERE
    t.COLUMN_NAME IN ('created_by', 'created_on', 'modified_by', 'modified_on')

UNION ALL
SELECT
    p.SPECIFIC_SCHEMA AS TABLE_SCHEMA
    , p.SPECIFIC_NAME AS TABLE_NAME
    , t.COLUMN_NAME
    , 'DefaultValue' AS EVENT_NAME
    , CAST(NULL AS nvarchar) AS HANDLER_SCHEMA
    , CAST(NULL AS nvarchar) AS HANDLER_NAME
    , 'ATTRIBUTE' AS HANDLER_TYPE
    , t.HANDLER_CODE
    , CAST(NULL AS nvarchar) AS TARGET_WORKSHEET
    , CAST(NULL AS int) AS MENU_ORDER
    , CAST(NULL AS bit) AS EDIT_PARAMETERS
FROM
    (
        SELECT
            TOP 1
            'company_id' AS COLUMN_NAME
            , CAST(company_id AS nvarchar) AS HANDLER_CODE
        FROM
            dbo27.users u
        WHERE
            u.is_default = 1
            AND u.username = USER_NAME()
        UNION ALL
        SELECT
            TOP 1
            'category_id' AS COLUMN_NAME
            , CAST(m.id AS nvarchar) AS HANDLER_CODE
        FROM
            dbo27.members m
            INNER JOIN (
                SELECT TOP 1 company_id FROM dbo27.users u WHERE u.is_default = 1 AND u.username = USER_NAME()
            ) c ON c.company_id = m.company_id
        WHERE
            m.dimension_id = 3
            AND m.is_active = 1
        ORDER BY
            m.sort_order
            , m.id
        UNION ALL
        SELECT
            TOP 1
            'time_id' AS COLUMN_NAME
            , CAST(m.id AS nvarchar) AS HANDLER_CODE
        FROM
            dbo27.members m
            INNER JOIN (
                SELECT TOP 1 company_id FROM dbo27.users u WHERE u.is_default = 1 AND u.username = USER_NAME()
            ) c ON c.company_id = m.company_id
        WHERE
            m.dimension_id = 2
            AND m.is_active = 1
        ORDER BY
            m.sort_order
            , m.id
        UNION ALL
        SELECT
            TOP 1
            'entity_id' AS COLUMN_NAME
            , CAST(m.id AS nvarchar) AS HANDLER_CODE
        FROM
            dbo27.members m
            INNER JOIN (
                SELECT TOP 1 company_id, u.username FROM dbo27.users u WHERE u.is_default = 1 AND u.username = USER_NAME()
            ) c ON c.company_id = m.company_id
            LEFT OUTER JOIN dbo27.member_permissions mp ON mp.member_id = m.id AND mp.username = c.username
        WHERE
            m.dimension_id = 4
            AND m.is_active = 1
        ORDER BY
            mp.update_permission DESC
            , mp.select_permission DESC
            , m.sort_order
            , m.id
        UNION ALL
        SELECT 'year' AS COLUMN_NAME, CAST(YEAR(GETDATE()) AS nvarchar) AS HANDLER_CODE
        UNION ALL
        SELECT 'start_year' AS COLUMN_NAME, CAST(YEAR(GETDATE()) AS nvarchar) AS HANDLER_CODE
        UNION ALL
        SELECT 'end_year' AS COLUMN_NAME, CAST(YEAR(GETDATE()) AS nvarchar) AS HANDLER_CODE
    ) t
    INNER JOIN (
        SELECT
            p.SPECIFIC_SCHEMA
            , p.SPECIFIC_NAME
            , SUBSTRING(p.PARAMETER_NAME, 2, 100) AS PARAMETER_NAME
        FROM
            INFORMATION_SCHEMA.PARAMETERS p
        WHERE
            p.SPECIFIC_SCHEMA = 'xls27'
            AND p.SPECIFIC_NAME LIKE 'usp_%'
            AND NOT RIGHT(p.SPECIFIC_NAME, 7) IN ('_insert', '_update', '_delete', '_change')
            AND (NOT p.PARAMETER_NAME = '@entity_id' OR p.SPECIFIC_NAME IN ('usp_budget_request1', 'usp_budget_request2'))
            AND NOT p.SPECIFIC_NAME IN ('usp_saved_report_types')
    ) p ON p.PARAMETER_NAME = t.COLUMN_NAME


