-- Description: Selects SQL commands to insert actual balances
-- =============================================

CREATE PROCEDURE [dbo27].[usp_export_actual_balances]
    @company nvarchar(50) = NULL
AS
BEGIN

SET NOCOUNT ON;

WITH cte(part, r, command) AS (
    SELECT
        1 AS part
        , ROW_NUMBER() OVER (ORDER BY c.code, a.code) AS r
        , 'EXEC dbo27.usp_import_actual_balances ' +
               + COALESCE('N''' + REPLACE(c.code, '''', '''''') + '''', 'NULL')
        + ', ' + COALESCE('N''' + REPLACE(a.code, '''', '''''') + '''', 'NULL')
        + ', ' + COALESCE('N''' + REPLACE(m.code, '''', '''''') + '''', 'NULL')
        + ', ' + COALESCE(CAST(t.balance AS nvarchar), 'NULL')
        + ', ' + COALESCE(CAST(t.balance$ AS nvarchar), 'NULL')
        + ';' AS command
    FROM
        dbo27.actual_balances t
        INNER JOIN dbo27.ledger_accounts a ON a.id = t.account_id
        LEFT OUTER JOIN dbo27.members m ON m.id = t.subsidiary_account_id
        INNER JOIN dbo27.users u ON u.company_id = a.company_id AND u.username = USER_NAME() AND u.is_active = 1 AND u.is_admin = 1
        INNER JOIN dbo27.companies c ON c.id = a.company_id AND c.code = COALESCE(@company, c.code)
)
SELECT
    command
FROM
    cte
ORDER BY
    part
    , r

END


