;WITH cte(part, r, command) AS (
    SELECT
        1 AS part
        , ROW_NUMBER() OVER(ORDER BY c.code, t.language, m.sort_order, m.code) AS r
        , 'EXEC dbo27.usp_import_translations '
        + 'N''' + REPLACE(c.code, '''', '''''') + ''''
