CREATE PROCEDURE [dbo27].[usp_import_clear_all_data]
    @company nvarchar(50) = NULL
    , @confirm nvarchar(50)
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

DECLARE @message nvarchar(max)

DECLARE @company_id int

SELECT TOP 1 @company_id = c.id FROM dbo27.companies c INNER JOIN dbo27.users u ON u.company_id = c.id  WHERE c.code = @company AND u.username = USER_NAME() AND u.is_active = 1 AND u.is_admin = 1

IF @company_id IS NULL
    BEGIN
    SET @message = dbo27.get_translated_string('Incorrect company_id', @data_language)
    RAISERROR (@message, 11, 0)
    RETURN
    END

IF @confirm IS NULL OR NOT @confirm = 'yes'
    RETURN

BEGIN TRANSACTION

DELETE FROM dbo27.translations                WHERE company_id = @company_id

DELETE FROM dbo27.journals                    WHERE company_id = @company_id

DELETE FROM dbo27.actual_balances            WHERE account_id IN (SELECT id FROM dbo27.ledger_accounts WHERE company_id = @company_id)
DELETE FROM dbo27.actual_currency_rates        WHERE currency_id IN (SELECT id FROM dbo27.units WHERE company_id = @company_id)
DELETE FROM dbo27.actual_closed_periods        WHERE company_id = @company_id

DELETE FROM dbo27.budget_balances            WHERE category_id IN (SELECT id FROM dbo27.members WHERE company_id = @company_id)
DELETE FROM dbo27.saved_reports                WHERE category_id IN (SELECT id FROM dbo27.members WHERE company_id = @company_id)
DELETE FROM dbo27.budget_requests            WHERE category_id IN (SELECT id FROM dbo27.members WHERE company_id = @company_id)
DELETE FROM dbo27.budget_currency_rates        WHERE category_id IN (SELECT id FROM dbo27.members WHERE company_id = @company_id)

DELETE FROM dbo27.saved_report_types        WHERE company_id = @company_id

DELETE FROM dbo27.member_groups                WHERE group_id IN (SELECT id FROM dbo27.subsidiary_groups WHERE company_id = @company_id)
DELETE FROM dbo27.subsidiary_groups            WHERE company_id = @company_id

DELETE FROM dbo27.member_permissions        WHERE member_id IN (SELECT id FROM dbo27.members WHERE company_id = @company_id)
DELETE FROM dbo27.member_relations            WHERE member_id IN (SELECT id FROM dbo27.members WHERE company_id = @company_id)

DELETE FROM dbo27.members                    WHERE company_id = @company_id

DELETE FROM dbo27.ledger_accounts            WHERE company_id = @company_id

DELETE FROM dbo27.report_sections            WHERE company_id = @company_id

DELETE FROM dbo27.vat_rates                    WHERE company_id = @company_id
DELETE FROM dbo27.units                        WHERE company_id = @company_id
DELETE FROM dbo27.users                        WHERE company_id = @company_id

DELETE FROM dbo27.companies                    WHERE id = @company_id

DECLARE @id int

SET @id = COALESCE((SELECT MAX(id) FROM dbo27.actual_balances), 0);

DBCC CHECKIDENT ('dbo27.actual_balances'    , RESEED, @id) WITH NO_INFOMSGS;

SET @id = COALESCE((SELECT MAX(id) FROM dbo27.budget_balances), @id);

DBCC CHECKIDENT ('dbo27.budget_balances'    , RESEED, @id) WITH NO_INFOMSGS;

SET @id = COALESCE((SELECT MAX(id) FROM dbo27.budget_requests), @id);

DBCC CHECKIDENT ('dbo27.budget_requests'    , RESEED, @id) WITH NO_INFOMSGS;

SET @id = COALESCE((SELECT MAX(id) FROM dbo27.companies), @id);

DBCC CHECKIDENT ('dbo27.companies'            , RESEED, @id) WITH NO_INFOMSGS;

SET @id = COALESCE((SELECT MAX(id) FROM dbo27.journals), @id);

DBCC CHECKIDENT ('dbo27.journals'            , RESEED, @id) WITH NO_INFOMSGS;

SET @id = COALESCE((SELECT MAX(id) FROM dbo27.ledger_accounts), @id);

DBCC CHECKIDENT ('dbo27.ledger_accounts'    , RESEED, @id) WITH NO_INFOMSGS;

SET @id = COALESCE((SELECT MAX(id) FROM dbo27.members), @id);

DBCC CHECKIDENT ('dbo27.members'            , RESEED, @id) WITH NO_INFOMSGS;

SET @id = COALESCE((SELECT MAX(id) FROM dbo27.report_sections), @id);

DBCC CHECKIDENT ('dbo27.report_sections'    , RESEED, @id) WITH NO_INFOMSGS;

SET @id = COALESCE((SELECT MAX(id) FROM dbo27.subsidiary_groups), @id);

DBCC CHECKIDENT ('dbo27.subsidiary_groups'    , RESEED, @id) WITH NO_INFOMSGS;

