-- =============================================

CREATE PROCEDURE [dbo27].[usp_import_companies]
    @code nvarchar(50)
    , @name nvarchar(100)
    , @default_language varchar(10) = NULL
    , @sort_order int = NULL
AS
BEGIN

SET NOCOUNT ON

DECLARE @company_id int = (SELECT id FROM dbo27.companies WHERE code = @code)

IF @company_id IS NOT NULL
    BEGIN

    IF NOT EXISTS (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1 AND is_admin = 1)
        RETURN

    UPDATE dbo27.companies
    SET
        name = @name
        , default_language = COALESCE(@default_language, 'en')
        , sort_order = @sort_order
    WHERE
        code = @code

    END
ELSE
    BEGIN
    INSERT INTO dbo27.companies
        (code, name, default_language, sort_order)
    VALUES
        (@code, @name, @default_language, @sort_order)

    SET @company_id = SCOPE_IDENTITY()

    INSERT INTO dbo27.users (company_id, username, name, is_admin, is_active)
    VALUES (@company_id, USER_NAME(), USER_NAME(), 1, 1)

    END

END


