-- =============================================

CREATE PROCEDURE [dbo27].[usp_import_translations]
    @company nvarchar(50)
    , @table nvarchar(50) = NULL
    , @member nvarchar(50) = NULL
    , @language varchar(10)
    , @name nvarchar(100) = NULL
AS
BEGIN

SET NOCOUNT ON

IF @language IS NULL
    RETURN

DECLARE @company_id int

SELECT TOP 1 @company_id = c.id FROM dbo27.companies c INNER JOIN dbo27.users u ON u.company_id = c.id  WHERE c.code = @company AND u.username = USER_NAME() AND u.is_active = 1 AND u.is_admin = 1

IF @company_id IS NULL
    RETURN

DECLARE @table_id tinyint = (SELECT TOP 1 t.id FROM dbo27.translated_tables t WHERE t.code = @table)

DECLARE @member_id int

IF @table_id = 1
    SELECT TOP 1 @member_id = t.id FROM dbo27.members t INNER JOIN dbo27.users u ON u.company_id = t.company_id  WHERE t.company_id = @company_id AND t.code = @member AND u.username = USER_NAME() AND u.is_active = 1
ELSE IF @table_id = 2
    SET @member_id = @company_id
ELSE IF @table_id = 3
    SELECT TOP 1 @member_id = t.id FROM dbo27.units t INNER JOIN dbo27.users u ON u.company_id = t.company_id  WHERE t.company_id = @company_id AND t.code = @member AND u.username = USER_NAME() AND u.is_active = 1
ELSE IF @table_id = 4
    SELECT TOP 1 @member_id = t.id FROM dbo27.vat_rates t INNER JOIN dbo27.users u ON u.company_id = t.company_id  WHERE t.company_id = @company_id AND t.code = @member AND u.username = USER_NAME() AND u.is_active = 1
ELSE IF @table_id = 10
    SELECT TOP 1 @member_id = t.id FROM dbo27.subsidiary_groups t INNER JOIN dbo27.users u ON u.company_id = t.company_id  WHERE t.company_id = @company_id AND t.code = @member AND u.username = USER_NAME() AND u.is_active = 1
ELSE IF @table_id = 11
    SELECT TOP 1 @member_id = t.id FROM dbo27.ledger_accounts t INNER JOIN dbo27.users u ON u.company_id = t.company_id  WHERE t.company_id = @company_id AND t.code = @member AND u.username = USER_NAME() AND u.is_active = 1
ELSE IF @table_id = 12
    SELECT TOP 1 @member_id = t.id FROM dbo27.saved_report_types t INNER JOIN dbo27.users u ON u.company_id = t.company_id  WHERE t.company_id = @company_id AND t.code = @member AND u.username = USER_NAME() AND u.is_active = 1
ELSE
    RETURN

IF @member_id IS NULL
    RETURN

;MERGE dbo27.translations t
USING
    (
    SELECT company_id = @company_id, @table_id AS table_id, @member_id AS member_id, @language AS language, @name AS name
    ) s ON s.company_id = t.company_id AND s.table_id = t.table_id AND s.member_id = t.member_id AND s.language = t.language
WHEN MATCHED AND NOT COALESCE(t.name, '') = COALESCE(s.name, '') THEN
    UPDATE SET name = s.name
WHEN NOT MATCHED THEN
    INSERT (company_id, table_id, member_id, language, name) VALUES (s.company_id, s.table_id, s.member_id, s.language, s.name);

END


GO

