-- Author:      Gartle LLC
-- Release:     3.0, 2022-07-05
-- Description: The procedure selects actual_balances
-- =============================================

CREATE PROCEDURE [xls27].[usp_actual_balances]
    @company_id int = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE username = USER_NAME() AND is_active = 1 AND is_default = 1)
ELSE
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

;WITH parents (id, parent_id) AS (
    SELECT
        a.id
        , a.id
    FROM
        dbo27.ledger_accounts a
        INNER JOIN dbo27.ledger_account_types t ON t.id = a.account_type_id
    WHERE
        a.company_id = @company_id
        AND COALESCE(t.is_pl, 0) = 0

    UNION ALL
    SELECT
        p.id
        , a.parent_id
    FROM
        parents p
        INNER JOIN dbo27.ledger_accounts a ON a.id = p.parent_id
    WHERE
        a.parent_id IS NOT NULL
        AND NOT p.id = a.parent_id
)

SELECT
    t.id
    , t.account_id
    , t.subsidiary_account_id
    , CASE WHEN lat.bs_sign = -1 THEN 0 WHEN s.id IS NOT NULL THEN 1 WHEN a.id IS NULL OR a.subsidiary_group_id IS NOT NULL OR p.parent_id IS NOT NULL THEN 0 ELSE 1 END AS is_debit_editable
    , CASE WHEN lat.bs_sign = 1  THEN 0 WHEN s.id IS NOT NULL THEN 1 WHEN a.id IS NULL OR a.subsidiary_group_id IS NOT NULL OR p.parent_id IS NOT NULL THEN 0 ELSE 1 END AS is_credit_editable
    , CASE WHEN p.parent_id IS NULL THEN 0 ELSE 1 END AS is_parent
    , a.code AS code
    , COALESCE(tr2.name, s.name) AS subsidiary_name
    , COALESCE('    ' + COALESCE(tr2.name, s.name), CASE WHEN a.parent_id IS NULL THEN '' ELSE '  ' END + COALESCE(tr1.name, a.name), 'Total') AS name
    , c.name AS currency
    , CASE WHEN s.id IS NOT NULL THEN t.debit WHEN a.id IS NULL OR a.subsidiary_group_id IS NOT NULL OR p.parent_id IS NOT NULL THEN NULL ELSE t.debit END AS debit
    , CASE WHEN s.id IS NOT NULL THEN t.credit WHEN a.id IS NULL OR a.subsidiary_group_id IS NOT NULL OR p.parent_id IS NOT NULL THEN NULL ELSE t.credit END AS credit
    , t.debit$
    , t.credit$
FROM
    (
        -- Accounts * subsidiary_accounts

        SELECT
            b.id
            , a.id AS account_id
            , s.id AS subsidiary_account_id
            , b.debit
            , b.credit
            , b.debit$
            , b.credit$
        FROM
            dbo27.ledger_accounts a
            INNER JOIN dbo27.member_groups g ON g.group_id = a.subsidiary_group_id
            INNER JOIN dbo27.members s ON s.id = g.member_id
            INNER JOIN dbo27.ledger_account_types t ON t.id = a.account_type_id
            LEFT OUTER JOIN dbo27.actual_balances b ON b.account_id = a.id AND b.subsidiary_account_id = s.id
        WHERE
            a.company_id = @company_id
            AND t.is_pl = 0

        -- Parent actual_balances

        UNION ALL
        SELECT
            NULL AS id
            , p.parent_id AS account_id
            , NULL AS subsidiary_account_id
            , SUM(b.debit) AS debit
            , SUM(b.credit) AS credit
            , SUM(b.debit$) AS debit$
            , SUM(b.credit$) AS credit$
        FROM
            parents p
            LEFT OUTER JOIN dbo27.actual_balances b ON b.account_id = p.id
        GROUP BY
            p.parent_id

        -- Totals

        UNION ALL
        SELECT
            NULL AS id
            , NULL AS account_id
            , NULL AS subsidiary_account_id
            , NULL AS debit
            , NULL AS credit
            , SUM(b.debit$) AS debit$
            , SUM(b.credit$) AS credit$
        FROM
            dbo27.actual_balances b
            INNER JOIN dbo27.ledger_accounts a ON a.id = b.account_id
        WHERE
            a.company_id = @company_id
    ) t
    LEFT OUTER JOIN dbo27.ledger_accounts a ON a.id = t.account_id
    LEFT JOIN dbo27.ledger_account_types lat ON lat.id = a.account_type_id
    LEFT OUTER JOIN dbo27.members s ON s.id = t.subsidiary_account_id
    LEFT OUTER JOIN dbo27.units c ON c.id = s.unit_id
    LEFT OUTER JOIN (
        SELECT DISTINCT a.parent_id FROM dbo27.ledger_accounts a WHERE a.company_id = @company_id
    ) p ON p.parent_id = a.id
    LEFT OUTER JOIN dbo27.translations tr1 ON tr1.company_id = a.company_id AND tr1.table_id = 11 AND tr1.member_id = a.id AND tr1.language = @data_language
    LEFT OUTER JOIN dbo27.translations tr2 ON tr2.company_id = s.company_id AND tr2.table_id = 1 AND tr2.member_id = s.id AND tr2.language = @data_language
ORDER BY
    a.code
    , s.name

END


GO

