-- Release:     3.0, 2022-07-05
-- Description: The procedure deleted data from dbo27.actual_balances
-- =============================================

CREATE PROCEDURE [xls27].[usp_actual_balances_delete]
    @account_id int
    , @subsidiary_account_id int = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

DECLARE @company_id int = NULL

SET @company_id = (SELECT TOP 1 a.company_id FROM dbo27.ledger_accounts a INNER JOIN dbo27.users u ON u.company_id = a.company_id WHERE a.id = @account_id AND u.username = USER_NAME() AND u.is_active = 1)

IF @company_id IS NULL
    RETURN

DECLARE @message nvarchar(max)

IF EXISTS(SELECT TOP 1 p.is_closed FROM dbo27.actual_closed_periods p WHERE p.is_closed = 1 AND p.date < (SELECT MIN(date) FROM dbo27.journals WHERE company_id = @company_id))
    BEGIN
    SET @message = dbo27.get_translated_string('The period is closed for changes', @data_language)
    RAISERROR (@message, 11, 0)
    RETURN
    END

SET NOCOUNT OFF

DELETE FROM dbo27.actual_balances
WHERE
    account_id = @account_id
    AND COALESCE(subsidiary_account_id, -1) = COALESCE(@subsidiary_account_id, -1)

END


GO

