-- Author:      Gartle LLC
-- Release:     3.0, 2022-07-05
-- Description: The procedure updates data of dbo27.actual_balances
-- =============================================

CREATE PROCEDURE [xls27].[usp_actual_balances_update]
    @account_id int
    , @subsidiary_account_id int = NULL
    , @debit float = NULL
    , @credit float = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

IF @account_id IS NULL
    RETURN

DECLARE @company_id int = NULL

SET @company_id = (SELECT TOP 1 a.company_id FROM dbo27.ledger_accounts a INNER JOIN dbo27.users u ON u.company_id = a.company_id WHERE a.id = @account_id AND u.username = USER_NAME() AND u.is_active = 1)

IF @subsidiary_account_id IS NOT NULL AND
    NOT EXISTS (SELECT id FROM dbo27.members WHERE id = @subsidiary_account_id AND company_id = @company_id)
    RETURN

DECLARE @message nvarchar(max)

IF EXISTS(SELECT TOP 1 p.is_closed FROM dbo27.actual_closed_periods p WHERE p.is_closed = 1 AND p.date < (SELECT MIN(date) FROM dbo27.journals WHERE company_id = @company_id))
    BEGIN
    SET @message = dbo27.get_translated_string('The period is closed for changes', @data_language)
    RAISERROR (@message, 11, 0)
    RETURN
    END

DECLARE @currency_id int = (SELECT m.unit_id FROM dbo27.members m INNER JOIN dbo27.units u ON u.id = m.unit_id WHERE m.id = @subsidiary_account_id AND u.is_functional_currency = 0)

DECLARE @balance money = COALESCE(@debit, 0) - COALESCE(@credit, 0)

IF @balance = 0
    BEGIN
    DELETE dbo27.actual_balances
    WHERE
        account_id = @account_id
        AND COALESCE(subsidiary_account_id, -1) = COALESCE(@subsidiary_account_id, -1)
    RETURN
    END

DECLARE @balance$ money = @balance

DECLARE @rate float

IF @currency_id IS NOT NULL
    BEGIN

    SELECT @rate = rate FROM dbo27.actual_currency_rates WHERE currency_id = @currency_id
        AND date = (SELECT MIN(date) FROM dbo27.actual_currency_rates WHERE currency_id = @currency_id)

    SELECT
        @balance$ = ROUND(@balance * CASE WHEN is_base_currency = 0 OR @rate = 0 THEN COALESCE(@rate, 1.0) ELSE 1.0 / COALESCE(@rate, 1.0) END, 2)
    FROM
        dbo27.units
    WHERE
        id = @currency_id

    END

SET NOCOUNT OFF

UPDATE dbo27.actual_balances
SET
    account_id = @account_id
    , subsidiary_account_id = @subsidiary_account_id
    , balance = @balance
    , balance$ = COALESCE(@balance$, @balance)
WHERE
    account_id = @account_id
    AND COALESCE(subsidiary_account_id, -1) = COALESCE(@subsidiary_account_id, -1)

IF @@ROWCOUNT = 0
INSERT INTO dbo27.actual_balances
    ( account_id
    , subsidiary_account_id
    , balance
    , balance$
    )
VALUES
    ( @account_id
    , @subsidiary_account_id
    , @balance
    , COALESCE(@balance$, @balance)
    )

END


GO

