-- Release:     3.0, 2022-07-05
-- Description: Selects rows to set closed periods
-- =============================================

CREATE PROCEDURE [xls27].[usp_actual_closed_periods]
    @company_id int = NULL
AS
BEGIN

SET NOCOUNT ON

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE username = USER_NAME() AND is_active = 1 AND is_default = 1)
ELSE
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

;WITH e1(n) AS
(
    SELECT 0 UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL
    SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9
),
e2(n) AS (SELECT d.n * 10 + e1.n FROM e1 d CROSS JOIN e1),
e3(n) AS (SELECT c.n * 100 + e2.n FROM e1 c CROSS JOIN e2)

SELECT
    DATEADD(MONTH, e3.n, '2019-01-01') AS date
    , CAST(COALESCE(is_closed, 0) AS bit) AS is_closed
FROM
    e3
    LEFT OUTER JOIN dbo27.actual_closed_periods p ON p.date = DATEADD(MONTH, e3.n, '2019-01-01') AND p.company_id = @company_id
WHERE
    e3.n < 132
    AND DATEADD(MONTH, e3.n, '2019-01-01') BETWEEN
        (SELECT DATEADD(MONTH, DATEDIFF(MONTH, 0, MIN(date)), 0) FROM dbo27.journals WHERE company_id = @company_id) AND GETDATE()
ORDER BY
    e3.n

END


