-- =============================================
-- Author:      Gartle LLC
-- Release:     3.0, 2022-07-05
-- Description: The procedure updates data of dbo27.actual_currency_rates
-- =============================================

CREATE PROCEDURE [xls27].[usp_actual_currency_rates_update]
    @currency_id int = NULL
    , @date date = NULL
    , @rate smallmoney = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

DECLARE @company_id int

SELECT TOP 1 @company_id = t.company_id FROM dbo27.units t INNER JOIN dbo27.users u ON u.company_id = t.company_id  WHERE t.id = @currency_id AND u.username = USER_NAME() AND u.is_active = 1

IF @company_id IS NULL
    RETURN

DECLARE @message nvarchar(max)

IF @date IS NULL OR @rate IS NULL
    RETURN

IF NOT EXISTS (SELECT id FROM dbo27.units WHERE id = @currency_id AND is_currency = 1 AND is_functional_currency = 0)
    RETURN

IF (SELECT p.is_closed FROM dbo27.actual_closed_periods p WHERE p.date = DATEADD(D, 1 - DATEPART(d, @date), @date) AND p.company_id = @company_id) = 1
    BEGIN
    SET @message = dbo27.get_translated_string('The period is closed for changes', @data_language)
    RAISERROR (@message, 11, 0)
    RETURN
    END

DECLARE @current_rate smallmoney
DECLARE @is_checked bit

SELECT @current_rate = rate, @is_checked = is_checked FROM dbo27.actual_currency_rates WHERE currency_id = @currency_id AND date = @date

IF @current_rate IS NULL
    BEGIN
    INSERT INTO dbo27.actual_currency_rates (currency_id, date, rate, is_checked)
    VALUES (@currency_id, @date, @rate, 1)
    RETURN
    END
ELSE IF @current_rate = @rate AND @is_checked = 1
    RETURN
ELSE IF @current_rate = @rate
    BEGIN
    UPDATE dbo27.actual_currency_rates
    SET
        is_checked = 1
    WHERE
        currency_id = @currency_id
        AND date = @date
        AND NOT (
            COALESCE(is_checked, 0) = 1
            )
    RETURN
    END

UPDATE dbo27.actual_currency_rates
SET
      rate = @rate
    , is_checked = 1
WHERE
    currency_id = @currency_id
    AND date = @date
    AND NOT (
        COALESCE(rate, -1) = COALESCE(@rate, -2)
        AND COALESCE(is_checked, -1) = 1
        )

DECLARE @is_base_currency bit = (SELECT is_base_currency FROM dbo27.units WHERE id = @currency_id)

DECLARE @rate_factor float = CASE WHEN @is_base_currency = 0 OR @rate = 0 THEN COALESCE(@rate, 1.0) ELSE 1.0 / COALESCE(@rate, 1.0) END

UPDATE dbo27.journals
SET
    amount$ = ROUND(amount * @rate_factor, 2)
FROM
    dbo27.journals j
    LEFT OUTER JOIN dbo27.members ds ON ds.id = j.debit_subsidiary_account_id
    LEFT OUTER JOIN dbo27.members cs ON cs.id = j.credit_subsidiary_account_id
WHERE
    j.date = @date
    AND (ds.unit_id = @currency_id OR cs.unit_id = @currency_id)
    AND j.auto_type_id = 0
    AND NOT (
        COALESCE(amount$, -1) = COALESCE(ROUND(amount * @rate_factor, 2), -2)
        )

END


