-- =============================================
-- Author:      Gartle LLC
-- Release:     3.0, 2022-07-05
-- Description: The procedure selects actual financial reports
-- =============================================

CREATE PROCEDURE [xls27].[usp_actual_reports]
    @company_id int = NULL
    , @year int = NULL
    , @entity_id int = NULL
    , @report_id int = NULL
    , @unit int = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE username = USER_NAME() AND is_active = 1 AND is_default = 1)
ELSE
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

DECLARE @start_date date
DECLARE @end_date date

SET @start_date = DATEFROMPARTS(COALESCE(@year, YEAR(GETDATE())), 1, 1)
SET @end_date = DATEFROMPARTS(COALESCE(@year, YEAR(GETDATE())), 12, 31)

-- Factor like 1, 0.001, or 0.000001

DECLARE @factor float = 1.0 / COALESCE(@unit, 1)

;WITH b1 AS (

    -- This section converts raw records to entry sides and data by month

    SELECT
        CASE WHEN s.id = 1 THEN j.debit_account_id ELSE j.credit_account_id END AS ledger_account_id
        , CASE WHEN s.id = 1 THEN j.debit_subsidiary_account_id ELSE j.credit_subsidiary_account_id END AS subsidiary_account_id
        , CASE WHEN s.id = 1 THEN j.credit_account_id ELSE j.debit_account_id END AS pair_account_id
        , s.id AS entry_side_id
        , NULL AS data00
        , CASE WHEN DATEPART(M, j.date) = 01 THEN j.amount$ ELSE NULL END AS data01
        , CASE WHEN DATEPART(M, j.date) = 02 THEN j.amount$ ELSE NULL END AS data02
        , CASE WHEN DATEPART(M, j.date) = 03 THEN j.amount$ ELSE NULL END AS data03
        , CASE WHEN DATEPART(M, j.date) = 04 THEN j.amount$ ELSE NULL END AS data04
        , CASE WHEN DATEPART(M, j.date) = 05 THEN j.amount$ ELSE NULL END AS data05
        , CASE WHEN DATEPART(M, j.date) = 06 THEN j.amount$ ELSE NULL END AS data06
        , CASE WHEN DATEPART(M, j.date) = 07 THEN j.amount$ ELSE NULL END AS data07
        , CASE WHEN DATEPART(M, j.date) = 08 THEN j.amount$ ELSE NULL END AS data08
        , CASE WHEN DATEPART(M, j.date) = 09 THEN j.amount$ ELSE NULL END AS data09
        , CASE WHEN DATEPART(M, j.date) = 10 THEN j.amount$ ELSE NULL END AS data10
        , CASE WHEN DATEPART(M, j.date) = 11 THEN j.amount$ ELSE NULL END AS data11
        , CASE WHEN DATEPART(M, j.date) = 12 THEN j.amount$ ELSE NULL END AS data12
    FROM
        dbo27.journals j
        CROSS JOIN dbo27.entry_sides s
    WHERE
        j.company_id = @company_id
        AND j.date BETWEEN @start_date AND @end_date
        AND COALESCE(j.entity_id, -1) = COALESCE(@entity_id, j.entity_id, -1)

    -- This section converts raw records to entries and selects balance00, dr, cr on ledger and subsidiary accounts

    UNION ALL
    SELECT
        b.account_id AS ledger_account_id
        , b.subsidiary_account_id
        , NULL AS pair_account_id
        , s.id AS entry_side_id
        , CASE WHEN s.id = 1 AND b.balance$ > 0 THEN b.balance$ WHEN s.id = 2 AND b.balance$ < 0 THEN b.balance$ ELSE NULL END AS data00
        , NULL AS data01, NULL AS data02, NULL AS data03, NULL AS data04, NULL AS data05, NULL AS data06, NULL AS data07, NULL AS data08, NULL AS data09, NULL AS data10, NULL AS data11, NULL AS data12
    FROM
        dbo27.actual_balances b
        INNER JOIN dbo27.ledger_accounts a ON a.id = b.account_id
        CROSS JOIN dbo27.entry_sides s
    WHERE
        a.company_id = @company_id
        AND (s.id = 1 AND balance$ > 0 OR s.id = 2 AND balance$ < 0)
        AND @entity_id IS NULL
)

, c1 AS (

    -- This section aggregates data by a report section, account, subsidiary account, and a value type

    SELECT
        rsa.section_type_id
        , CASE WHEN rst.hide_accounts = 1 THEN NULL ELSE b1.ledger_account_id END ledger_account_id
        , CASE WHEN rsa.value_type_id IN (3, 4) THEN b1.subsidiary_account_id ELSE NULL END AS subsidiary_account_id
        , rsa.value_type_id
        , SUM(rsa.value_sign * data00) AS data00
        , SUM(rsa.value_sign * data01) AS data01
        , SUM(rsa.value_sign * data02) AS data02
        , SUM(rsa.value_sign * data03) AS data03
        , SUM(rsa.value_sign * data04) AS data04
        , SUM(rsa.value_sign * data05) AS data05
        , SUM(rsa.value_sign * data06) AS data06
        , SUM(rsa.value_sign * data07) AS data07
        , SUM(rsa.value_sign * data08) AS data08
        , SUM(rsa.value_sign * data09) AS data09
        , SUM(rsa.value_sign * data10) AS data10
        , SUM(rsa.value_sign * data11) AS data11
        , SUM(rsa.value_sign * data12) AS data12
    FROM
        b1
        INNER JOIN dbo27.ledger_accounts a ON a.id = b1.ledger_account_id
        LEFT OUTER JOIN dbo27.ledger_accounts p ON p.id = b1.pair_account_id
        INNER JOIN dbo27.report_section_accounts rsa ON rsa.account_type_id = a.account_type_id AND rsa.entry_side_id = b1.entry_side_id
        INNER JOIN dbo27.report_section_types rst ON rst.id = rsa.section_type_id
        LEFT OUTER JOIN dbo27.ledger_accounts c ON c.id = b1.pair_account_id
    WHERE
        rst.report_type_id = COALESCE(@report_id, rst.report_type_id)
        AND (rsa.pair_account_type_id IS NULL OR rsa.pair_account_type_id = c.account_type_id)
        AND (p.id IS NULL OR NOT p.account_type_id = 150)
    GROUP BY
        rsa.section_type_id
        , CASE WHEN rst.hide_accounts = 1 THEN NULL ELSE b1.ledger_account_id END
        , CASE WHEN rsa.value_type_id IN (3, 4) THEN b1.subsidiary_account_id END
        , rsa.value_type_id
)

, c2 AS (

    -- This section calculates flow and balance values

    SELECT
        section_type_id
        , ledger_account_id
        , subsidiary_account_id
        , value_type_id
        , CASE WHEN value_type_id = 1 THEN NULL ELSE data00 END AS data00
        , CASE WHEN value_type_id = 1 THEN data01 ELSE COALESCE(data00, 0) + COALESCE(data01, 0) END AS data01
        , CASE WHEN value_type_id = 1 THEN data02 ELSE COALESCE(data00, 0) + COALESCE(data01, 0) + COALESCE(data02, 0) END AS data02
        , CASE WHEN value_type_id = 1 THEN data03 ELSE COALESCE(data00, 0) + COALESCE(data01, 0) + COALESCE(data02, 0) + COALESCE(data03, 0) END AS data03
        , CASE WHEN value_type_id = 1 THEN data04 ELSE COALESCE(data00, 0) + COALESCE(data01, 0) + COALESCE(data02, 0) + COALESCE(data03, 0) + COALESCE(data04, 0) END AS data04
        , CASE WHEN value_type_id = 1 THEN data05 ELSE COALESCE(data00, 0) + COALESCE(data01, 0) + COALESCE(data02, 0) + COALESCE(data03, 0) + COALESCE(data04, 0) + COALESCE(data05, 0) END AS data05
        , CASE WHEN value_type_id = 1 THEN data06 ELSE COALESCE(data00, 0) + COALESCE(data01, 0) + COALESCE(data02, 0) + COALESCE(data03, 0) + COALESCE(data04, 0) + COALESCE(data05, 0) + COALESCE(data06, 0) END AS data06
        , CASE WHEN value_type_id = 1 THEN data07 ELSE COALESCE(data00, 0) + COALESCE(data01, 0) + COALESCE(data02, 0) + COALESCE(data03, 0) + COALESCE(data04, 0) + COALESCE(data05, 0) + COALESCE(data06, 0) + COALESCE(data07, 0) END AS data07
        , CASE WHEN value_type_id = 1 THEN data08 ELSE COALESCE(data00, 0) + COALESCE(data01, 0) + COALESCE(data02, 0) + COALESCE(data03, 0) + COALESCE(data04, 0) + COALESCE(data05, 0) + COALESCE(data06, 0) + COALESCE(data07, 0) + COALESCE(data08, 0) END AS data08
        , CASE WHEN value_type_id = 1 THEN data09 ELSE COALESCE(data00, 0) + COALESCE(data01, 0) + COALESCE(data02, 0) + COALESCE(data03, 0) + COALESCE(data04, 0) + COALESCE(data05, 0) + COALESCE(data06, 0) + COALESCE(data07, 0) + COALESCE(data08, 0) + COALESCE(data09, 0) END AS data09
        , CASE WHEN value_type_id = 1 THEN data10 ELSE COALESCE(data00, 0) + COALESCE(data01, 0) + COALESCE(data02, 0) + COALESCE(data03, 0) + COALESCE(data04, 0) + COALESCE(data05, 0) + COALESCE(data06, 0) + COALESCE(data07, 0) + COALESCE(data08, 0) + COALESCE(data09, 0) + COALESCE(data10, 0) END AS data10
        , CASE WHEN value_type_id = 1 THEN data11 ELSE COALESCE(data00, 0) + COALESCE(data01, 0) + COALESCE(data02, 0) + COALESCE(data03, 0) + COALESCE(data04, 0) + COALESCE(data05, 0) + COALESCE(data06, 0) + COALESCE(data07, 0) + COALESCE(data08, 0) + COALESCE(data09, 0) + COALESCE(data10, 0) + COALESCE(data11, 0) END AS data11
        , CASE WHEN value_type_id = 1 THEN data12 ELSE COALESCE(data00, 0) + COALESCE(data01, 0) + COALESCE(data02, 0) + COALESCE(data03, 0) + COALESCE(data04, 0) + COALESCE(data05, 0) + COALESCE(data06, 0) + COALESCE(data07, 0) + COALESCE(data08, 0) + COALESCE(data09, 0) + COALESCE(data10, 0) + COALESCE(data11, 0) + COALESCE(data12, 0) END AS data12
    FROM
        c1
)

, c3 AS (

    -- This section calculates debit and credit balances. It removes value_type_id

    SELECT
        section_type_id
        , ledger_account_id
        , subsidiary_account_id
        , CASE WHEN value_type_id = 3 AND data00 < 0 THEN NULL WHEN value_type_id = 4 AND data00 > 0 THEN NULL ELSE data00 END AS data00
        , CASE WHEN value_type_id = 3 AND data01 < 0 THEN NULL WHEN value_type_id = 4 AND data01 > 0 THEN NULL ELSE data01 END AS data01
        , CASE WHEN value_type_id = 3 AND data02 < 0 THEN NULL WHEN value_type_id = 4 AND data02 > 0 THEN NULL ELSE data02 END AS data02
        , CASE WHEN value_type_id = 3 AND data03 < 0 THEN NULL WHEN value_type_id = 4 AND data03 > 0 THEN NULL ELSE data03 END AS data03
        , CASE WHEN value_type_id = 3 AND data04 < 0 THEN NULL WHEN value_type_id = 4 AND data04 > 0 THEN NULL ELSE data04 END AS data04
        , CASE WHEN value_type_id = 3 AND data05 < 0 THEN NULL WHEN value_type_id = 4 AND data05 > 0 THEN NULL ELSE data05 END AS data05
        , CASE WHEN value_type_id = 3 AND data06 < 0 THEN NULL WHEN value_type_id = 4 AND data06 > 0 THEN NULL ELSE data06 END AS data06
        , CASE WHEN value_type_id = 3 AND data07 < 0 THEN NULL WHEN value_type_id = 4 AND data07 > 0 THEN NULL ELSE data07 END AS data07
        , CASE WHEN value_type_id = 3 AND data08 < 0 THEN NULL WHEN value_type_id = 4 AND data08 > 0 THEN NULL ELSE data08 END AS data08
        , CASE WHEN value_type_id = 3 AND data09 < 0 THEN NULL WHEN value_type_id = 4 AND data09 > 0 THEN NULL ELSE data09 END AS data09
        , CASE WHEN value_type_id = 3 AND data10 < 0 THEN NULL WHEN value_type_id = 4 AND data10 > 0 THEN NULL ELSE data10 END AS data10
        , CASE WHEN value_type_id = 3 AND data11 < 0 THEN NULL WHEN value_type_id = 4 AND data11 > 0 THEN NULL ELSE data11 END AS data11
        , CASE WHEN value_type_id = 3 AND data12 < 0 THEN NULL WHEN value_type_id = 4 AND data12 > 0 THEN NULL ELSE data12 END AS data12
    FROM
        c2
)

, c4 AS (

    -- This section aggregates data by a report section and accounts
    -- The data contains the correct values for flows, balances, debit balances and credit balances
    -- It removes the subsidiary account

    SELECT
        section_type_id
        , ledger_account_id
        , SUM(data00) AS data00
        , SUM(data01) AS data01
        , SUM(data02) AS data02
        , SUM(data03) AS data03
        , SUM(data04) AS data04
        , SUM(data05) AS data05
        , SUM(data06) AS data06
        , SUM(data07) AS data07
        , SUM(data08) AS data08
        , SUM(data09) AS data09
        , SUM(data10) AS data10
        , SUM(data11) AS data11
        , SUM(data12) AS data12
    FROM
        c3
    GROUP BY
        section_type_id
        , ledger_account_id
)

, factors (id, parent_id, is_parent) AS (

    -- This section selects child-parent factors for report sections

    SELECT
        rs.id
        , rs.id AS parent_id
        , COALESCE(rs.hide_items, 0) AS is_parent
    FROM
        dbo27.report_sections rs
    WHERE
        rs.company_id = @company_id
        AND rs.report_type_id = COALESCE(@report_id, rs.report_type_id)

    UNION ALL
    SELECT
        rs.id
        , rs.parent2_id
        , 1 AS is_parent
    FROM
        dbo27.report_sections rs
    WHERE
        rs.company_id = @company_id
        AND rs.report_type_id = COALESCE(@report_id, rs.report_type_id)
        AND rs.parent2_id IS NOT NULL

    UNION ALL
    SELECT
        f.id
        , rs.parent_id
        , 1 AS is_parent
    FROM
        factors f
        INNER JOIN dbo27.report_sections rs ON rs.id = f.parent_id
    WHERE
        rs.parent_id IS NOT NULL
        AND NOT f.id = rs.parent_id
)

, report_rows AS (

    -- This section multiplies section rows by factors and calculates totals by section and ledger account
    -- It also adds empty sections to show report headers
    -- Also it selects the total_type_id of the report sections

    SELECT
        section_id
        , ledger_account_id
        , MAX(total_type_id) AS total_type_id
        , SUM(data00) AS data00, SUM(data01) AS data01, SUM(data02) AS data02, SUM(data03) AS data03, SUM(data04) AS data04, SUM(data05) AS data05, SUM(data06) AS data06, SUM(data07) AS data07, SUM(data08) AS data08, SUM(data09) AS data09, SUM(data10) AS data10, SUM(data11) AS data11, SUM(data12) AS data12
        , SUM(data_rows) AS data_rows
    FROM
        (
        SELECT
            f.parent_id AS section_id
            , CASE WHEN f.is_parent = 1 THEN NULL ELSE r.ledger_account_id END AS ledger_account_id
            , rst.total_type_id
            , data00, data01, data02, data03, data04, data05, data06, data07, data08, data09, data10, data11, data12
            , 1 AS data_rows
        FROM
            c4 r
            INNER JOIN dbo27.report_sections rs ON rs.section_type_id = r.section_type_id
            INNER JOIN dbo27.report_section_types rst ON rst.id = rs.section_type_id
            INNER JOIN factors f ON f.id = rs.id
        WHERE
            rs.company_id = @company_id

        -- Adding report rows to show even without values

        UNION ALL
        SELECT
            rs.id AS section_id
            , NULL AS ledger_account_id
            , NULL AS total_type_id
            , NULL AS data00, NULL AS data01, NULL AS data02, NULL AS data03, NULL AS data04, NULL AS data05, NULL AS data06, NULL AS data07, NULL AS data08, NULL AS data09, NULL AS data10, NULL AS data11, NULL AS data12
            , NULL AS data_rows
        FROM
            dbo27.report_sections rs
        WHERE
            rs.company_id = @company_id
            AND rs.report_type_id = COALESCE(@report_id, rs.report_type_id)
            AND COALESCE(rs.show_line, 1) = 1
            AND rs.section_type_id IS NULL
        ) r
    GROUP BY
        section_id
        , ledger_account_id
)

, result AS (

    -- Main report output

    SELECT
        row_index
        , ref_number
        , code AS account
        , report
        , page_break
        , CAST(NULL AS int) AS row_level
        , row_format
        , 1 AS hide
        , CASE WHEN data00 <> 0 OR  data01 <> 0 OR  data02 <> 0 OR  data03 <> 0 OR  data04 <> 0 OR  data05 <> 0 OR  data06 <> 0 OR  data07 <> 0 OR  data08 <> 0 OR  data09 <> 0 OR  data10 <> 0 OR  data11 <> 0 OR  data12 <> 0 THEN 1 ELSE 0 END AS zero
        , CAST(NULL AS nvarchar(50)) AS code1
        , CAST(NULL AS nvarchar(50)) AS code2
        , code
        , name
        , CASE total_type_id
            WHEN 2 THEN data00
            WHEN 3 THEN data12
            WHEN 0 THEN NULL
            ELSE COALESCE(data01, 0) + COALESCE(data02, 0) + COALESCE(data03, 0) + COALESCE(data04, 0) + COALESCE(data05, 0) + COALESCE(data06, 0) + COALESCE(data07, 0) + COALESCE(data08, 0) + COALESCE(data09, 0) + COALESCE(data10, 0) + COALESCE(data11, 0) + COALESCE(data12, 0) END AS data00
        , data01, data02, data03, data04, data05, data06, data07, data08, data09, data10, data11, data12
    FROM
        (
            SELECT
                ROW_NUMBER() OVER (ORDER BY rs.report_type_id, rs.section, v.r, la.code, la.name) AS row_index
                , CASE WHEN v.r = 0 THEN rs.ref_number + COALESCE(la.ref_number, 0) ELSE NULL END AS ref_number
                , rs.report_type_id AS report
                , CASE WHEN rs.page_break_before IS NULL THEN NULL
                    WHEN rs.show_line_before = 1 AND v.r = -1 THEN 1
                    WHEN COALESCE(rs.show_line_before, 0) = 0 AND v.r = 0 THEN 1
                    ELSE NULL END AS page_break
                , CASE WHEN rs.row_format = 9 AND v.r = -1 THEN 8 WHEN v.r = -1 THEN NULL WHEN v.r = 1 THEN NULL
                    WHEN r.ledger_account_id IS NULL THEN rs.row_format ELSE NULL END row_format
                , CASE WHEN v.r = 0 THEN la.code ELSE NULL END AS code
                , CASE WHEN v.r = 0 THEN COALESCE(tr2.name, la.name, tr1.name, rs.description) ELSE NULL END AS name
                , CASE WHEN v.r = 0 AND data_rows > 0 THEN r.total_type_id ELSE 0 END AS total_type_id
                , data00 * @factor * f * rs.line_factor AS data00
                , data01 * @factor * f * rs.line_factor AS data01
                , data02 * @factor * f * rs.line_factor AS data02
                , data03 * @factor * f * rs.line_factor AS data03
                , data04 * @factor * f * rs.line_factor AS data04
                , data05 * @factor * f * rs.line_factor AS data05
                , data06 * @factor * f * rs.line_factor AS data06
                , data07 * @factor * f * rs.line_factor AS data07
                , data08 * @factor * f * rs.line_factor AS data08
                , data09 * @factor * f * rs.line_factor AS data09
                , data10 * @factor * f * rs.line_factor AS data10
                , data11 * @factor * f * rs.line_factor AS data11
                , data12 * @factor * f * rs.line_factor AS data12
            FROM
                report_rows r
                INNER JOIN dbo27.report_sections rs ON rs.id = r.section_id
                LEFT OUTER JOIN dbo27.ledger_accounts la ON la.id = r.ledger_account_id
                LEFT OUTER JOIN dbo27.translations tr1 ON tr1.company_id = rs.company_id AND tr1.table_id = 9 AND tr1.member_id = rs.id AND tr1.language = @data_language
                LEFT OUTER JOIN dbo27.translations tr2 ON tr2.company_id = rs.company_id AND tr2.table_id = 11 AND tr2.member_id = la.id AND tr2.language = @data_language
                CROSS JOIN (VALUES (-1, NULL), (0, 1), (1, NULL)) v(r, f)
            WHERE
                (rs.show_line = 1
                    OR (data00 <> 0 OR  data01 <> 0 OR  data02 <> 0 OR  data03 <> 0 OR  data04 <> 0 OR  data05 <> 0 OR  data06 <> 0 OR  data07 <> 0 OR  data08 <> 0 OR  data09 <> 0 OR  data10 <> 0 OR  data11 <> 0 OR  data12 <> 0)
                )
                AND (COALESCE(rs.show_line, 1) = 1 OR r.ledger_account_id IS NOT NULL)
                AND (r.ledger_account_id IS NULL OR COALESCE(rs.hide_items, 0) = 0)
                AND (v.f = 1
                    OR rs.show_line_before = 1 AND v.r = -1
                    OR rs.show_line_after = 1 AND v.r = 1
                )

        ) t
)

SELECT * FROM result ORDER BY row_index

END


