-- =============================================

CREATE PROCEDURE [xls27].[usp_balance_sheet_trial]
    @start_year int = NULL
    , @start_month int = NULL
    , @start_day int = NULL
    , @end_year int = NULL
    , @end_month int = NULL
    , @end_day int = NULL
    , @company_id int = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE username = USER_NAME() AND is_active = 1 AND is_default = 1)
ELSE
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

DECLARE @start_date date
DECLARE @end_date date

SET @start_date = DATEFROMPARTS(COALESCE(@start_year, YEAR(GETDATE())), COALESCE(@start_month, 1), 1)
SET @end_date = DATEFROMPARTS(COALESCE(@end_year, YEAR(GETDATE())), COALESCE(@end_month, 12), 1)

IF @start_day > 1
    IF DATEADD(DAY, @start_day - 1, @start_date) < DATEADD(MONTH, 1, @start_date)
        SET @start_date = DATEADD(DAY, @start_day - 1, @start_date)
    ELSE
        SET @start_date = DATEADD(DAY, -1, DATEADD(MONTH, 1, @start_date))

IF @end_day > 1 AND DATEADD(DAY, @end_day - 1, @end_date) < DATEADD(MONTH, 1, @end_date)
    SET @end_date = DATEADD(DAY, @end_day - 1, @end_date)
ELSE
    SET @end_date = DATEADD(DAY, -1, DATEADD(MONTH, 1, @end_date))

DECLARE @system_currency nvarchar(50) = (SELECT name FROM dbo27.units WHERE company_id = @company_id AND is_functional_currency = 1)

;WITH data AS (

-- Opening actual_balances

SELECT
    NULL AS id
    , b.account_id
    , b.subsidiary_account_id
    , b.balance AS balance
    , b.balance$ AS balance$
    , NULL AS debit
    , NULL AS debit$
    , NULL AS credit
    , NULL AS credit$
FROM
    dbo27.actual_balances b
    INNER JOIN dbo27.ledger_accounts a ON a.id = b.account_id
WHERE
    a.company_id = @company_id

-- debit opening balance

UNION ALL
SELECT
    j.id
    , j.debit_account_id AS account_id
    , j.debit_subsidiary_account_id AS subsidiary_account_id
    , j.amount AS balance
    , j.amount$ AS balance$
    , NULL AS debit
    , NULL AS debit$
    , NULL AS credit
    , NULL AS credit$
FROM
    dbo27.journals j
WHERE
    j.company_id = @company_id
    AND j.[date] < @start_date

-- credit opening balance

UNION ALL
SELECT
    j.id
    , j.credit_account_id AS account_id
    , j.credit_subsidiary_account_id AS subsidiary_account_id
    , -j.amount AS balance
    , -j.amount$ AS balance$
    , NULL AS debit
    , NULL AS debit$
    , NULL AS credit
    , NULL AS credit$
FROM
    dbo27.journals j
WHERE
    j.company_id = @company_id
    AND j.[date] < @start_date

-- debit turnover

UNION ALL
SELECT
    j.id
    , j.debit_account_id AS account_id
    , j.debit_subsidiary_account_id AS subsidiary_account_id
    , NULL AS balance
    , NULL AS balance$
    , j.amount AS debit
    , j.amount$ AS debit$
    , NULL AS credit
    , NULL AS credit$
FROM
    dbo27.journals j
WHERE
    j.company_id = @company_id
    AND j.[date] BETWEEN @start_date AND @end_date

-- credit turnover

UNION ALL
SELECT
    j.id
    , j.credit_account_id AS account_id
    , j.credit_subsidiary_account_id AS subsidiary_account_id
    , NULL AS balance
    , NULL AS balance$
    , NULL AS debit
    , NULL AS debit$
    , j.amount AS credit
    , j.amount$ AS credit$
FROM
    dbo27.journals j
WHERE
    j.company_id = @company_id
    AND j.[date] BETWEEN @start_date AND @end_date
)

, subamounts AS (

SELECT
    j.account_id
    , j.subsidiary_account_id
    , SUM(j.balance) AS balance
    , SUM(j.balance$) AS balance$
    , SUM(j.debit) AS debit
    , SUM(j.debit$) AS debit$
    , SUM(j.credit) AS credit
    , SUM(j.credit$) AS credit$
FROM
    data j
GROUP BY
    j.account_id
    , j.subsidiary_account_id
)

, amounts AS (

SELECT
    j.account_id
    , j.subsidiary_account_id
    , CASE WHEN j.balance < 0 THEN NULL ELSE j.balance END AS opening_debit
    , CASE WHEN j.balance < 0 THEN -j.balance ELSE NULL END opening_credit
    , CASE WHEN j.balance$ < 0 THEN NULL ELSE j.balance$ END AS opening_debit$
    , CASE WHEN j.balance$ < 0 THEN -j.balance$ ELSE NULL END opening_credit$
    , j.debit
    , j.debit$
    , j.credit
    , j.credit$
    , CASE WHEN (COALESCE(j.balance, 0) + COALESCE(j.debit, 0) - COALESCE(j.credit, 0)) > 0 THEN COALESCE(j.balance, 0) + COALESCE(j.debit, 0) - COALESCE(j.credit, 0) ELSE NULL END AS closing_debit
    , CASE WHEN (COALESCE(j.balance, 0) + COALESCE(j.debit, 0) - COALESCE(j.credit, 0)) < 0 THEN -(COALESCE(j.balance, 0) + COALESCE(j.debit, 0) - COALESCE(j.credit, 0)) ELSE NULL END AS closing_credit
    , CASE WHEN (COALESCE(j.balance$, 0) + COALESCE(j.debit$, 0) - COALESCE(j.credit$, 0)) > 0 THEN (COALESCE(j.balance$, 0) + COALESCE(j.debit$, 0) - COALESCE(j.credit$, 0)) ELSE NULL END AS closing_debit$
    , CASE WHEN (COALESCE(j.balance$, 0) + COALESCE(j.debit$, 0) - COALESCE(j.credit$, 0)) < 0 THEN -(COALESCE(j.balance$, 0) + COALESCE(j.debit$, 0) - COALESCE(j.credit$, 0)) ELSE NULL END AS closing_credit$
FROM
    subamounts j

)

, parents (id, parent_id) AS (
    SELECT
        s.id
        , s.id
    FROM
        dbo27.ledger_accounts s
    WHERE
        s.company_id = @company_id

    UNION ALL
    SELECT
        parents.id
        , s.parent_id
    FROM
        parents
        INNER JOIN dbo27.ledger_accounts s ON s.id = parents.parent_id
    WHERE
        s.parent_id IS NOT NULL
        AND NOT parents.id = s.parent_id
)

SELECT
    j.account_id
    , j.subsidiary_account_id
    , CASE WHEN j.subsidiary_account_id IS NOT NULL THEN NULL WHEN a.parent_id IS NULL THEN 1 ELSE 2 END AS row_format
    , CASE WHEN j.subsidiary_account_id IS NOT NULL THEN 2 WHEN a.parent_id IS NULL THEN 0 ELSE 1 END AS level
    , line.n AS line
    , a.code AS code
    , COALESCE(tr2.name, s.name) AS subsidiary_name
    , COALESCE('    ' + s.name, CASE WHEN a.parent_id IS NULL THEN '' ELSE '  ' END + COALESCE(tr1.name, a.name))
        + CASE WHEN line.n = 1 THEN ' (' + COALESCE(c.name,  NULL) + ')' ELSE '' END AS name
    , CASE WHEN line.n = 1 THEN COALESCE(c.name, NULL) WHEN c.is_functional_currency = 1 THEN @system_currency ELSE NULL END AS currency
    , CASE WHEN line.n = 0 AND j.opening_debit$ IS NULL AND j.opening_credit$ IS NULL THEN NULL
           WHEN line.n = 0 THEN COALESCE(j.opening_debit$, 0) - COALESCE(j.opening_credit$, 0)
           WHEN j.opening_debit IS NULL AND j.opening_credit IS NULL THEN NULL
           ELSE COALESCE(j.opening_debit, 0) - COALESCE(j.opening_credit, 0)
           END AS opening_balance
    , CASE WHEN line.n = 0 THEN j.opening_debit$ ELSE j.opening_debit END AS opening_debit
    , CASE WHEN line.n = 0 THEN j.opening_credit$ ELSE j.opening_credit END AS opening_credit
    , CASE WHEN line.n = 0 THEN j.debit$ ELSE j.debit END AS debit_turnover
    , CASE WHEN line.n = 0 THEN j.credit$ ELSE j.credit END AS credit_turnover
    , CASE WHEN line.n = 0 AND j.closing_debit$ IS NULL AND j.closing_credit$ IS NULL THEN NULL
           WHEN line.n = 0 THEN ROUND(COALESCE(j.closing_debit$, 0) - COALESCE(j.closing_credit$, 0), 2)
           WHEN j.closing_debit IS NULL AND j.closing_credit IS NULL THEN NULL
           ELSE ROUND(COALESCE(j.closing_debit, 0) - COALESCE(j.closing_credit, 0), 2)
           END AS closing_balance
    , CASE WHEN line.n = 0 THEN ROUND(j.closing_debit$, 2) ELSE ROUND(j.closing_debit, 2) END AS closing_debit
    , CASE WHEN line.n = 0 THEN ROUND(j.closing_credit$, 2) ELSE ROUND(j.closing_credit, 2) END AS closing_credit
FROM
    (

        SELECT
            j.account_id
            , j.subsidiary_account_id
            , j.opening_debit
            , j.opening_credit
            , j.opening_debit$
            , j.opening_credit$
            , j.debit
            , j.debit$
            , j.credit
            , j.credit$
            , j.closing_debit
            , j.closing_credit
            , j.closing_debit$
            , j.closing_credit$
        FROM
            amounts j
        WHERE
            j.subsidiary_account_id IS NOT NULL

        UNION ALL

        SELECT
            p.parent_id AS account_id
            , NULL AS subsidiary_account_id
            , SUM(j.opening_debit) AS opening_debit
            , SUM(j.opening_credit) AS opening_credit
            , SUM(j.opening_debit$) AS opening_debit$
            , SUM(j.opening_credit$) AS opening_credit$
            , SUM(j.debit) AS debit
            , SUM(j.debit$) AS debit$
            , SUM(j.credit) AS credit
            , SUM(j.credit$) AS credit$
            , SUM(j.closing_debit) AS closing_debit
            , SUM(j.closing_credit) AS closing_credit
            , SUM(j.closing_debit$) AS closing_debit$
            , SUM(j.closing_credit$) AS closing_credit$
        FROM
            amounts j
            INNER JOIN parents p ON p.id = j.account_id
        GROUP BY
            p.parent_id
    ) j
    INNER JOIN dbo27.ledger_accounts a ON a.id = j.account_id
    LEFT OUTER JOIN dbo27.members s ON s.id = j.subsidiary_account_id
    LEFT OUTER JOIN dbo27.units c ON c.id = s.unit_id
    LEFT OUTER JOIN dbo27.translations tr1 ON tr1.company_id = a.company_id AND tr1.table_id = 11 AND tr1.member_id = a.id AND tr1.language = @data_language
    LEFT OUTER JOIN dbo27.translations tr2 ON tr2.company_id = s.company_id AND tr2.table_id = 1 AND tr2.member_id = s.id AND tr2.language = @data_language
    CROSS JOIN (VALUES (0), (1)) line(n)
WHERE
    (line.n = 0 OR c.is_functional_currency <> 1)
    AND (
        COALESCE(j.opening_debit, 0) <> 0
        OR COALESCE(j.opening_credit, 0) <> 0
        OR COALESCE(j.debit, 0) <> 0
        OR COALESCE(j.credit, 0) <> 0
        OR COALESCE(j.closing_debit, 0) <> 0
        OR COALESCE(j.closing_credit, 0) <> 0
        OR COALESCE(j.opening_debit$, 0) <> 0
        OR COALESCE(j.opening_credit$, 0) <> 0
        OR COALESCE(j.debit$, 0) <> 0
        OR COALESCE(j.credit$, 0) <> 0
        OR COALESCE(j.closing_debit$, 0) <> 0
        OR COALESCE(j.closing_credit$, 0) <> 0
    )
ORDER BY
    a.code
    , s.name
    , line.n


END


