CREATE PROCEDURE [xls27].[usp_budget_balances]
    @company_id int = NULL
    , @category_id int = NULL
    , @time_id int = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

IF @company_id IS NULL
    SELECT TOP 1 @company_id = company_id FROM dbo27.members WHERE id IN (@category_id, @time_id)

SELECT
    t.id
    , t.account_id
    , CASE WHEN lat.bs_sign = -1 THEN 0 WHEN a.id IS NULL OR p.parent_id IS NOT NULL THEN 0 ELSE 1 END AS is_debit_editable
    , CASE WHEN lat.bs_sign = 1  THEN 0 WHEN a.id IS NULL OR p.parent_id IS NOT NULL THEN 0 ELSE 1 END AS is_credit_editable
    , CASE WHEN p.parent_id IS NULL THEN 0 ELSE 1 END AS is_parent
    , t.code
    , COALESCE(tr.name, a.name, t.name) AS name
    , t.debit
    , t.credit
FROM
    (
        SELECT
            t.id
            , a.id AS account_id
            , a.code
            , a.name
            , t.debit
            , t.credit
        FROM
            dbo27.ledger_accounts a
            INNER JOIN dbo27.users u ON u.company_id = a.company_id AND u.username = USER_NAME() AND u.is_active = 1
            LEFT OUTER JOIN dbo27.budget_balances t ON t.account_id = a.id AND t.category_id = @category_id AND t.time_id = @time_id
            LEFT JOIN dbo27.ledger_account_types lat ON lat.id = a.account_type_id
        WHERE
            a.company_id = @company_id
            AND COALESCE(lat.is_pl, 0) = 0

        UNION ALL
        SELECT
            NULL
            , NULL AS account_id
            , NULL AS code
            , 'Total' AS name
            , SUM(t.debit) AS debit
            , SUM(t.credit) AS credit
        FROM
            dbo27.budget_balances t
            INNER JOIN dbo27.members m1 ON m1.id = t.category_id
            INNER JOIN dbo27.users u ON u.company_id = m1.company_id AND u.username = USER_NAME() AND u.is_active = 1
        WHERE
            t.category_id = @category_id AND t.time_id = @time_id
            AND m1.company_id = @company_id
    ) t
    LEFT OUTER JOIN dbo27.ledger_accounts a ON a.id = t.account_id
    LEFT JOIN dbo27.ledger_account_types lat ON lat.id = a.account_type_id
    LEFT OUTER JOIN (
        SELECT DISTINCT a.parent_id FROM dbo27.ledger_accounts a WHERE a.company_id = @company_id
    ) p ON p.parent_id = t.account_id
    LEFT OUTER JOIN dbo27.translations tr ON tr.company_id = @company_id AND tr.table_id = 11 AND tr.member_id = a.id AND tr.language = @data_language

ORDER BY
    code

END


