CREATE PROCEDURE [xls27].[usp_budget_balances_update]
    @category_id int
    , @time_id int
    , @account_id int
    , @debit money = NULL
    , @credit money = NULL
AS
BEGIN

SET NOCOUNT ON

IF @debit IS NULL AND @credit IS NULL
    BEGIN
    EXEC xls27.usp_budget_balances_delete @category_id, @time_id, @account_id
    RETURN
    END

DECLARE @company_id int

SELECT TOP 1 @company_id = t.company_id FROM dbo27.members t INNER JOIN dbo27.users u ON u.company_id = t.company_id  WHERE t.id = @category_id AND u.username = USER_NAME() AND u.is_active = 1

IF @company_id IS NULL
    RETURN

IF NOT EXISTS (SELECT TOP 1 id FROM dbo27.members WHERE id = @time_id AND company_id = @company_id)
    RETURN

IF NOT EXISTS (SELECT TOP 1 id FROM dbo27.ledger_accounts WHERE id = @account_id AND company_id = @company_id)
    RETURN

SET NOCOUNT OFF

UPDATE dbo27.budget_balances
SET
    category_id = @category_id
    , time_id = @time_id
    , account_id = @account_id
    , debit = @debit
    , credit = @credit
WHERE
    category_id = @category_id
    AND time_id = @time_id
    AND account_id = @account_id

IF @@ROWCOUNT = 0
INSERT INTO dbo27.budget_balances
    ( category_id
    , time_id
    , account_id
    , debit
    , credit
    )
VALUES
    ( @category_id
    , @time_id
    , @account_id
    , @debit
    , @credit
    )

END


