-- =============================================
-- Author:      Gartle LLC
-- Release:     3.0, 2022-07-05
-- Description: Selects rows to set closed periods
-- =============================================

CREATE PROCEDURE [xls27].[usp_budget_closed_periods]
    @company_id int = NULL
WITH EXECUTE AS OWNER
AS
BEGIN

SET NOCOUNT ON

EXECUTE AS CALLER

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE username = USER_NAME() AND is_active = 1 AND is_default = 1)
ELSE
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

REVERT

DECLARE @columns nvarchar(max) = NULL

IF @company_id IS NOT NULL
    SELECT @columns = STUFF((
        SELECT
            ', [' + CAST(m.code AS nvarchar) + ']'
        FROM
            dbo27.members m
        WHERE
            m.dimension_id = 3
            AND m.is_active = 1
            AND m.company_id = @company_id
        ORDER BY
            m.sort_order
            , m.code
        FOR XML PATH(''), TYPE
    ).value('.', 'nvarchar(MAX)'), 1, 2, '')

IF @columns IS NULL
    BEGIN
    SELECT
        m.id
        , m.sort_order
        , m.code
        , m.name
        , NULL AS data_format
    FROM
        dbo27.members m
    WHERE
        m.id IS NULL
    RETURN
    END

-- PRINT @columns

DECLARE @sql nvarchar(max)

SET @sql = 'SELECT
    m.id
    , m.sort_order
    , m.code
    , m.name
    , NULL AS data_format
    , ' + @columns + '
FROM
    dbo27.members m
    LEFT OUTER JOIN (
        SELECT
            m.id
            , c.code
            , CAST(mp.update_permission AS tinyint) AS value
        FROM
            dbo27.member_relations mp
            INNER JOIN dbo27.members m ON m.id = mp.member_id
            INNER JOIN dbo27.members c ON c.id = mp.partner_id
        WHERE
            m.dimension_id = 2
            AND c.dimension_id = 3
            AND m.company_id = ' + CAST(@company_id AS nvarchar) + '
    ) s PIVOT (
        MAX(value) FOR code IN (' + @columns + ')
    ) p ON p.id = m.id
WHERE
    m.dimension_id = 2
    AND m.company_id = ' + CAST(@company_id AS nvarchar) + '
ORDER BY
    m.sort_order
    , m.code
'

-- PRINT @sql

EXEC (@sql)

END


