-- Description: Cell change event handler for usp_closed_periods
-- =============================================

CREATE PROCEDURE [xls27].[usp_budget_closed_periods_change]
    @column_name nvarchar(128) = NULL
    , @cell_number_value int = NULL
    , @id int = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

DECLARE @message nvarchar(max)

-- Protecting non-data columns

IF @column_name IN ('id', 'sort_order', 'data_format')
    BEGIN
    SET @message = dbo27.get_translated_string('The column ''%s'' is not updatable', @data_language)
    RAISERROR (@message, 11, 0, @column_name)
    RETURN
    END

DECLARE @company_id int

SELECT TOP 1 @company_id = t.company_id FROM dbo27.members t INNER JOIN dbo27.users u ON u.company_id = t.company_id  WHERE t.id = @id AND u.username = USER_NAME() AND u.is_active = 1

IF @company_id IS NULL
    BEGIN
    SET @message = dbo27.get_translated_string('Cannot find member_id %i', @data_language)
    RAISERROR (@message, 11, 0, @id)
    RETURN
    END

DECLARE @category_code nvarchar(128) = @column_name
DECLARE @category_id int

SELECT @category_id = id FROM dbo27.members WHERE code = @category_code AND company_id = @company_id

IF @category_id IS NULL
    BEGIN
    SET @message = dbo27.get_translated_string('Cannot find member_id %s', @data_language)
    RAISERROR (@message, 11, 0, @category_code)
    RETURN
    END

DECLARE @update_permission bit = NULL

IF @cell_number_value IS NULL
    BEGIN
    DELETE FROM dbo27.member_relations WHERE member_id = @id AND partner_id = @category_id
    RETURN
    END
ELSE IF @cell_number_value = 0
    BEGIN
    SET @update_permission = 0
    END
ELSE IF @cell_number_value = 1
    BEGIN
    SET @update_permission = 1
    END
ELSE
    BEGIN
    SET @message = dbo27.get_translated_string('Unknown option: %i (Use 0 or 1)', @data_language)
    RAISERROR (@message, 11, 0, @cell_number_value)
    END

UPDATE dbo27.member_relations
SET
    update_permission = @update_permission
WHERE
    member_id = @id
    AND partner_id = @category_id

IF @@ROWCOUNT = 0
    INSERT INTO dbo27.member_relations (member_id, partner_id, update_permission)
    VALUES (@id, @category_id, @update_permission)

END


