CREATE PROCEDURE [xls27].[usp_budget_currency_rates]
    @company_id int = NULL
    , @category_id int = NULL
    , @time_id int = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE username = USER_NAME() AND is_active = 1 AND is_default = 1)
ELSE
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

DECLARE @system_currency_code nvarchar(50) = (SELECT code FROM dbo27.units WHERE company_id = @company_id AND is_functional_currency = 1)

SELECT
    c.id AS category_id
    , p.id AS time_id
    , u.id AS unit_id
    , COALESCE(t1.name, c.name) AS category_name
    , COALESCE(t2.name, p.name) AS period_name
    , CASE WHEN u.is_base_currency = 0 THEN @system_currency_code + '/' + u.code ELSE u.code + '/' + @system_currency_code END AS pair
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.balance00 = 0 THEN t.balance00 ELSE 1 / t.balance00 END AS smallmoney) AS balance00
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.period01  = 0 THEN t.period01  ELSE 1 / t.period01  END AS smallmoney) AS period01
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.balance01 = 0 THEN t.balance01 ELSE 1 / t.balance01 END AS smallmoney) AS balance01
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.period02  = 0 THEN t.period02  ELSE 1 / t.period02  END AS smallmoney) AS period02
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.balance02 = 0 THEN t.balance02 ELSE 1 / t.balance02 END AS smallmoney) AS balance02
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.period03  = 0 THEN t.period03  ELSE 1 / t.period03  END AS smallmoney) AS period03
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.balance03 = 0 THEN t.balance03 ELSE 1 / t.balance03 END AS smallmoney) AS balance03
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.period04  = 0 THEN t.period04  ELSE 1 / t.period04  END AS smallmoney) AS period04
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.balance04 = 0 THEN t.balance04 ELSE 1 / t.balance04 END AS smallmoney) AS balance04
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.period05  = 0 THEN t.period05  ELSE 1 / t.period05  END AS smallmoney) AS period05
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.balance05 = 0 THEN t.balance05 ELSE 1 / t.balance05 END AS smallmoney) AS balance05
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.period06  = 0 THEN t.period06  ELSE 1 / t.period06  END AS smallmoney) AS period06
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.balance06 = 0 THEN t.balance06 ELSE 1 / t.balance06 END AS smallmoney) AS balance06
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.period07  = 0 THEN t.period07  ELSE 1 / t.period07  END AS smallmoney) AS period07
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.balance07 = 0 THEN t.balance07 ELSE 1 / t.balance07 END AS smallmoney) AS balance07
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.period08  = 0 THEN t.period08  ELSE 1 / t.period08  END AS smallmoney) AS period08
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.balance08 = 0 THEN t.balance08 ELSE 1 / t.balance08 END AS smallmoney) AS balance08
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.period09  = 0 THEN t.period09  ELSE 1 / t.period09  END AS smallmoney) AS period09
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.balance09 = 0 THEN t.balance09 ELSE 1 / t.balance09 END AS smallmoney) AS balance09
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.period10  = 0 THEN t.period10  ELSE 1 / t.period10  END AS smallmoney) AS period10
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.balance10 = 0 THEN t.balance10 ELSE 1 / t.balance10 END AS smallmoney) AS balance10
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.period11  = 0 THEN t.period11  ELSE 1 / t.period11  END AS smallmoney) AS period11
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.balance11 = 0 THEN t.balance11 ELSE 1 / t.balance11 END AS smallmoney) AS balance11
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.period12  = 0 THEN t.period12  ELSE 1 / t.period12  END AS smallmoney) AS period12
    , CAST(CASE WHEN u.is_base_currency = 0 OR t.balance12 = 0 THEN t.balance12 ELSE 1 / t.balance12 END AS smallmoney) AS balance12
    , t.row_formulas
FROM
    dbo27.units u
    CROSS JOIN dbo27.members c
    CROSS JOIN dbo27.members p
    LEFT OUTER JOIN dbo27.budget_currency_rates t ON t.unit_id = u.id AND t.category_id = c.id AND t.time_id = p.id
    LEFT OUTER JOIN dbo27.translations t1 ON t1.company_id = c.company_id AND t1.table_id = 1 AND t1.member_id = c.id AND t1.language = @data_language
    LEFT OUTER JOIN dbo27.translations t2 ON t2.company_id = p.company_id AND t2.table_id = 1 AND t2.member_id = p.id AND t2.language = @data_language
WHERE
    u.is_currency = 1
    AND u.is_functional_currency = 0
    AND u.company_id = @company_id
    AND c.dimension_id = 3 AND c.id = COALESCE(@category_id, c.id) AND c.company_id = @company_id
    AND p.dimension_id = 2 AND p.id = COALESCE(@time_id, p.id) AND p.company_id = @company_id
ORDER BY
    u.sort_order
    , u.id
    , c.sort_order
    , c.code
    , p.sort_order
    , p.code

END


