        , COALESCE(CAST(r.charges06 * COALESCE(cr.period06, 1) * COALESCE(vr.factor, 0) AS numeric(15,2)), 0) AS vat06
        , COALESCE(CAST(r.charges07 * COALESCE(cr.period07, 1) * COALESCE(vr.factor, 0) AS numeric(15,2)), 0) AS vat07
        , COALESCE(CAST(r.charges08 * COALESCE(cr.period08, 1) * COALESCE(vr.factor, 0) AS numeric(15,2)), 0) AS vat08
        , COALESCE(CAST(r.charges09 * COALESCE(cr.period09, 1) * COALESCE(vr.factor, 0) AS numeric(15,2)), 0) AS vat09
        , COALESCE(CAST(r.charges10 * COALESCE(cr.period10, 1) * COALESCE(vr.factor, 0) AS numeric(15,2)), 0) AS vat10
        , COALESCE(CAST(r.charges11 * COALESCE(cr.period11, 1) * COALESCE(vr.factor, 0) AS numeric(15,2)), 0) AS vat11
        , COALESCE(CAST(r.charges12 * COALESCE(cr.period12, 1) * COALESCE(vr.factor, 0) AS numeric(15,2)), 0) AS vat12

        , COALESCE(CAST(r.balance00 * COALESCE(cr.balance00, 1) AS numeric(15,2)), 0) AS balance00
    FROM
        dbo27.budget_requests r
        INNER JOIN dbo27.members m1 ON m1.id = r.account_id
        INNER JOIN dbo27.member_types t ON t.id = m1.account_type_id
        LEFT OUTER JOIN dbo27.vat_rates vr ON vr.id = r.vat_rate_id
        LEFT OUTER JOIN dbo27.budget_currency_rates cr ON cr.category_id = r.category_id AND cr.time_id = r.time_id AND cr.unit_id = r.unit_id
    WHERE
        r.category_id = @category_id
        AND r.time_id = @time_id
        AND m1.company_id = @company_id
        AND r.[entity_id] = COALESCE(@entity_id, r.[entity_id])
)

, a2 AS (

    -- final raw payments, charges + cf, pl, signed vat on account level with analytic members

    SELECT
        id, account_id, [entity_id], product_id, region_id, subsidiary_account_id

        , cf_sign, pl_sign, vat_sign, is_charge_net

         , payments01, payments02, payments03, payments04, payments05, payments06, payments07, payments08, payments09, payments10, payments11, payments12
         , charges01, charges02, charges03, charges04, charges05, charges06, charges07, charges08, charges09, charges10, charges11, charges12

        , cf_sign * payments01 AS cf01
        , cf_sign * payments02 AS cf02
        , cf_sign * payments03 AS cf03
        , cf_sign * payments04 AS cf04
        , cf_sign * payments05 AS cf05
        , cf_sign * payments06 AS cf06
        , cf_sign * payments07 AS cf07
        , cf_sign * payments08 AS cf08
        , cf_sign * payments09 AS cf09
        , cf_sign * payments10 AS cf10
        , cf_sign * payments11 AS cf11
        , cf_sign * payments12 AS cf12

        , vat_sign * vat01 AS vat01
        , vat_sign * vat02 AS vat02
        , vat_sign * vat03 AS vat03
        , vat_sign * vat04 AS vat04
        , vat_sign * vat05 AS vat05
        , vat_sign * vat06 AS vat06
        , vat_sign * vat07 AS vat07
        , vat_sign * vat08 AS vat08
        , vat_sign * vat09 AS vat09
        , vat_sign * vat10 AS vat10
        , vat_sign * vat11 AS vat11
        , vat_sign * vat12 AS vat12

        , pl_sign * (charges01 - COALESCE(vat01, 0)) AS pl01
        , pl_sign * (charges02 - COALESCE(vat02, 0)) AS pl02
        , pl_sign * (charges03 - COALESCE(vat03, 0)) AS pl03
        , pl_sign * (charges04 - COALESCE(vat04, 0)) AS pl04
        , pl_sign * (charges05 - COALESCE(vat05, 0)) AS pl05
        , pl_sign * (charges06 - COALESCE(vat06, 0)) AS pl06
        , pl_sign * (charges07 - COALESCE(vat07, 0)) AS pl07
        , pl_sign * (charges08 - COALESCE(vat08, 0)) AS pl08
        , pl_sign * (charges09 - COALESCE(vat09, 0)) AS pl09
        , pl_sign * (charges10 - COALESCE(vat10, 0)) AS pl10
        , pl_sign * (charges11 - COALESCE(vat11, 0)) AS pl11
        , pl_sign * (charges12 - COALESCE(vat12, 0)) AS pl12

    FROM
        a1
)

, b1 AS (

    -- raw dr, cr, balance00 on ledger and subsidiary accounts based on raw data and configured accounts

    SELECT
        payment_debit_id AS ledger_account_id
        , CASE WHEN cf_sign = 1 THEN -1 ELSE COALESCE(subsidiary_account_id, -1) END AS subsidiary_account_id
        , NULL AS balance00
        , payments01 AS dr01, payments02 AS dr02, payments03 AS dr03, payments04 AS dr04, payments05 AS dr05, payments06 AS dr06, payments07 AS dr07, payments08 AS dr08, payments09 AS dr09, payments10 AS dr10, payments11 AS dr11, payments12 AS dr12
        , NULL AS cr01, NULL AS cr02, NULL AS cr03, NULL AS cr04, NULL AS cr05, NULL AS cr06, NULL AS cr07, NULL AS cr08, NULL AS cr09, NULL AS cr10, NULL AS cr11, NULL AS cr12
    FROM
        a1
    WHERE
        payment_debit_id IS NOT NULL AND payment_credit_id IS NOT NULL

    UNION ALL
    SELECT
        payment_credit_id AS ledger_account_id
        , CASE WHEN cf_sign = -1 THEN -1 ELSE COALESCE(subsidiary_account_id, -1) END AS subsidiary_account_id
        , NULL AS balance00
        , NULL AS dr01, NULL AS dr02, NULL AS dr03, NULL AS dr04, NULL AS dr05, NULL AS dr06, NULL AS dr07, NULL AS dr08, NULL AS dr09, NULL AS dr10, NULL AS dr11, NULL AS dr12
        , payments01 AS cr01, payments02 AS cr02, payments03 AS cr03, payments04 AS cr04, payments05 AS cr05, payments06 AS cr06, payments07 AS cr07, payments08 AS cr08, payments09 AS cr09, payments10 AS cr10, payments11 AS cr11, payments12 AS cr12
    FROM
        a1
    WHERE
        payment_debit_id IS NOT NULL AND payment_credit_id IS NOT NULL

    UNION ALL
    SELECT
        charge_debit_id AS ledger_account_id
        , CASE WHEN pl_sign = -1 THEN -1 ELSE COALESCE(subsidiary_account_id, -1) END AS subsidiary_account_id
        , NULL AS balance00
        , charges01 - is_charge_net * vat01 AS dr01
        , charges02 - is_charge_net * vat02 AS dr02
        , charges03 - is_charge_net * vat03 AS dr03
        , charges04 - is_charge_net * vat04 AS dr04
        , charges05 - is_charge_net * vat05 AS dr05
        , charges06 - is_charge_net * vat06 AS dr06
        , charges07 - is_charge_net * vat07 AS dr07
        , charges08 - is_charge_net * vat08 AS dr08
        , charges09 - is_charge_net * vat09 AS dr09
        , charges10 - is_charge_net * vat10 AS dr10
        , charges11 - is_charge_net * vat11 AS dr11
        , charges12 - is_charge_net * vat12 AS dr12
        , NULL AS cr01, NULL AS cr02, NULL AS cr03, NULL AS cr04, NULL AS cr05, NULL AS cr06, NULL AS cr07, NULL AS cr08, NULL AS cr09, NULL AS cr10, NULL AS cr11, NULL AS cr12
    FROM
        a1
    WHERE
        charge_debit_id IS NOT NULL AND charge_credit_id IS NOT NULL


    UNION ALL
    SELECT
        charge_credit_id AS ledger_account_id
        , CASE WHEN pl_sign = 1 THEN -1 ELSE COALESCE(subsidiary_account_id, -1) END AS subsidiary_account_id
        , NULL AS balance00
        , NULL AS dr01, NULL AS dr02, NULL AS dr03, NULL AS dr04, NULL AS dr05, NULL AS dr06, NULL AS dr07, NULL AS dr08, NULL AS dr09, NULL AS dr10, NULL AS dr11, NULL AS dr12
        , charges01 - is_charge_net * vat01 AS cr01
        , charges02 - is_charge_net * vat02 AS cr02
        , charges03 - is_charge_net * vat03 AS cr03
        , charges04 - is_charge_net * vat04 AS cr04
        , charges05 - is_charge_net * vat05 AS cr05
        , charges06 - is_charge_net * vat06 AS cr06
        , charges07 - is_charge_net * vat07 AS cr07
        , charges08 - is_charge_net * vat08 AS cr08
        , charges09 - is_charge_net * vat09 AS cr09
        , charges10 - is_charge_net * vat10 AS cr10
        , charges11 - is_charge_net * vat11 AS cr11
        , charges12 - is_charge_net * vat12 AS cr12
    FROM
        a1
    WHERE
        charge_debit_id IS NOT NULL AND charge_credit_id IS NOT NULL

    UNION ALL
    SELECT
        vat_debit_id AS ledger_account_id
        , CASE WHEN vat_debit_id = charge_debit_id THEN COALESCE(subsidiary_account_id, -1) ELSE -1 END AS subsidiary_account_id
        , NULL AS balance00
        , vat01 AS dr01, vat02 AS dr02, vat03 AS dr03, vat04 AS dr04, vat05 AS dr05, vat06 AS dr06, vat07 AS dr07, vat08 AS dr08, vat09 AS dr09, vat10 AS dr10, vat11 AS dr11, vat12 AS dr12
        , NULL AS cr01, NULL AS cr02, NULL AS cr03, NULL AS cr04, NULL AS cr05, NULL AS cr06, NULL AS cr07, NULL AS cr08, NULL AS cr09, NULL AS cr10, NULL AS cr11, NULL AS cr12
    FROM
        a1
    WHERE
        vat_debit_id IS NOT NULL AND vat_credit_id IS NOT NULL

    UNION ALL
    SELECT
        vat_credit_id AS ledger_account_id
        , CASE WHEN vat_credit_id = charge_credit_id THEN COALESCE(subsidiary_account_id, -1) ELSE -1 END AS subsidiary_account_id
        , NULL AS balance00
        , NULL AS dr01, NULL AS dr02, NULL AS dr03, NULL AS dr04, NULL AS dr05, NULL AS dr06, NULL AS dr07, NULL AS dr08, NULL AS dr09, NULL AS dr10, NULL AS dr11, NULL AS dr12
        , vat01 AS cr01, vat02 AS cr02, vat03 AS cr03, vat04 AS cr04, vat05 AS cr05, vat06 AS cr06, vat07 AS cr07, vat08 AS cr08, vat09 AS cr09, vat10 AS cr10, vat11 AS cr11, vat12 AS cr12
    FROM
        a1
    WHERE
        vat_debit_id IS NOT NULL AND vat_credit_id IS NOT NULL

    UNION ALL
    SELECT
        CASE WHEN cf_sign = 1 THEN payment_credit_id ELSE payment_debit_id END AS ledger_account_id
        , COALESCE(subsidiary_account_id, -1) AS subsidiary_account_id
        , CASE WHEN cf_sign = 1 THEN balance00 ELSE -balance00 END AS balance00
        , NULL AS dr01, NULL AS dr02, NULL AS dr03, NULL AS dr04, NULL AS dr05, NULL AS dr06, NULL AS dr07, NULL AS dr08, NULL AS dr09, NULL AS dr10, NULL AS dr11, NULL AS dr12
        , NULL AS cr01, NULL AS cr02, NULL AS cr03, NULL AS cr04, NULL AS cr05, NULL AS cr06, NULL AS cr07, NULL AS cr08, NULL AS cr09, NULL AS cr10, NULL AS cr11, NULL AS cr12
    FROM
        a1
    WHERE
        payment_debit_id IS NOT NULL AND payment_credit_id IS NOT NULL
)

, b2 AS (

    -- total dr, cr, balance00 on ledger and subsidiary accounts based on b1

    SELECT
        ledger_account_id
        , subsidiary_account_id
        , COALESCE(SUM(balance00), 0) AS balance00
        , COALESCE(SUM(dr01), 0) AS dr01, COALESCE(SUM(dr02), 0) AS dr02, COALESCE(SUM(dr03), 0) AS dr03, COALESCE(SUM(dr04), 0) AS dr04, COALESCE(SUM(dr05), 0) AS dr05, COALESCE(SUM(dr06), 0) AS dr06, COALESCE(SUM(dr07), 0) AS dr07, COALESCE(SUM(dr08), 0) AS dr08, COALESCE(SUM(dr09), 0) AS dr09, COALESCE(SUM(dr10), 0) AS dr10, COALESCE(SUM(dr11), 0) AS dr11, COALESCE(SUM(dr12), 0) AS dr12
        , COALESCE(SUM(cr01), 0) AS cr01, COALESCE(SUM(cr02), 0) AS cr02, COALESCE(SUM(cr03), 0) AS cr03, COALESCE(SUM(cr04), 0) AS cr04, COALESCE(SUM(cr05), 0) AS cr05, COALESCE(SUM(cr06), 0) AS cr06, COALESCE(SUM(cr07), 0) AS cr07, COALESCE(SUM(cr08), 0) AS cr08, COALESCE(SUM(cr09), 0) AS cr09, COALESCE(SUM(cr10), 0) AS cr10, COALESCE(SUM(cr11), 0) AS cr11, COALESCE(SUM(cr12), 0) AS cr12
    FROM
        b1
    GROUP BY
        ledger_account_id
        , subsidiary_account_id
)

, b3 AS (

    -- total dr, cr, balance00 + balances + bs_sign on ledger and subsidiary accounts

    SELECT
        ledger_account_id
        , subsidiary_account_id
        , dr01, dr02, dr03, dr04, dr05, dr06, dr07, dr08, dr09, dr10, dr11, dr12
        , cr01, cr02, cr03, cr04, cr05, cr06, cr07, cr08, cr09, cr10, cr11, cr12
        , balance00 AS balance00
        , balance00 + dr01 - cr01 AS balance01
        , balance00 + dr01 - cr01 + dr02 - cr02 AS balance02
        , balance00 + dr01 - cr01 + dr02 - cr02 + dr03 - cr03 AS balance03
        , balance00 + dr01 - cr01 + dr02 - cr02 + dr03 - cr03 + dr04 - cr04 AS balance04
        , balance00 + dr01 - cr01 + dr02 - cr02 + dr03 - cr03 + dr04 - cr04 + dr05 - cr05 AS balance05
        , balance00 + dr01 - cr01 + dr02 - cr02 + dr03 - cr03 + dr04 - cr04 + dr05 - cr05 + dr06 - cr06 AS balance06
        , balance00 + dr01 - cr01 + dr02 - cr02 + dr03 - cr03 + dr04 - cr04 + dr05 - cr05 + dr06 - cr06 + dr07 - cr07 AS balance07
        , balance00 + dr01 - cr01 + dr02 - cr02 + dr03 - cr03 + dr04 - cr04 + dr05 - cr05 + dr06 - cr06 + dr07 - cr07 + dr08 - cr08 AS balance08
        , balance00 + dr01 - cr01 + dr02 - cr02 + dr03 - cr03 + dr04 - cr04 + dr05 - cr05 + dr06 - cr06 + dr07 - cr07 + dr08 - cr08 + dr09 - cr09 AS balance09
        , balance00 + dr01 - cr01 + dr02 - cr02 + dr03 - cr03 + dr04 - cr04 + dr05 - cr05 + dr06 - cr06 + dr07 - cr07 + dr08 - cr08 + dr09 - cr09 + dr10 - cr10 AS balance10
        , balance00 + dr01 - cr01 + dr02 - cr02 + dr03 - cr03 + dr04 - cr04 + dr05 - cr05 + dr06 - cr06 + dr07 - cr07 + dr08 - cr08 + dr09 - cr09 + dr10 - cr10 + dr11 - cr11 AS balance11
        , balance00 + dr01 - cr01 + dr02 - cr02 + dr03 - cr03 + dr04 - cr04 + dr05 - cr05 + dr06 - cr06 + dr07 - cr07 + dr08 - cr08 + dr09 - cr09 + dr10 - cr10 + dr11 - cr11 + dr12 - cr12 AS balance12
        , bs_sign
    FROM
        b2
        INNER JOIN dbo27.ledger_accounts a ON a.id = ledger_account_id
        LEFT OUTER JOIN dbo27.ledger_account_types t ON t.id = a.account_type_id
)

, b4 AS (

    -- total dr, cr, balances + debit and credit balances on ledger and subsidiary accounts

    SELECT
        ledger_account_id
        , subsidiary_account_id
        , dr01, dr02, dr03, dr04, dr05, dr06, dr07, dr08, dr09, dr10, dr11, dr12
        , cr01, cr02, cr03, cr04, cr05, cr06, cr07, cr08, cr09, cr10, cr11, cr12
        , balance00, balance01, balance02, balance03, balance04, balance05, balance06, balance07, balance08, balance09, balance10, balance11, balance12
        , bs_sign
        , CASE WHEN bs_sign = 1 OR bs_sign = 0 AND balance00 > 0 THEN balance00 ELSE NULL END AS bd00
        , CASE WHEN bs_sign = 1 OR bs_sign = 0 AND balance01 > 0 THEN balance01 ELSE NULL END AS bd01
        , CASE WHEN bs_sign = 1 OR bs_sign = 0 AND balance02 > 0 THEN balance02 ELSE NULL END AS bd02
        , CASE WHEN bs_sign = 1 OR bs_sign = 0 AND balance03 > 0 THEN balance03 ELSE NULL END AS bd03
        , CASE WHEN bs_sign = 1 OR bs_sign = 0 AND balance04 > 0 THEN balance04 ELSE NULL END AS bd04
        , CASE WHEN bs_sign = 1 OR bs_sign = 0 AND balance05 > 0 THEN balance05 ELSE NULL END AS bd05
        , CASE WHEN bs_sign = 1 OR bs_sign = 0 AND balance06 > 0 THEN balance06 ELSE NULL END AS bd06
        , CASE WHEN bs_sign = 1 OR bs_sign = 0 AND balance07 > 0 THEN balance07 ELSE NULL END AS bd07
        , CASE WHEN bs_sign = 1 OR bs_sign = 0 AND balance08 > 0 THEN balance08 ELSE NULL END AS bd08
        , CASE WHEN bs_sign = 1 OR bs_sign = 0 AND balance09 > 0 THEN balance09 ELSE NULL END AS bd09
        , CASE WHEN bs_sign = 1 OR bs_sign = 0 AND balance10 > 0 THEN balance10 ELSE NULL END AS bd10
        , CASE WHEN bs_sign = 1 OR bs_sign = 0 AND balance11 > 0 THEN balance11 ELSE NULL END AS bd11
        , CASE WHEN bs_sign = 1 OR bs_sign = 0 AND balance12 > 0 THEN balance12 ELSE NULL END AS bd12
        , CASE WHEN bs_sign = -1 OR bs_sign = 0 AND balance00 < 0 THEN -balance00 ELSE NULL END AS bc00
        , CASE WHEN bs_sign = -1 OR bs_sign = 0 AND balance01 < 0 THEN -balance01 ELSE NULL END AS bc01
        , CASE WHEN bs_sign = -1 OR bs_sign = 0 AND balance02 < 0 THEN -balance02 ELSE NULL END AS bc02
        , CASE WHEN bs_sign = -1 OR bs_sign = 0 AND balance03 < 0 THEN -balance03 ELSE NULL END AS bc03
        , CASE WHEN bs_sign = -1 OR bs_sign = 0 AND balance04 < 0 THEN -balance04 ELSE NULL END AS bc04
        , CASE WHEN bs_sign = -1 OR bs_sign = 0 AND balance05 < 0 THEN -balance05 ELSE NULL END AS bc05
        , CASE WHEN bs_sign = -1 OR bs_sign = 0 AND balance06 < 0 THEN -balance06 ELSE NULL END AS bc06
        , CASE WHEN bs_sign = -1 OR bs_sign = 0 AND balance07 < 0 THEN -balance07 ELSE NULL END AS bc07
        , CASE WHEN bs_sign = -1 OR bs_sign = 0 AND balance08 < 0 THEN -balance08 ELSE NULL END AS bc08
        , CASE WHEN bs_sign = -1 OR bs_sign = 0 AND balance09 < 0 THEN -balance09 ELSE NULL END AS bc09
        , CASE WHEN bs_sign = -1 OR bs_sign = 0 AND balance10 < 0 THEN -balance10 ELSE NULL END AS bc10
        , CASE WHEN bs_sign = -1 OR bs_sign = 0 AND balance11 < 0 THEN -balance11 ELSE NULL END AS bc11
        , CASE WHEN bs_sign = -1 OR bs_sign = 0 AND balance12 < 0 THEN -balance12 ELSE NULL END AS bc12
        , 0 AS debit, 0 AS credit
    FROM
        b3

    UNION ALL
    SELECT
        r.account_id AS ledger_account_id
        , NULL AS subsidiary_account_id

        , 0 AS dr01, 0 AS dr02, 0 AS dr03, 0 AS dr04, 0 AS dr05, 0 AS dr06, 0 AS dr07, 0 AS dr08, 0 AS dr09, 0 AS dr10, 0 AS dr11, 0 AS dr12
        , 0 AS cr01, 0 AS cr02, 0 AS cr03, 0 AS cr04, 0 AS cr05, 0 AS cr06, 0 AS cr07, 0 AS cr08, 0 AS cr09, 0 AS cr10, 0 AS cr11, 0 AS cr12
        , 0 AS balance00, 0 AS balance01, 0 AS balance02, 0 AS balance03, 0 AS balance04, 0 AS balance05, 0 AS balance06, 0 AS balance07, 0 AS balance08, 0 AS balance09, 0 AS balance10, 0 AS balance11, 0 AS balance12
        , bs_sign
        , 0 AS bd00, 0 AS bd01, 0 AS bd02, 0 AS bd03, 0 AS bd04, 0 AS bd05, 0 AS bd06, 0 AS bd07, 0 AS bd08, 0 AS bd09, 0 AS bd10, 0 AS bd11, 0 AS bd12
        , 0 AS bc00, 0 AS bc01, 0 AS bc02, 0 AS bc03, 0 AS bc04, 0 AS bc05, 0 AS bc06, 0 AS bc07, 0 AS bc08, 0 AS bc09, 0 AS bc10, 0 AS bc11, 0 AS bc12
        , COALESCE(debit, 0) AS debit, COALESCE(credit, 0) AS credit
    FROM
        dbo27.budget_balances r
        INNER JOIN dbo27.ledger_accounts a ON a.id = r.account_id
        LEFT OUTER JOIN dbo27.ledger_account_types t ON t.id = a.account_type_id
    WHERE
        r.category_id = @category_id
        AND r.time_id = @time_id
        AND (r.debit IS NOT NULL OR r.credit IS NOT NULL)
        --AND @entity_id IS NULL
)

, c1 AS (

    -- total dr, cr, balances + debit and credit balances on ledger and subsidiary accounts

    SELECT
        ledger_account_id
        , COALESCE(SUM(dr01), 0) AS dr01, COALESCE(SUM(dr02), 0) AS dr02, COALESCE(SUM(dr03), 0) AS dr03, COALESCE(SUM(dr04), 0) AS dr04, COALESCE(SUM(dr05), 0) AS dr05, COALESCE(SUM(dr06), 0) AS dr06, COALESCE(SUM(dr07), 0) AS dr07, COALESCE(SUM(dr08), 0) AS dr08, COALESCE(SUM(dr09), 0) AS dr09, COALESCE(SUM(dr10), 0) AS dr10, COALESCE(SUM(dr11), 0) AS dr11, COALESCE(SUM(dr12), 0) AS dr12
        , COALESCE(SUM(cr01), 0) AS cr01, COALESCE(SUM(cr02), 0) AS cr02, COALESCE(SUM(cr03), 0) AS cr03, COALESCE(SUM(cr04), 0) AS cr04, COALESCE(SUM(cr05), 0) AS cr05, COALESCE(SUM(cr06), 0) AS cr06, COALESCE(SUM(cr07), 0) AS cr07, COALESCE(SUM(cr08), 0) AS cr08, COALESCE(SUM(cr09), 0) AS cr09, COALESCE(SUM(cr10), 0) AS cr10, COALESCE(SUM(cr11), 0) AS cr11, COALESCE(SUM(cr12), 0) AS cr12
        , SUM(debit) AS debit
        , SUM(credit) AS credit
        , SUM(debit - credit) AS balance00
        , SUM(debit - credit + dr01 - cr01) AS balance01
        , SUM(debit - credit + dr01 - cr01 + dr02 - cr02) AS balance02
        , SUM(debit - credit + dr01 - cr01 + dr02 - cr02 + dr03 - cr03) AS balance03
        , SUM(debit - credit + dr01 - cr01 + dr02 - cr02 + dr03 - cr03 + dr04 - cr04) AS balance04
        , SUM(debit - credit + dr01 - cr01 + dr02 - cr02 + dr03 - cr03 + dr04 - cr04 + dr05 - cr05) AS balance05
        , SUM(debit - credit + dr01 - cr01 + dr02 - cr02 + dr03 - cr03 + dr04 - cr04 + dr05 - cr05 + dr06 - cr06) AS balance06
        , SUM(debit - credit + dr01 - cr01 + dr02 - cr02 + dr03 - cr03 + dr04 - cr04 + dr05 - cr05 + dr06 - cr06 + dr07 - cr07) AS balance07
        , SUM(debit - credit + dr01 - cr01 + dr02 - cr02 + dr03 - cr03 + dr04 - cr04 + dr05 - cr05 + dr06 - cr06 + dr07 - cr07 + dr08 - cr08) AS balance08
        , SUM(debit - credit + dr01 - cr01 + dr02 - cr02 + dr03 - cr03 + dr04 - cr04 + dr05 - cr05 + dr06 - cr06 + dr07 - cr07 + dr08 - cr08 + dr09 - cr09) AS balance09
        , SUM(debit - credit + dr01 - cr01 + dr02 - cr02 + dr03 - cr03 + dr04 - cr04 + dr05 - cr05 + dr06 - cr06 + dr07 - cr07 + dr08 - cr08 + dr09 - cr09 + dr10 - cr10) AS balance10
        , SUM(debit - credit + dr01 - cr01 + dr02 - cr02 + dr03 - cr03 + dr04 - cr04 + dr05 - cr05 + dr06 - cr06 + dr07 - cr07 + dr08 - cr08 + dr09 - cr09 + dr10 - cr10 + dr11 - cr11) AS balance11
        , SUM(debit - credit + dr01 - cr01 + dr02 - cr02 + dr03 - cr03 + dr04 - cr04 + dr05 - cr05 + dr06 - cr06 + dr07 - cr07 + dr08 - cr08 + dr09 - cr09 + dr10 - cr10 + dr11 - cr11 + dr12 - cr12) AS balance12
        , COALESCE(SUM(bd00), 0) AS bd00, COALESCE(SUM(bd01), 0) AS bd01, COALESCE(SUM(bd02), 0) AS bd02, COALESCE(SUM(bd03), 0) AS bd03, COALESCE(SUM(bd04), 0) AS bd04, COALESCE(SUM(bd05), 0) AS bd05, COALESCE(SUM(bd06), 0) AS bd06, COALESCE(SUM(bd07), 0) AS bd07, COALESCE(SUM(bd08), 0) AS bd08, COALESCE(SUM(bd09), 0) AS bd09, COALESCE(SUM(bd10), 0) AS bd10, COALESCE(SUM(bd11), 0) AS bd11, COALESCE(SUM(bd12), 0) AS bd12
        , COALESCE(SUM(bc00), 0) AS bc00, COALESCE(SUM(bc01), 0) AS bc01, COALESCE(SUM(bc02), 0) AS bc02, COALESCE(SUM(bc03), 0) AS bc03, COALESCE(SUM(bc04), 0) AS bc04, COALESCE(SUM(bc05), 0) AS bc05, COALESCE(SUM(bc06), 0) AS bc06, COALESCE(SUM(bc07), 0) AS bc07, COALESCE(SUM(bc08), 0) AS bc08, COALESCE(SUM(bc09), 0) AS bc09, COALESCE(SUM(bc10), 0) AS bc10, COALESCE(SUM(bc11), 0) AS bc11, COALESCE(SUM(bc12), 0) AS bc12
        , MAX(bs_sign) AS bs_sign
    FROM
        b4
    GROUP BY
        ledger_account_id
)

, c2 AS (

    -- final data on ledger accounts

    SELECT
        ledger_account_id
        , dr01, dr02, dr03, dr04, dr05, dr06, dr07, dr08, dr09, dr10, dr11, dr12
        , cr01, cr02, cr03, cr04, cr05, cr06, cr07, cr08, cr09, cr10, cr11, cr12
