CREATE PROCEDURE [xls27].[usp_budget_reports_raw]
    @company_id int = NULL
    , @category_id int = NULL
    , @time_id int = NULL
    , @entity_id int = NULL
    , @subsidiary_account_id int = NULL
    , @region_id int = NULL
    , @product_id int = NULL
    , @report_id int = NULL
    , @level tinyint = NULL
    , @unit int = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE username = USER_NAME() AND is_active = 1 AND is_default = 1)
ELSE
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

-- Factor like 1, 0.001, or 0.000001

DECLARE @factor float = 1.0 / COALESCE(@unit, 1)

;WITH cf AS (
    SELECT
        r.account_id
        , COALESCE(ROUND(r.payments01 * COALESCE(cr.period01, 1), 2), 0) AS data01
        , COALESCE(ROUND(r.payments02 * COALESCE(cr.period02, 1), 2), 0) AS data02
        , COALESCE(ROUND(r.payments03 * COALESCE(cr.period03, 1), 2), 0) AS data03
        , COALESCE(ROUND(r.payments04 * COALESCE(cr.period04, 1), 2), 0) AS data04
        , COALESCE(ROUND(r.payments05 * COALESCE(cr.period05, 1), 2), 0) AS data05
        , COALESCE(ROUND(r.payments06 * COALESCE(cr.period06, 1), 2), 0) AS data06
        , COALESCE(ROUND(r.payments07 * COALESCE(cr.period07, 1), 2), 0) AS data07
        , COALESCE(ROUND(r.payments08 * COALESCE(cr.period08, 1), 2), 0) AS data08
        , COALESCE(ROUND(r.payments09 * COALESCE(cr.period09, 1), 2), 0) AS data09
        , COALESCE(ROUND(r.payments10 * COALESCE(cr.period10, 1), 2), 0) AS data10
        , COALESCE(ROUND(r.payments11 * COALESCE(cr.period11, 1), 2), 0) AS data11
        , COALESCE(ROUND(r.payments12 * COALESCE(cr.period12, 1), 2), 0) AS data12
    FROM
        dbo27.budget_requests r
        INNER JOIN dbo27.members a ON a.id = r.account_id
        LEFT OUTER JOIN dbo27.budget_currency_rates cr ON cr.unit_id = r.unit_id AND cr.time_id = r.time_id AND cr.category_id = r.category_id
    WHERE
        r.category_id = @category_id
        AND r.time_id = @time_id
        AND a.company_id = @company_id
        AND COALESCE(r.entity_id, 0) = COALESCE(@entity_id, r.entity_id, 0)
        AND COALESCE(r.subsidiary_account_id, 0) = COALESCE(@subsidiary_account_id, r.subsidiary_account_id, 0)
        AND COALESCE(r.region_id, 0) = COALESCE(@region_id, r.region_id, 0)
        AND COALESCE(r.product_id, 0) = COALESCE(@product_id, r.product_id, 0)
)
, cfs AS (
    SELECT
        account_id
        , SUM(data01) AS data01
        , SUM(data02) AS data02
        , SUM(data03) AS data03
        , SUM(data04) AS data04
        , SUM(data05) AS data05
        , SUM(data06) AS data06
        , SUM(data07) AS data07
        , SUM(data08) AS data08
        , SUM(data09) AS data09
        , SUM(data10) AS data10
        , SUM(data11) AS data11
        , SUM(data12) AS data12
    FROM
        cf
    GROUP BY
        account_id
)
, pl AS (
    SELECT
        r.account_id
        , COALESCE(ROUND(r.charges01 * COALESCE(cr.period01, 1), 2), 0) - CASE WHEN a.vat_debit_id IS NOT NULL AND a.vat_credit_id IS NOT NULL THEN COALESCE(ROUND(r.charges01 * COALESCE(cr.period01, 1) * v.factor, 2), 0) ELSE 0 END AS data01
        , COALESCE(ROUND(r.charges02 * COALESCE(cr.period02, 1), 2), 0) - CASE WHEN a.vat_debit_id IS NOT NULL AND a.vat_credit_id IS NOT NULL THEN COALESCE(ROUND(r.charges02 * COALESCE(cr.period02, 1) * v.factor, 2), 0) ELSE 0 END AS data02
        , COALESCE(ROUND(r.charges03 * COALESCE(cr.period03, 1), 2), 0) - CASE WHEN a.vat_debit_id IS NOT NULL AND a.vat_credit_id IS NOT NULL THEN COALESCE(ROUND(r.charges03 * COALESCE(cr.period03, 1) * v.factor, 2), 0) ELSE 0 END AS data03
        , COALESCE(ROUND(r.charges04 * COALESCE(cr.period04, 1), 2), 0) - CASE WHEN a.vat_debit_id IS NOT NULL AND a.vat_credit_id IS NOT NULL THEN COALESCE(ROUND(r.charges04 * COALESCE(cr.period04, 1) * v.factor, 2), 0) ELSE 0 END AS data04
        , COALESCE(ROUND(r.charges05 * COALESCE(cr.period05, 1), 2), 0) - CASE WHEN a.vat_debit_id IS NOT NULL AND a.vat_credit_id IS NOT NULL THEN COALESCE(ROUND(r.charges05 * COALESCE(cr.period05, 1) * v.factor, 2), 0) ELSE 0 END AS data05
        , COALESCE(ROUND(r.charges06 * COALESCE(cr.period06, 1), 2), 0) - CASE WHEN a.vat_debit_id IS NOT NULL AND a.vat_credit_id IS NOT NULL THEN COALESCE(ROUND(r.charges06 * COALESCE(cr.period06, 1) * v.factor, 2), 0) ELSE 0 END AS data06
        , COALESCE(ROUND(r.charges07 * COALESCE(cr.period07, 1), 2), 0) - CASE WHEN a.vat_debit_id IS NOT NULL AND a.vat_credit_id IS NOT NULL THEN COALESCE(ROUND(r.charges07 * COALESCE(cr.period07, 1) * v.factor, 2), 0) ELSE 0 END AS data07
        , COALESCE(ROUND(r.charges08 * COALESCE(cr.period08, 1), 2), 0) - CASE WHEN a.vat_debit_id IS NOT NULL AND a.vat_credit_id IS NOT NULL THEN COALESCE(ROUND(r.charges08 * COALESCE(cr.period08, 1) * v.factor, 2), 0) ELSE 0 END AS data08
        , COALESCE(ROUND(r.charges09 * COALESCE(cr.period09, 1), 2), 0) - CASE WHEN a.vat_debit_id IS NOT NULL AND a.vat_credit_id IS NOT NULL THEN COALESCE(ROUND(r.charges09 * COALESCE(cr.period09, 1) * v.factor, 2), 0) ELSE 0 END AS data09
        , COALESCE(ROUND(r.charges10 * COALESCE(cr.period10, 1), 2), 0) - CASE WHEN a.vat_debit_id IS NOT NULL AND a.vat_credit_id IS NOT NULL THEN COALESCE(ROUND(r.charges10 * COALESCE(cr.period10, 1) * v.factor, 2), 0) ELSE 0 END AS data10
        , COALESCE(ROUND(r.charges11 * COALESCE(cr.period11, 1), 2), 0) - CASE WHEN a.vat_debit_id IS NOT NULL AND a.vat_credit_id IS NOT NULL THEN COALESCE(ROUND(r.charges11 * COALESCE(cr.period11, 1) * v.factor, 2), 0) ELSE 0 END AS data11
        , COALESCE(ROUND(r.charges12 * COALESCE(cr.period12, 1), 2), 0) - CASE WHEN a.vat_debit_id IS NOT NULL AND a.vat_credit_id IS NOT NULL THEN COALESCE(ROUND(r.charges12 * COALESCE(cr.period12, 1) * v.factor, 2), 0) ELSE 0 END AS data12
    FROM
        dbo27.budget_requests r
        INNER JOIN dbo27.members a ON a.id = r.account_id
        LEFT OUTER JOIN dbo27.budget_currency_rates cr ON cr.unit_id = r.unit_id AND cr.time_id = r.time_id AND cr.category_id = r.category_id
        LEFT OUTER JOIN dbo27.vat_rates v ON v.id = r.vat_rate_id
    WHERE
        r.category_id = @category_id
        AND r.time_id = @time_id
        AND a.company_id = @company_id
        AND COALESCE(r.entity_id, 0) = COALESCE(@entity_id, r.entity_id, 0)
        AND COALESCE(r.subsidiary_account_id, 0) = COALESCE(@subsidiary_account_id, r.subsidiary_account_id, 0)
        AND COALESCE(r.region_id, 0) = COALESCE(@region_id, r.region_id, 0)
        AND COALESCE(r.product_id, 0) = COALESCE(@product_id, r.product_id, 0)
)
, pls AS (
    SELECT
        account_id
        , SUM(data01) AS data01
        , SUM(data02) AS data02
        , SUM(data03) AS data03
        , SUM(data04) AS data04
        , SUM(data05) AS data05
        , SUM(data06) AS data06
        , SUM(data07) AS data07
        , SUM(data08) AS data08
        , SUM(data09) AS data09
        , SUM(data10) AS data10
        , SUM(data11) AS data11
        , SUM(data12) AS data12
    FROM
        pl
    GROUP BY
        account_id
)
, factors (id, parent_id) AS (
    SELECT
        id
        , parent_id
    FROM
        dbo27.members
    WHERE
        parent_id IS NOT NULL

    UNION ALL
    SELECT
        p.id
        , m.parent_id
    FROM
        factors p
        INNER JOIN dbo27.members m ON m.id = p.parent_id
    WHERE
        m.parent_id IS NOT NULL
        AND NOT p.id = m.parent_id
)

SELECT
    report
    , level
    , code
    , name
    , data01
    , data02
    , data03
    , data04
    , data05
    , data06
    , data07
    , data08
    , data09
    , data10
    , data11
    , data12
FROM
    (
        SELECT
            account_id
            , 1 AS report
            , CASE WHEN a.parent_id IS NULL THEN 0 ELSE 1 END AS level
            , a.code
            , a.name
            , data01 * pl_sign AS data01
            , data02 * pl_sign AS data02
            , data03 * pl_sign AS data03
            , data04 * pl_sign AS data04
            , data05 * pl_sign AS data05
            , data06 * pl_sign AS data06
            , data07 * pl_sign AS data07
            , data08 * pl_sign AS data08
            , data09 * pl_sign AS data09
            , data10 * pl_sign AS data10
            , data11 * pl_sign AS data11
            , data12 * pl_sign AS data12
        FROM
            pls
            INNER JOIN dbo27.members a ON a.id = pls.account_id
            INNER JOIN dbo27.member_types t ON t.id = a.account_type_id

        UNION ALL
        SELECT
            p.id AS account_id
            , 1 AS report
            , CASE WHEN MAX(p.parent_id) IS NULL THEN 0 ELSE 1 END AS level
            , MAX(p.code) AS code
            , MAX(p.name) AS name
            , SUM(data01 * pl_sign) AS data01
            , SUM(data02 * pl_sign) AS data02
            , SUM(data03 * pl_sign) AS data03
            , SUM(data04 * pl_sign) AS data04
            , SUM(data05 * pl_sign) AS data05
            , SUM(data06 * pl_sign) AS data06
            , SUM(data07 * pl_sign) AS data07
            , SUM(data08 * pl_sign) AS data08
            , SUM(data09 * pl_sign) AS data09
            , SUM(data10 * pl_sign) AS data10
            , SUM(data11 * pl_sign) AS data11
            , SUM(data12 * pl_sign) AS data12
        FROM
            pls
            INNER JOIN dbo27.members a ON a.id = pls.account_id
            INNER JOIN dbo27.member_types t ON t.id = a.account_type_id
            INNER JOIN factors f ON f.id = pls.account_id
            INNER JOIN dbo27.members p ON p.id = f.parent_id
        GROUP BY
            p.id

        UNION ALL
        SELECT
            NULL AS account_id
            , 4 AS report
            , 0 level
            , NULL AS code
            , NULL AS name
            , NULL AS data01
            , NULL AS data02
            , NULL AS data03
            , NULL AS data04
            , NULL AS data05
            , NULL AS data06
            , NULL AS data07
            , NULL AS data08
            , NULL AS data09
            , NULL AS data10
            , NULL AS data11
            , NULL AS data12

        UNION ALL
        SELECT
            account_id
            , 4 AS report
            , CASE WHEN a.parent_id IS NULL THEN 0 ELSE 1 END AS level
            , a.code
            , a.name
            , data01 * cf_sign AS data01
            , data02 * cf_sign AS data02
            , data03 * cf_sign AS data03
            , data04 * cf_sign AS data04
            , data05 * cf_sign AS data05
            , data06 * cf_sign AS data06
            , data07 * cf_sign AS data07
            , data08 * cf_sign AS data08
            , data09 * cf_sign AS data09
            , data10 * cf_sign AS data10
            , data11 * cf_sign AS data11
            , data12 * cf_sign AS data12
        FROM
            cfs
            INNER JOIN dbo27.members a ON a.id = cfs.account_id
            INNER JOIN dbo27.member_types t ON t.id = a.account_type_id

        UNION ALL
        SELECT
            p.id AS account_id
            , 4 AS report
            , CASE WHEN MAX(p.parent_id) IS NULL THEN 0 ELSE 1 END AS level
            , MAX(p.code) AS code
            , MAX(p.name) AS name
            , SUM(data01 * cf_sign) AS data01
            , SUM(data02 * cf_sign) AS data02
            , SUM(data03 * cf_sign) AS data03
            , SUM(data04 * cf_sign) AS data04
            , SUM(data05 * cf_sign) AS data05
            , SUM(data06 * cf_sign) AS data06
            , SUM(data07 * cf_sign) AS data07
            , SUM(data08 * cf_sign) AS data08
            , SUM(data09 * cf_sign) AS data09
            , SUM(data10 * cf_sign) AS data10
            , SUM(data11 * cf_sign) AS data11
            , SUM(data12 * cf_sign) AS data12
        FROM
            cfs
            INNER JOIN dbo27.members a ON a.id = cfs.account_id
            INNER JOIN dbo27.member_types t ON t.id = a.account_type_id
            INNER JOIN factors f ON f.id = cfs.account_id
            INNER JOIN dbo27.members p ON p.id = f.parent_id
        GROUP BY
            p.id
    ) d
WHERE
    (code IS NULL OR data01 <> 0 OR  data02 <> 0 OR  data03 <> 0 OR  data04 <> 0 OR  data05 <> 0 OR  data06 <> 0 OR  data07 <> 0 OR  data08 <> 0 OR  data09 <> 0 OR  data10 <> 0 OR  data11 <> 0 OR  data12 <> 0)
    AND level <= COALESCE(@level, 10)
ORDER BY
    report
    , code

END


