CREATE PROCEDURE [xls27].[usp_budget_request1]
    @company_id int = NULL
    , @category_id int = NULL
    , @time_id int = NULL
    , @entity_id int = NULL
    , @rows int = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

DECLARE @message nvarchar(max)

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.members WHERE id IN (@category_id, @time_id))

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE username = USER_NAME() AND is_active = 1 AND is_default = 1)
ELSE
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

SET @category_id = (SELECT id FROM dbo27.members WHERE id = @category_id AND company_id = @company_id)

IF @category_id IS NOT NULL AND @time_id IS NOT NULL AND @entity_id IS NOT NULL
    AND EXISTS (
        SELECT
            p.name
        FROM
            sys.database_principals p
            INNER JOIN sys.database_role_members rm ON rm.member_principal_id = p.principal_id
        WHERE
            p.name = USER_NAME()
            AND rm.role_principal_id = USER_ID('budgeting_app_users')
    ) AND 0 < (
        SELECT
            COUNT(*) - SUM(COALESCE(mp.select_permission, 0))
        FROM
            dbo27.members m
            LEFT OUTER JOIN dbo27.member_permissions mp ON mp.member_id = m.id AND mp.username = USER_NAME()
        WHERE
            m.id IN (@category_id, @time_id, @entity_id)
    )
    BEGIN
    SET @category_id = NULL
    SET @time_id = NULL
    SET @entity_id = NULL
    END

IF @rows IS NULL SET @rows = 5

DECLARE @row_count int = COALESCE((
    SELECT
        MAX(t.row_index) AS row
    FROM
        dbo27.budget_requests t
    WHERE
        t.[entity_id] = @entity_id
        AND t.category_id = @category_id
        AND t.[time_id] = @time_id
        AND t.is_empty = 0
    ), 0)

IF @row_count < @rows SET @row_count = @rows

;WITH e1(n) AS
(
    SELECT 1 UNION ALL SELECT 1 UNION ALL SELECT 1 UNION ALL SELECT 1 UNION ALL SELECT 1 UNION ALL
    SELECT 1 UNION ALL SELECT 1 UNION ALL SELECT 1 UNION ALL SELECT 1 UNION ALL SELECT 1
),                                              -- 10
e2(n) AS (SELECT 1 FROM e1 CROSS JOIN e1 AS b), -- 10*10
e3(n) AS (SELECT 1 FROM e1 CROSS JOIN e2)       -- 10*100

SELECT
    n.row_index
    , t.row_format
    , t.description
    , t.account_id
    , COALESCE(tr1.name, m1.name) AS account_name
    , t.subsidiary_account_id
    , t.region_id
    , t.product_id
    , t.vat_rate_id
    , t.unit_id
    , '=[@payments01]+[@payments02]+[@payments03]+[@payments04]+[@payments05]+[@payments06]+[@payments07]+[@payments08]+[@payments09]+[@payments10]+[@payments11]+[@payments12]' AS total_payments
    , '=[@charges01]+[@charges02]+[@charges03]+[@charges04]+[@charges05]+[@charges06]+[@charges07]+[@charges08]+[@charges09]+[@charges10]+[@charges11]+[@charges12]' AS total_charges
    , t.balance00
    , t.payments01
    , t.charges01
    , '=[@balance00]-[@payments01]+[@charges01]' balance01
    , t.payments02
    , t.charges02
    , '=[@balance01]-[@payments02]+[@charges02]' balance02
    , t.payments03
    , t.charges03
    , '=[@balance02]-[@payments03]+[@charges03]' balance03
    , t.payments04
    , t.charges04
    , '=[@balance03]-[@payments04]+[@charges04]' balance04
    , t.payments05
    , t.charges05
    , '=[@balance04]-[@payments05]+[@charges05]' balance05
    , t.payments06
    , t.charges06
    , '=[@balance05]-[@payments06]+[@charges06]' balance06
    , t.payments07
    , t.charges07
    , '=[@balance06]-[@payments07]+[@charges07]' balance07
    , t.payments08
    , t.charges08
    , '=[@balance07]-[@payments08]+[@charges08]' balance08
    , t.payments09
    , t.charges09
    , '=[@balance08]-[@payments09]+[@charges09]' balance09
    , t.payments10
    , t.charges10
    , '=[@balance09]-[@payments10]+[@charges10]' balance10
    , t.payments11
    , t.charges11
    , '=[@balance10]-[@payments11]+[@charges11]' balance11
    , t.payments12
    , t.charges12
    , '=[@balance11]-[@payments12]+[@charges12]' balance12
    , t.row_formulas
    , t.row_comments
    , COALESCE(u.name, t.modified_by) AS modified_by
    , t.modified_on
FROM
    (SELECT row_index = ROW_NUMBER() OVER (ORDER BY n) FROM e3) n
    LEFT OUTER JOIN dbo27.budget_requests t ON
        t.row_index = n.row_index
        AND t.category_id = @category_id
        AND t.[time_id] = @time_id
        AND t.[entity_id] = @entity_id
    LEFT OUTER JOIN dbo27.members m1 ON m1.id = t.account_id
    LEFT OUTER JOIN dbo27.users u ON u.company_id = @company_id AND u.username = t.modified_by
    LEFT OUTER JOIN dbo27.translations tr1 ON tr1.company_id = @company_id AND tr1.table_id = 1 AND tr1.member_id = m1.id AND tr1.language = @data_language
WHERE
    n.row_index BETWEEN 1 AND @row_count
ORDER BY
    n.row_index

END


