-- Description: The procedure selects budget requests
-- =============================================

CREATE PROCEDURE [xls27].[usp_budget_requests]
    @company_id int = NULL
    , @category_id int = NULL
    , @time_id int = NULL
    , @entity_id int = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.members WHERE id IN (@category_id, @time_id, @entity_id))

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE username = USER_NAME() AND is_active = 1 AND is_default = 1)
ELSE
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

SELECT
    r.category_id
    , r.time_id
    , r.entity_id
    , COALESCE(tr1.name, m1.name) AS category_name
    , COALESCE(tr2.name, m2.name) AS period_name
    , COALESCE(tr3.name, m3.name) AS entity_name
    , r.rows
    , r.modified_on
FROM
    (
        SELECT
            r.category_id
            , r.time_id
            , r.entity_id
            , MAX(r.modified_on) AS modified_on
            , COUNT(*) - SUM(CAST(r.is_empty AS tinyint)) AS rows
        FROM
            dbo27.budget_requests r
            INNER JOIN dbo27.members m1 ON m1.id = r.category_id
        WHERE
            m1.company_id = @company_id
            AND r.category_id = COALESCE(@category_id, r.category_id)
            AND r.time_id = COALESCE(@time_id, r.time_id)
            AND r.entity_id = COALESCE(@entity_id, r.entity_id)
        GROUP BY
            r.category_id
            , r.time_id
            , r.entity_id
    ) r
    INNER JOIN dbo27.members m1 ON m1.id = r.category_id
    INNER JOIN dbo27.members m2 ON m2.id = r.time_id
    INNER JOIN dbo27.members m3 ON m3.id = r.entity_id
    LEFT OUTER JOIN dbo27.translations tr1 ON tr1.company_id = @company_id AND tr1.table_id = 1 AND tr1.member_id = m1.id AND tr1.language = @data_language
    LEFT OUTER JOIN dbo27.translations tr2 ON tr2.company_id = @company_id AND tr2.table_id = 1 AND tr2.member_id = m2.id AND tr2.language = @data_language
    LEFT OUTER JOIN dbo27.translations tr3 ON tr3.company_id = @company_id AND tr3.table_id = 1 AND tr3.member_id = m3.id AND tr3.language = @data_language
ORDER BY
    r.category_id
    , r.time_id
    , r.entity_id

END


