        , COALESCE(ROUND(r.payments12 * COALESCE(cr.period12, 1), 2), 0) AS payments12

        , COALESCE(ROUND(r.charges01 * COALESCE(cr.period01, 1), 2), 0) AS charges01
        , COALESCE(ROUND(r.charges02 * COALESCE(cr.period02, 1), 2), 0) AS charges02
        , COALESCE(ROUND(r.charges03 * COALESCE(cr.period03, 1), 2), 0) AS charges03
        , COALESCE(ROUND(r.charges04 * COALESCE(cr.period04, 1), 2), 0) AS charges04
        , COALESCE(ROUND(r.charges05 * COALESCE(cr.period05, 1), 2), 0) AS charges05
        , COALESCE(ROUND(r.charges06 * COALESCE(cr.period06, 1), 2), 0) AS charges06
        , COALESCE(ROUND(r.charges07 * COALESCE(cr.period07, 1), 2), 0) AS charges07
        , COALESCE(ROUND(r.charges08 * COALESCE(cr.period08, 1), 2), 0) AS charges08
        , COALESCE(ROUND(r.charges09 * COALESCE(cr.period09, 1), 2), 0) AS charges09
        , COALESCE(ROUND(r.charges10 * COALESCE(cr.period10, 1), 2), 0) AS charges10
        , COALESCE(ROUND(r.charges11 * COALESCE(cr.period11, 1), 2), 0) AS charges11
        , COALESCE(ROUND(r.charges12 * COALESCE(cr.period12, 1), 2), 0) AS charges12

        , COALESCE(ROUND(r.payments01 * COALESCE(cr.period01, 1) * t.cf_sign, 2), 0) AS cf01
        , COALESCE(ROUND(r.payments02 * COALESCE(cr.period02, 1) * t.cf_sign, 2), 0) AS cf02
        , COALESCE(ROUND(r.payments03 * COALESCE(cr.period03, 1) * t.cf_sign, 2), 0) AS cf03
        , COALESCE(ROUND(r.payments04 * COALESCE(cr.period04, 1) * t.cf_sign, 2), 0) AS cf04
        , COALESCE(ROUND(r.payments05 * COALESCE(cr.period05, 1) * t.cf_sign, 2), 0) AS cf05
        , COALESCE(ROUND(r.payments06 * COALESCE(cr.period06, 1) * t.cf_sign, 2), 0) AS cf06
        , COALESCE(ROUND(r.payments07 * COALESCE(cr.period07, 1) * t.cf_sign, 2), 0) AS cf07
        , COALESCE(ROUND(r.payments08 * COALESCE(cr.period08, 1) * t.cf_sign, 2), 0) AS cf08
        , COALESCE(ROUND(r.payments09 * COALESCE(cr.period09, 1) * t.cf_sign, 2), 0) AS cf09
        , COALESCE(ROUND(r.payments10 * COALESCE(cr.period10, 1) * t.cf_sign, 2), 0) AS cf10
        , COALESCE(ROUND(r.payments11 * COALESCE(cr.period11, 1) * t.cf_sign, 2), 0) AS cf11
        , COALESCE(ROUND(r.payments12 * COALESCE(cr.period12, 1) * t.cf_sign, 2), 0) AS cf12

        , COALESCE(ROUND(r.charges01 * COALESCE(cr.period01, 1) * t.cf_sign * v.factor, 2), 0) AS vat01
        , COALESCE(ROUND(r.charges02 * COALESCE(cr.period02, 1) * t.cf_sign * v.factor, 2), 0) AS vat02
        , COALESCE(ROUND(r.charges03 * COALESCE(cr.period03, 1) * t.cf_sign * v.factor, 2), 0) AS vat03
        , COALESCE(ROUND(r.charges04 * COALESCE(cr.period04, 1) * t.cf_sign * v.factor, 2), 0) AS vat04
        , COALESCE(ROUND(r.charges05 * COALESCE(cr.period05, 1) * t.cf_sign * v.factor, 2), 0) AS vat05
        , COALESCE(ROUND(r.charges06 * COALESCE(cr.period06, 1) * t.cf_sign * v.factor, 2), 0) AS vat06
        , COALESCE(ROUND(r.charges07 * COALESCE(cr.period07, 1) * t.cf_sign * v.factor, 2), 0) AS vat07
        , COALESCE(ROUND(r.charges08 * COALESCE(cr.period08, 1) * t.cf_sign * v.factor, 2), 0) AS vat08
        , COALESCE(ROUND(r.charges09 * COALESCE(cr.period09, 1) * t.cf_sign * v.factor, 2), 0) AS vat09
        , COALESCE(ROUND(r.charges10 * COALESCE(cr.period10, 1) * t.cf_sign * v.factor, 2), 0) AS vat10
        , COALESCE(ROUND(r.charges11 * COALESCE(cr.period11, 1) * t.cf_sign * v.factor, 2), 0) AS vat11
        , COALESCE(ROUND(r.charges12 * COALESCE(cr.period12, 1) * t.cf_sign * v.factor, 2), 0) AS vat12

        , COALESCE((ROUND(r.charges01 * COALESCE(cr.period01, 1), 2) - COALESCE(ROUND(r.charges01 * COALESCE(cr.period01, 1) * v.factor, 2), 0)) * t.pl_sign, 0) AS pl01
        , COALESCE((ROUND(r.charges02 * COALESCE(cr.period02, 1), 2) - COALESCE(ROUND(r.charges02 * COALESCE(cr.period02, 1) * v.factor, 2), 0)) * t.pl_sign, 0) AS pl02
        , COALESCE((ROUND(r.charges03 * COALESCE(cr.period03, 1), 2) - COALESCE(ROUND(r.charges03 * COALESCE(cr.period03, 1) * v.factor, 2), 0)) * t.pl_sign, 0) AS pl03
        , COALESCE((ROUND(r.charges04 * COALESCE(cr.period04, 1), 2) - COALESCE(ROUND(r.charges04 * COALESCE(cr.period04, 1) * v.factor, 2), 0)) * t.pl_sign, 0) AS pl04
        , COALESCE((ROUND(r.charges05 * COALESCE(cr.period05, 1), 2) - COALESCE(ROUND(r.charges05 * COALESCE(cr.period05, 1) * v.factor, 2), 0)) * t.pl_sign, 0) AS pl05
        , COALESCE((ROUND(r.charges06 * COALESCE(cr.period06, 1), 2) - COALESCE(ROUND(r.charges06 * COALESCE(cr.period06, 1) * v.factor, 2), 0)) * t.pl_sign, 0) AS pl06
        , COALESCE((ROUND(r.charges07 * COALESCE(cr.period07, 1), 2) - COALESCE(ROUND(r.charges07 * COALESCE(cr.period07, 1) * v.factor, 2), 0)) * t.pl_sign, 0) AS pl07
        , COALESCE((ROUND(r.charges08 * COALESCE(cr.period08, 1), 2) - COALESCE(ROUND(r.charges08 * COALESCE(cr.period08, 1) * v.factor, 2), 0)) * t.pl_sign, 0) AS pl08
        , COALESCE((ROUND(r.charges09 * COALESCE(cr.period09, 1), 2) - COALESCE(ROUND(r.charges09 * COALESCE(cr.period09, 1) * v.factor, 2), 0)) * t.pl_sign, 0) AS pl09
        , COALESCE((ROUND(r.charges10 * COALESCE(cr.period10, 1), 2) - COALESCE(ROUND(r.charges10 * COALESCE(cr.period10, 1) * v.factor, 2), 0)) * t.pl_sign, 0) AS pl10
        , COALESCE((ROUND(r.charges11 * COALESCE(cr.period11, 1), 2) - COALESCE(ROUND(r.charges11 * COALESCE(cr.period11, 1) * v.factor, 2), 0)) * t.pl_sign, 0) AS pl11
        , COALESCE((ROUND(r.charges12 * COALESCE(cr.period12, 1), 2) - COALESCE(ROUND(r.charges12 * COALESCE(cr.period12, 1) * v.factor, 2), 0)) * t.pl_sign, 0) AS pl12

        , CASE WHEN t.cf_sign = -1 AND t.pl_sign = 0 THEN COALESCE((ROUND(r.charges01 * COALESCE(cr.period01, 1), 2) - COALESCE(ROUND(r.charges01 * COALESCE(cr.period01, 1) * v.factor, 2), 0)), 0) WHEN t.cf_sign = 0 AND t.pl_sign = -1 THEN COALESCE(-r.charges01, 0) ELSE 0 END AS assets01
