        , CASE WHEN t.cf_sign = -1 AND t.pl_sign = 0 THEN COALESCE((ROUND(r.charges03 * COALESCE(cr.period03, 1), 2) - COALESCE(ROUND(r.charges03 * COALESCE(cr.period03, 1) * v.factor, 2), 0)), 0) WHEN t.cf_sign = 0 AND t.pl_sign = -1 THEN COALESCE(-r.charges03, 0) ELSE 0 END AS assets03
        , CASE WHEN t.cf_sign = -1 AND t.pl_sign = 0 THEN COALESCE((ROUND(r.charges04 * COALESCE(cr.period04, 1), 2) - COALESCE(ROUND(r.charges04 * COALESCE(cr.period04, 1) * v.factor, 2), 0)), 0) WHEN t.cf_sign = 0 AND t.pl_sign = -1 THEN COALESCE(-r.charges04, 0) ELSE 0 END AS assets04
        , CASE WHEN t.cf_sign = -1 AND t.pl_sign = 0 THEN COALESCE((ROUND(r.charges05 * COALESCE(cr.period05, 1), 2) - COALESCE(ROUND(r.charges05 * COALESCE(cr.period05, 1) * v.factor, 2), 0)), 0) WHEN t.cf_sign = 0 AND t.pl_sign = -1 THEN COALESCE(-r.charges05, 0) ELSE 0 END AS assets05
        , CASE WHEN t.cf_sign = -1 AND t.pl_sign = 0 THEN COALESCE((ROUND(r.charges06 * COALESCE(cr.period06, 1), 2) - COALESCE(ROUND(r.charges06 * COALESCE(cr.period06, 1) * v.factor, 2), 0)), 0) WHEN t.cf_sign = 0 AND t.pl_sign = -1 THEN COALESCE(-r.charges06, 0) ELSE 0 END AS assets06
        , CASE WHEN t.cf_sign = -1 AND t.pl_sign = 0 THEN COALESCE((ROUND(r.charges07 * COALESCE(cr.period07, 1), 2) - COALESCE(ROUND(r.charges07 * COALESCE(cr.period07, 1) * v.factor, 2), 0)), 0) WHEN t.cf_sign = 0 AND t.pl_sign = -1 THEN COALESCE(-r.charges07, 0) ELSE 0 END AS assets07
        , CASE WHEN t.cf_sign = -1 AND t.pl_sign = 0 THEN COALESCE((ROUND(r.charges08 * COALESCE(cr.period08, 1), 2) - COALESCE(ROUND(r.charges08 * COALESCE(cr.period08, 1) * v.factor, 2), 0)), 0) WHEN t.cf_sign = 0 AND t.pl_sign = -1 THEN COALESCE(-r.charges08, 0) ELSE 0 END AS assets08
        , CASE WHEN t.cf_sign = -1 AND t.pl_sign = 0 THEN COALESCE((ROUND(r.charges09 * COALESCE(cr.period09, 1), 2) - COALESCE(ROUND(r.charges09 * COALESCE(cr.period09, 1) * v.factor, 2), 0)), 0) WHEN t.cf_sign = 0 AND t.pl_sign = -1 THEN COALESCE(-r.charges09, 0) ELSE 0 END AS assets09
        , CASE WHEN t.cf_sign = -1 AND t.pl_sign = 0 THEN COALESCE((ROUND(r.charges10 * COALESCE(cr.period10, 1), 2) - COALESCE(ROUND(r.charges10 * COALESCE(cr.period10, 1) * v.factor, 2), 0)), 0) WHEN t.cf_sign = 0 AND t.pl_sign = -1 THEN COALESCE(-r.charges10, 0) ELSE 0 END AS assets10
        , CASE WHEN t.cf_sign = -1 AND t.pl_sign = 0 THEN COALESCE((ROUND(r.charges11 * COALESCE(cr.period11, 1), 2) - COALESCE(ROUND(r.charges11 * COALESCE(cr.period11, 1) * v.factor, 2), 0)), 0) WHEN t.cf_sign = 0 AND t.pl_sign = -1 THEN COALESCE(-r.charges11, 0) ELSE 0 END AS assets11
        , CASE WHEN t.cf_sign = -1 AND t.pl_sign = 0 THEN COALESCE((ROUND(r.charges12 * COALESCE(cr.period12, 1), 2) - COALESCE(ROUND(r.charges12 * COALESCE(cr.period12, 1) * v.factor, 2), 0)), 0) WHEN t.cf_sign = 0 AND t.pl_sign = -1 THEN COALESCE(-r.charges12, 0) ELSE 0 END AS assets12

        ,  COALESCE(ROUND(r.balance00 * COALESCE(cr.balance00 * t.cf_sign, 1), 2), 0) AS bs00
        , (COALESCE(ROUND(r.charges01 * COALESCE(cr.period01, 1), 2), 0) - COALESCE(ROUND(r.payments01 * COALESCE(cr.period01, 1), 2), 0)) * t.cf_sign AS bs01
        , (COALESCE(ROUND(r.charges02 * COALESCE(cr.period02, 1), 2), 0) - COALESCE(ROUND(r.payments02 * COALESCE(cr.period02, 1), 2), 0)) * t.cf_sign AS bs02
        , (COALESCE(ROUND(r.charges03 * COALESCE(cr.period03, 1), 2), 0) - COALESCE(ROUND(r.payments03 * COALESCE(cr.period03, 1), 2), 0)) * t.cf_sign AS bs03
        , (COALESCE(ROUND(r.charges04 * COALESCE(cr.period04, 1), 2), 0) - COALESCE(ROUND(r.payments04 * COALESCE(cr.period04, 1), 2), 0)) * t.cf_sign AS bs04
        , (COALESCE(ROUND(r.charges05 * COALESCE(cr.period05, 1), 2), 0) - COALESCE(ROUND(r.payments05 * COALESCE(cr.period05, 1), 2), 0)) * t.cf_sign AS bs05
        , (COALESCE(ROUND(r.charges06 * COALESCE(cr.period06, 1), 2), 0) - COALESCE(ROUND(r.payments06 * COALESCE(cr.period06, 1), 2), 0)) * t.cf_sign AS bs06
        , (COALESCE(ROUND(r.charges07 * COALESCE(cr.period07, 1), 2), 0) - COALESCE(ROUND(r.payments07 * COALESCE(cr.period07, 1), 2), 0)) * t.cf_sign AS bs07
        , (COALESCE(ROUND(r.charges08 * COALESCE(cr.period08, 1), 2), 0) - COALESCE(ROUND(r.payments08 * COALESCE(cr.period08, 1), 2), 0)) * t.cf_sign AS bs08
        , (COALESCE(ROUND(r.charges09 * COALESCE(cr.period09, 1), 2), 0) - COALESCE(ROUND(r.payments09 * COALESCE(cr.period09, 1), 2), 0)) * t.cf_sign AS bs09
        , (COALESCE(ROUND(r.charges10 * COALESCE(cr.period10, 1), 2), 0) - COALESCE(ROUND(r.payments10 * COALESCE(cr.period10, 1), 2), 0)) * t.cf_sign AS bs10
        , (COALESCE(ROUND(r.charges11 * COALESCE(cr.period11, 1), 2), 0) - COALESCE(ROUND(r.payments11 * COALESCE(cr.period11, 1), 2), 0)) * t.cf_sign AS bs11
        , (COALESCE(ROUND(r.charges12 * COALESCE(cr.period12, 1), 2), 0) - COALESCE(ROUND(r.payments12 * COALESCE(cr.period12, 1), 2), 0)) * t.cf_sign AS bs12

        , COALESCE(r.balance00 * t.cf_sign, 0) AS cs00
        , (COALESCE(r.charges01, 0) - COALESCE(r.payments01, 0)) * t.cf_sign AS cs01
        , (COALESCE(r.charges02, 0) - COALESCE(r.payments02, 0)) * t.cf_sign AS cs02
        , (COALESCE(r.charges03, 0) - COALESCE(r.payments03, 0)) * t.cf_sign AS cs03
        , (COALESCE(r.charges04, 0) - COALESCE(r.payments04, 0)) * t.cf_sign AS cs04
        , (COALESCE(r.charges05, 0) - COALESCE(r.payments05, 0)) * t.cf_sign AS cs05
        , (COALESCE(r.charges06, 0) - COALESCE(r.payments06, 0)) * t.cf_sign AS cs06
        , (COALESCE(r.charges07, 0) - COALESCE(r.payments07, 0)) * t.cf_sign AS cs07
        , (COALESCE(r.charges08, 0) - COALESCE(r.payments08, 0)) * t.cf_sign AS cs08
        , (COALESCE(r.charges09, 0) - COALESCE(r.payments09, 0)) * t.cf_sign AS cs09
        , (COALESCE(r.charges10, 0) - COALESCE(r.payments10, 0)) * t.cf_sign AS cs10
        , (COALESCE(r.charges11, 0) - COALESCE(r.payments11, 0)) * t.cf_sign AS cs11
        , (COALESCE(r.charges12, 0) - COALESCE(r.payments12, 0)) * t.cf_sign AS cs12
    FROM
        dbo27.budget_requests r
        LEFT OUTER JOIN dbo27.vat_rates v ON v.id = r.vat_rate_id
        INNER JOIN dbo27.members a ON a.id = r.account_id
        LEFT OUTER JOIN dbo27.member_types t ON t.id = a.account_type_id
        LEFT OUTER JOIN dbo27.budget_currency_rates cr ON cr.unit_id = r.unit_id AND cr.time_id = r.time_id AND cr.category_id = r.category_id
    WHERE
        r.category_id = @category_id
        AND r.time_id = @time_id
        AND a.company_id = @company_id
        AND COALESCE(r.entity_id, 0) = COALESCE(@entity_id, r.entity_id, 0)
        AND COALESCE(r.subsidiary_account_id, 0) = COALESCE(@subsidiary_account_id, r.subsidiary_account_id, 0)
        AND COALESCE(r.region_id, 0) = COALESCE(@region_id, r.region_id, 0)
        AND COALESCE(r.product_id, 0) = COALESCE(@product_id, r.product_id, 0)
)

SELECT
    a.code
    , COALESCE(tr.name, a.name) AS name
    , COALESCE(t1.TRANSLATED_NAME, t.name) AS type
    , payments
    , charges
    , cf
    , pl
    , assets
    , vat
    , bs
    , pd.code AS payment_debit
    , pc.code AS payment_credit
    , cd.code AS charge_debit
    , cc.code AS charge_credit
    , vd.code AS vat_debit
    , vc.code AS vat_credit

    , t.payment_debit_type_id AS payment_debit_type_id
    , t.payment_credit_type_id AS payment_credit_type_id
    , t.charge_debit_type_id AS charge_debit_type_id
    , t.charge_credit_type_id AS charge_credit_type_id
    , t.vat_debit_type_id AS vat_debit_type_id
    , t.vat_credit_type_id AS vat_credit_type_id
FROM
    (
        SELECT
            t.account_id

            , SUM(payments01 * m01)
            + SUM(payments02 * m02)
            + SUM(payments03 * m03)
            + SUM(payments04 * m04)
            + SUM(payments05 * m05)
            + SUM(payments06 * m06)
            + SUM(payments07 * m07)
            + SUM(payments08 * m08)
            + SUM(payments09 * m09)
            + SUM(payments10 * m10)
            + SUM(payments11 * m11)
            + SUM(payments12 * m12) AS payments

            , SUM(charges01 * m01)
            + SUM(charges02 * m02)
            + SUM(charges03 * m03)
            + SUM(charges04 * m04)
            + SUM(charges05 * m05)
            + SUM(charges06 * m06)
            + SUM(charges07 * m07)
            + SUM(charges08 * m08)
            + SUM(charges09 * m09)
            + SUM(charges10 * m10)
            + SUM(charges11 * m11)
            + SUM(charges12 * m12) AS charges

            , SUM(cf01 * m01)
            + SUM(cf02 * m02)
            + SUM(cf03 * m03)
            + SUM(cf04 * m04)
            + SUM(cf05 * m05)
            + SUM(cf06 * m06)
            + SUM(cf07 * m07)
            + SUM(cf08 * m08)
            + SUM(cf09 * m09)
            + SUM(cf10 * m10)
            + SUM(cf11 * m11)
            + SUM(cf12 * m12) AS cf

            , SUM(pl01 * m01)
            + SUM(pl02 * m02)
            + SUM(pl03 * m03)
            + SUM(pl04 * m04)
            + SUM(pl05 * m05)
            + SUM(pl06 * m06)
            + SUM(pl07 * m07)
            + SUM(pl08 * m08)
            + SUM(pl09 * m09)
            + SUM(pl10 * m10)
            + SUM(pl11 * m11)
            + SUM(pl12 * m12) AS pl

            , SUM(assets01 * m01)
            + SUM(assets02 * m02)
            + SUM(assets03 * m03)
            + SUM(assets04 * m04)
            + SUM(assets05 * m05)
            + SUM(assets06 * m06)
            + SUM(assets07 * m07)
            + SUM(assets08 * m08)
            + SUM(assets09 * m09)
            + SUM(assets10 * m10)
            + SUM(assets11 * m11)
            + SUM(assets12 * m12) AS assets

            , SUM(vat01 * m01)
            + SUM(vat02 * m02)
            + SUM(vat03 * m03)
            + SUM(vat04 * m04)
            + SUM(vat05 * m05)
            + SUM(vat06 * m06)
            + SUM(vat07 * m07)
            + SUM(vat08 * m08)
            + SUM(vat09 * m09)
            + SUM(vat10 * m10)
            + SUM(vat11 * m11)
            + SUM(vat12 * m12) AS vat

            , SUM(bs01 * m01)
            + SUM(bs02 * m02)
            + SUM(bs03 * m03)
            + SUM(bs04 * m04)
            + SUM(bs05 * m05)
            + SUM(bs06 * m06)
            + SUM(bs07 * m07)
            + SUM(bs08 * m08)
            + SUM(bs09 * m09)
            + SUM(bs10 * m10)
            + SUM(bs11 * m11)
            + SUM(bs12 * m12) AS bs
        FROM
            raw_data t
            CROSS JOIN (VALUES
                ( CASE WHEN @first_month < 2 AND @last_month > 0 THEN @factor ELSE 0 END
                , CASE WHEN @first_month < 3 AND @last_month > 1 THEN @factor ELSE 0 END
                , CASE WHEN @first_month < 4 AND @last_month > 2 THEN @factor ELSE 0 END
                , CASE WHEN @first_month < 5 AND @last_month > 3 THEN @factor ELSE 0 END
                , CASE WHEN @first_month < 6 AND @last_month > 4 THEN @factor ELSE 0 END
                , CASE WHEN @first_month < 7 AND @last_month > 5 THEN @factor ELSE 0 END
                , CASE WHEN @first_month < 8 AND @last_month > 6 THEN @factor ELSE 0 END
                , CASE WHEN @first_month < 9 AND @last_month > 7 THEN @factor ELSE 0 END
                , CASE WHEN @first_month < 10 AND @last_month > 8 THEN @factor ELSE 0 END
                , CASE WHEN @first_month < 11 AND @last_month > 9 THEN @factor ELSE 0 END
                , CASE WHEN @first_month < 12 AND @last_month > 10 THEN @factor ELSE 0 END
                , CASE WHEN @first_month < 13 AND @last_month > 11 THEN @factor ELSE 0 END
                )) p(m01, m02, m03, m04, m05, m06, m07, m08, m09, m10, m11, m12)
        GROUP BY
            t.account_id
    ) d
    INNER JOIN dbo27.members a ON a.id = d.account_id
    LEFT OUTER JOIN dbo27.member_types t ON t.id = a.account_type_id
    LEFT OUTER JOIN dbo27.ledger_accounts pd ON pd.id = a.payment_debit_id
    LEFT OUTER JOIN dbo27.ledger_accounts pc ON pc.id = a.payment_credit_id
    LEFT OUTER JOIN dbo27.ledger_accounts cd ON cd.id = a.charge_debit_id
    LEFT OUTER JOIN dbo27.ledger_accounts cc ON cc.id = a.charge_credit_id
    LEFT OUTER JOIN dbo27.ledger_accounts vd ON vd.id = a.vat_debit_id
    LEFT OUTER JOIN dbo27.ledger_accounts vc ON vc.id = a.vat_credit_id
    LEFT OUTER JOIN dbo27.translations tr ON tr.company_id = @company_id AND tr.table_id = 1 AND tr.member_id = a.id AND tr.language = @data_language
    LEFT OUTER JOIN xls.translations t1 ON t1.TABLE_SCHEMA = 'dbo27' AND t1.TABLE_NAME = 'member_types_name' AND t1.COLUMN_NAME = t.name AND t1.LANGUAGE_NAME = @data_language
ORDER BY
    a.code

END


