CREATE PROCEDURE [xls27].[usp_budgeting_accounts]
    @company_id int = NULL
    , @account_type_id tinyint = NULL
    , @is_in_budget bit = NULL
    , @is_active bit = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE username = USER_NAME() AND is_active = 1 AND is_default = 1)
ELSE
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

-- Special accounts (Cash, Net Income, Retained Earnings)

DECLARE @cash_account_id int
DECLARE @vat_account_id int

DECLARE @cash_account_code nvarchar(50)

SELECT TOP 1 @cash_account_id = id FROM dbo27.ledger_accounts WHERE account_type_id = 11
SELECT TOP 1 @vat_account_id = id FROM dbo27.ledger_accounts WHERE account_type_id = 27

SELECT TOP 1 @cash_account_code = code FROM dbo27.ledger_accounts WHERE account_type_id = 11

SELECT
    m.id
    , m.code
    , m.name
    , tr.name AS translated_name
    , CASE WHEN p.parent_id IS NULL THEN 0 ELSE 1 END AS is_parent
    , COALESCE(u.is_currency, 1) AS is_currency
    , m.parent_id
    , m.unit_id
    , m.vat_rate_id
    , m.account_type_id
    , COALESCE(m.payment_debit_id, CASE WHEN t.cf_sign = 1 THEN @cash_account_id ELSE NULL END) AS payment_debit_id
    , COALESCE(m.payment_credit_id, CASE WHEN t.cf_sign = -1 THEN @cash_account_id ELSE NULL END) AS payment_credit_id
    , m.charge_debit_id
    , m.charge_credit_id
    , COALESCE(vat_debit_id, CASE WHEN t.vat_sign = -1 THEN @vat_account_id ELSE NULL END) AS vat_debit_id
    , COALESCE(vat_credit_id, CASE WHEN t.vat_sign = 1 THEN @vat_account_id WHEN t.vat_sign = -1 THEN m.charge_credit_id ELSE NULL END) AS vat_credit_id
    , t.payment_debit_type_id
    , t.payment_credit_type_id
    , t.charge_debit_type_id
    , t.charge_credit_type_id
    , t.vat_debit_type_id
    , t.vat_credit_type_id
    , CASE WHEN p.parent_id IS NULL AND m.is_active = 1 THEN 1 ELSE 0 END AS is_in_budget_request
    , m.is_active
FROM
    dbo27.members m
    LEFT OUTER JOIN (
        SELECT
            DISTINCT
            p.parent_id
        FROM
            dbo27.members p
    ) p ON p.parent_id = m.id
    LEFT OUTER JOIN dbo27.member_types t ON t.id = m.account_type_id
    LEFT OUTER JOIN dbo27.units u ON u.id = m.unit_id
    LEFT OUTER JOIN dbo27.translations tr ON tr.company_id = m.company_id AND tr.table_id = 1 AND tr.member_id = m.id AND tr.language = @data_language
WHERE
    m.dimension_id = 1
    AND m.company_id = @company_id
    AND m.is_active = COALESCE(@is_active, m.is_active)
    AND (CASE WHEN p.parent_id IS NULL AND m.is_active = 1 THEN 1 ELSE 0 END = @is_in_budget OR @is_in_budget IS NULL)
    AND COALESCE(m.account_type_id, 0) = COALESCE(@account_type_id, m.account_type_id, 0)
ORDER BY
    m.dimension_id
    , m.sort_order
    , m.code
    , m.name
    , m.id

END


