CREATE PROCEDURE [xls27].[usp_budgeting_accounts_update]
      @id int = NULL
    , @code nvarchar(50) = NULL
    , @name nvarchar(100) = NULL
    , @translated_name nvarchar(100) = NULL
    , @parent_id int = NULL
    , @unit_id int = NULL
    , @vat_rate_id int = NULL
    , @account_type_id int = NULL
    , @payment_debit_id int = NULL
    , @payment_credit_id int = NULL
    , @charge_debit_id int = NULL
    , @charge_credit_id int = NULL
    , @vat_debit_id int = NULL
    , @vat_credit_id int = NULL
    , @is_active bit = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

DECLARE @company_id int
DECLARE @current_name nvarchar(100)

SELECT TOP 1 @company_id = u.company_id, @current_name = t.name FROM dbo27.members t INNER JOIN dbo27.users u ON u.company_id = t.company_id  WHERE t.id = @id AND u.username = USER_NAME() AND u.is_active = 1

IF @company_id IS NULL
    RETURN

SET @code = LTRIM(RTRIM(@code))
SET @name = LTRIM(RTRIM(@name))

DECLARE @message nvarchar(max)

IF CHARINDEX(@code, ' ') > 0 OR CHARINDEX(@code, '''') > 0 OR CHARINDEX(@code, '"') > 0 OR CHARINDEX(@code, '[') > 0 OR CHARINDEX(@code, ']') > 0
    BEGIN
    SET @message = dbo27.get_translated_string('The code ''%s'' contains prohibited symbols (space, '', ", [ or ])', @data_language)
    RAISERROR (@message, 11, 0, @code)
    RETURN
    END

SET NOCOUNT OFF

UPDATE dbo27.members
SET
    code = @code
    , name = @name
    , parent_id = @parent_id
    , unit_id = @unit_id
    , vat_rate_id = @vat_rate_id
    , account_type_id = @account_type_id
    , payment_debit_id = @payment_debit_id
    , payment_credit_id = @payment_credit_id
    , charge_debit_id = @charge_debit_id
    , charge_credit_id = @charge_credit_id
    , vat_debit_id = @vat_debit_id
    , vat_credit_id = @vat_credit_id
WHERE
    id = @id
    AND NOT (
        COALESCE(code, '-1') = COALESCE(@code, '-2')
        AND COALESCE(name, '-1') = COALESCE(@name, '-2')
        AND COALESCE(parent_id, -1) = COALESCE(@parent_id, -2)
        AND COALESCE(unit_id, -1) = COALESCE(@unit_id, -2)
        AND COALESCE(vat_rate_id, -1) = COALESCE(@vat_rate_id, -2)
        AND COALESCE(account_type_id, -1) = COALESCE(@account_type_id, -2)
        AND COALESCE(payment_debit_id, -1) = COALESCE(@payment_debit_id, -2)
        AND COALESCE(payment_credit_id, -1) = COALESCE(@payment_credit_id, -2)
        AND COALESCE(charge_debit_id, -1) = COALESCE(@charge_debit_id, -2)
        AND COALESCE(charge_credit_id, -1) = COALESCE(@charge_credit_id, -2)
        AND COALESCE(vat_debit_id, -1) = COALESCE(@vat_debit_id, -2)
        AND COALESCE(vat_credit_id, -1) = COALESCE(@vat_credit_id, -2)
        )

IF @data_language IS NOT NULL AND @translated_name IS NOT NULL
    BEGIN
    MERGE dbo27.translations t
    USING
        (
        SELECT company_id = @company_id, 1 AS table_id, @id AS member_id, @data_language AS language, @translated_name AS name
        ) s ON s.company_id = t.company_id AND s.table_id = t.table_id AND s.member_id = t.member_id AND s.language = t.language
    WHEN MATCHED AND NOT COALESCE(t.name, '') = COALESCE(s.name, '') THEN
        UPDATE SET name = s.name
    WHEN NOT MATCHED THEN
        INSERT (company_id, table_id, member_id, language, name) VALUES (s.company_id, s.table_id, s.member_id, s.language, s.name);
    END

END


