CREATE PROCEDURE [xls27].[usp_companies]
    @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

SELECT
    c.id
    , c.code
    , c.name
    , tr.name AS translated_name
    , c.default_language
    , c.sort_order
