-- Author:      Gartle LLC
-- Release:     3.0, 2022-07-05
-- Description: Journal
-- =============================================

CREATE PROCEDURE [xls27].[usp_journal]
    @start_year int = NULL
    , @start_month int = NULL
    , @start_day int = NULL
    , @end_year int = NULL
    , @end_month int = NULL
    , @end_day int = NULL
    , @company_id int = NULL
    , @account_id int = NULL
    , @subsidiary_account_id int = NULL
    , @entity_id int = NULL
    , @region_id int = NULL
    , @product_id int = NULL
AS
BEGIN

SET NOCOUNT ON

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE username = USER_NAME() AND is_active = 1 AND is_default = 1)
ELSE
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

DECLARE @start_date date
DECLARE @end_date date

SET @start_date = DATEFROMPARTS(COALESCE(@start_year, YEAR(GETDATE())), COALESCE(@start_month, 1), 1)
SET @end_date = DATEFROMPARTS(COALESCE(@end_year, YEAR(GETDATE())), COALESCE(@end_month, 12), 1)

IF @start_day > 1
    IF DATEADD(DAY, @start_day - 1, @start_date) < DATEADD(MONTH, 1, @start_date)
        SET @start_date = DATEADD(DAY, @start_day - 1, @start_date)
    ELSE
        SET @start_date = DATEADD(DAY, -1, DATEADD(MONTH, 1, @start_date))

IF @end_day > 1 AND DATEADD(DAY, @end_day - 1, @end_date) < DATEADD(MONTH, 1, @end_date)
    SET @end_date = DATEADD(DAY, @end_day - 1, @end_date)
ELSE
    SET @end_date = DATEADD(DAY, -1, DATEADD(MONTH, 1, @end_date))

DECLARE @system_currency nvarchar(50) = (SELECT name FROM dbo27.units WHERE company_id = @company_id AND is_functional_currency = 1)

;WITH parents (id, parent_id) AS (
    SELECT
        a.id
        , a.id
    FROM
        dbo27.ledger_accounts a
    WHERE
        a.company_id = @company_id

    UNION ALL
    SELECT
        p.id
        , a.parent_id
    FROM
        parents p
        INNER JOIN dbo27.ledger_accounts a ON a.id = p.parent_id
    WHERE
        a.parent_id IS NOT NULL
        AND NOT p.id = a.parent_id
)
, filtered_accounts AS (
    SELECT
        p.id
    FROM
        parents p
    WHERE
        p.parent_id = @account_id
)

SELECT
    j.id
    , CAST(j.date AS datetime) AS date
    , j.debit_account_id
    , j.credit_account_id
    , j.amount
    , j.debit_subsidiary_account_id
    , j.credit_subsidiary_account_id
    , j.description
    , COALESCE(dc.name, cc.name) AS currency
    , r.rate
    , j.amount$
    , j.entity_id
    , j.account_id
    , j.region_id
    , j.product_id
    , j.is_draft
    , j.auto_type_id
    , COALESCE(u.name, j.modified_by) AS modified_by
    , j.modified_on
FROM
    dbo27.journals j
    LEFT OUTER JOIN dbo27.members ds ON ds.id = j.debit_subsidiary_account_id
    LEFT OUTER JOIN dbo27.members cs ON cs.id = j.credit_subsidiary_account_id
    LEFT OUTER JOIN dbo27.units dc ON dc.id = ds.id AND dc.is_functional_currency = 0
    LEFT OUTER JOIN dbo27.units cc ON cc.id = cs.id AND cc.is_functional_currency = 0
    LEFT OUTER JOIN dbo27.actual_currency_rates r ON r.currency_id = COALESCE(dc.id, cc.id) AND r.date = j.date
    LEFT OUTER JOIN dbo27.users u ON u.company_id = j.company_id AND u.username = j.modified_by
WHERE
    j.[date] BETWEEN @start_date AND @end_date
    AND j.company_id = @company_id
    AND (COALESCE(j.debit_subsidiary_account_id, 0) = COALESCE(@subsidiary_account_id, j.debit_subsidiary_account_id, 0)
        OR COALESCE(j.credit_subsidiary_account_id, 0) = COALESCE(@subsidiary_account_id, j.credit_subsidiary_account_id, 0)
        )
    AND (@account_id IS NULL
        OR j.debit_account_id IN (SELECT id FROM filtered_accounts)
        OR j.credit_account_id IN (SELECT id FROM filtered_accounts)
        )
    AND COALESCE(j.entity_id, 0) = COALESCE(@entity_id, j.entity_id, 0)
    AND COALESCE(j.region_id, 0) = COALESCE(@region_id, j.region_id, 0)
    AND COALESCE(j.product_id, 0) = COALESCE(@product_id, j.product_id, 0)

ORDER BY
    j.date
    , j.auto_type_id
    , j.id

END


GO

