CREATE PROCEDURE [xls27].[usp_journal_update]
    @id int
    , @company_id int = NULL
    , @date date = NULL
    , @amount money = NULL
    , @description nvarchar(255) = NULL
    , @debit_account_id int = NULL
    , @debit_subsidiary_account_id int = NULL
    , @credit_account_id int = NULL
    , @credit_subsidiary_account_id int = NULL
    , @entity_id int = NULL
    , @account_id int = NULL
    , @region_id int = NULL
    , @product_id int = NULL
    , @is_draft bit = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

IF @company_id IS NULL
    BEGIN

    IF @debit_account_id IS NOT NULL
        SET @company_id = (SELECT TOP 1 company_id FROM dbo27.ledger_accounts WHERE id = @debit_account_id)
    ELSE IF @credit_account_id IS NOT NULL
        SET @company_id = (SELECT TOP 1 company_id FROM dbo27.ledger_accounts WHERE id = @credit_account_id)
    ELSE IF @debit_subsidiary_account_id IS NOT NULL
        SET @company_id = (SELECT TOP 1 company_id FROM dbo27.members WHERE id = @debit_subsidiary_account_id)
    ELSE IF @credit_subsidiary_account_id IS NOT NULL
        SET @company_id = (SELECT TOP 1 company_id FROM dbo27.members WHERE id = @credit_subsidiary_account_id)
    ELSE IF @id IS NOT NULL
        SET @company_id = (SELECT TOP 1 j.company_id FROM dbo27.journals j INNER JOIN dbo27.users u ON u.company_id = j.company_id WHERE j.id = @id AND username = USER_NAME() AND is_active = 1)
    ELSE
        SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE username = USER_NAME() AND is_active = 1 AND is_default = 1)

    IF @company_id IS NULL
        RETURN
    END

IF @debit_account_id IS NOT NULL AND
    NOT EXISTS (SELECT id FROM dbo27.ledger_accounts WHERE id = @debit_account_id AND company_id = @company_id)
    RETURN

IF @credit_account_id IS NOT NULL AND
    NOT EXISTS (SELECT id FROM dbo27.ledger_accounts WHERE id = @credit_account_id AND company_id = @company_id)
    RETURN

IF @debit_subsidiary_account_id IS NOT NULL AND
    NOT EXISTS (SELECT id FROM dbo27.members WHERE id = @debit_subsidiary_account_id AND company_id = @company_id)
    RETURN

IF @credit_subsidiary_account_id IS NOT NULL AND
    NOT EXISTS (SELECT id FROM dbo27.members WHERE id = @credit_subsidiary_account_id AND company_id = @company_id)
    RETURN

IF @entity_id IS NOT NULL AND
    NOT EXISTS (SELECT id FROM dbo27.members WHERE id = @entity_id AND company_id = @company_id)
    RETURN

IF @account_id IS NOT NULL AND
    NOT EXISTS (SELECT id FROM dbo27.members WHERE id = @account_id AND company_id = @company_id)
    RETURN

IF @product_id IS NOT NULL AND
    NOT EXISTS (SELECT id FROM dbo27.members WHERE id = @product_id AND company_id = @company_id)
    RETURN

IF @region_id IS NOT NULL AND
    NOT EXISTS (SELECT id FROM dbo27.members WHERE id = @region_id AND company_id = @company_id)
    RETURN

DECLARE @message nvarchar(max)

IF (SELECT p.is_closed FROM dbo27.journals j INNER JOIN dbo27.actual_closed_periods p ON p.date = j.month AND p.company_id = @company_id WHERE j.id = @id) = 1
    OR (SELECT p.is_closed FROM dbo27.actual_closed_periods p WHERE p.date = DATEADD(D, 1 - DATEPART(d, @date), @date) AND p.company_id = @company_id) = 1
    BEGIN
    SET @message = dbo27.get_translated_string('The period is closed for changes', @data_language)
    RAISERROR (@message, 11, 0)
    RETURN
    END

DECLARE @currency_id int = COALESCE(
    (SELECT m.unit_id FROM dbo27.members m INNER JOIN dbo27.units u ON u.id = m.unit_id WHERE m.id = @debit_subsidiary_account_id AND u.is_currency = 1 AND u.is_functional_currency = 0),
    (SELECT m.unit_id FROM dbo27.members m INNER JOIN dbo27.units u ON u.id = m.unit_id WHERE m.id = @credit_subsidiary_account_id AND u.is_currency = 1 AND u.is_functional_currency = 0))

DECLARE @amount$ float = @amount

DECLARE @rate_date date
DECLARE @rate float

IF @currency_id IS NOT NULL
    BEGIN

    SELECT @rate_date = date, @rate = rate FROM dbo27.actual_currency_rates WHERE currency_id = @currency_id AND date = @date

    IF @rate_date IS NULL
        BEGIN

        SELECT @rate_date = date, @rate = rate FROM dbo27.actual_currency_rates WHERE currency_id = @currency_id
            AND date = (SELECT MAX(date) FROM dbo27.actual_currency_rates WHERE currency_id = @currency_id AND date <= @date)

        INSERT INTO dbo27.actual_currency_rates (currency_id, date, rate)
        VALUES (@currency_id, @date, COALESCE(@rate, 1.0))

        END

    SELECT
        @amount$ = ROUND(@amount * CASE WHEN is_base_currency = 0 OR @rate = 0 THEN COALESCE(@rate, 1.0) ELSE 1.0 / COALESCE(@rate, 1.0) END, 2)
    FROM
        dbo27.units
    WHERE
        id = @currency_id

    END

SET NOCOUNT OFF

IF @id IS NULL
    INSERT INTO dbo27.journals (company_id, date, amount, description, debit_account_id, debit_subsidiary_account_id, credit_account_id, credit_subsidiary_account_id, amount$, entity_id, account_id, product_id, region_id, is_draft, modified_on, modified_by)
    VALUES (@company_id, @date, @amount, @description, @debit_account_id, @debit_subsidiary_account_id, @credit_account_id, @credit_subsidiary_account_id, @amount$, @entity_id, @account_id, @product_id, @region_id, COALESCE(@is_draft, 0), GETDATE(), USER_NAME())
ELSE
    UPDATE dbo27.journals
    SET
        date = @date
        , amount = @amount
        , description = @description
        , debit_account_id = @debit_account_id
        , debit_subsidiary_account_id = @debit_subsidiary_account_id
        , credit_account_id = @credit_account_id
        , credit_subsidiary_account_id = @credit_subsidiary_account_id
        , amount$ = @amount$
        , entity_id = @entity_id
        , account_id = @account_id
        , product_id = @product_id
        , region_id = @region_id
        , is_draft = COALESCE(@is_draft, 0)
        , modified_on = GETDATE()
        , modified_by = USER_NAME()
    WHERE
        id = @id

END


