    , t.specific_type_id
    , d.[1] AS pl_dr, c.[1] AS pl_cr
    , d.[2] AS cf_dr, c.[2] AS cf_cr
    , d.[3] AS bs_dr, c.[3] AS bs_cr
    , d.[4] AS cfd_dr, c.[4] AS cfd_cr
    , d.[5] AS vat_dr, c.[5] AS vat_cr
