            INNER JOIN dbo27.ledger_account_types lat ON lat.id = rsa.account_type_id
            INNER JOIN dbo27.report_section_types rst ON rst.id = rsa.section_type_id
        WHERE
            rsa.entry_side_id = 1
    ) s PIVOT (
        SUM(value) FOR report_type_id IN ([1], [2], [3], [4], [5])
    ) d ON d.id = t.id
    LEFT OUTER JOIN (
        SELECT
            lat.id
            , rst.report_type_id
            , rsa.value_sign AS value
        FROM
            dbo27.report_section_accounts rsa
            INNER JOIN dbo27.ledger_account_types lat ON lat.id = rsa.account_type_id
            INNER JOIN dbo27.report_section_types rst ON rst.id = rsa.section_type_id
        WHERE
            rsa.entry_side_id = 2
    ) s PIVOT (
        SUM(value) FOR report_type_id IN ([1], [2], [3], [4], [5])
    ) c ON c.id = t.id
