IF @company_id IS NULL
    RETURN

SET @code = LTRIM(RTRIM(@code))
SET @name = LTRIM(RTRIM(@name))

DECLARE @message nvarchar(max)

IF CHARINDEX(@code, ' ') > 0 OR CHARINDEX(@code, '''') > 0 OR CHARINDEX(@code, '"') > 0 OR CHARINDEX(@code, '[') > 0 OR CHARINDEX(@code, ']') > 0
    BEGIN
    SET @message = dbo27.get_translated_string('The code ''%s'' contains prohibited symbols (space, '', ", [ or ])', @data_language)
    RAISERROR (@message, 11, 0, @code)
    RETURN
    END

SET NOCOUNT OFF

UPDATE dbo27.ledger_accounts
SET
    code = @code
    , name = @name
    , parent_id = @parent_id
    , account_type_id = @account_type_id
    , subsidiary_group_id = @subsidiary_group_id
    , closing_child_account_id = @closing_child_account_id
    , closing_debit_account_id = @closing_debit_account_id
    , closing_credit_account_id = @closing_credit_account_id
    , ref_number = @ref_number
WHERE
    id = @id
    AND NOT (
        COALESCE(code, '-1') = COALESCE(@code, '-2')
        AND COALESCE(name, '-1') = COALESCE(@name, '-2')
        AND COALESCE(parent_id, -1) = COALESCE(@parent_id, -2)
        AND COALESCE(account_type_id, -1) = COALESCE(@account_type_id, -2)
        AND COALESCE(subsidiary_group_id, -1) = COALESCE(@subsidiary_group_id, -2)
