CREATE PROCEDURE [xls27].[usp_member_permissions]
    @company_id int = NULL
    , @dimension_id tinyint = NULL
    , @username nvarchar(128) = NULL
    , @data_language varchar(10) = NULL
WITH EXECUTE AS OWNER
AS
BEGIN

SET NOCOUNT ON

EXECUTE AS CALLER

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE username = USER_NAME() AND is_active = 1 AND is_default = 1)
ELSE
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

REVERT

DECLARE @columns nvarchar(max)

IF @company_id IS NOT NULL
    SELECT @columns = STUFF((
        SELECT
            ', [' + CAST(u.username AS nvarchar) + ']'
        FROM
            dbo27.users u
        WHERE
            u.company_id = @company_id
            AND u.is_active = 1
            AND u.is_admin = 0
        ORDER BY
            u.username
        FOR XML PATH(''), TYPE
    ).value('.', 'nvarchar(MAX)'), 1, 2, '')

DECLARE @sql nvarchar(max)

SET @sql = 'SELECT
    m.id
    , m.dimension_id
    , m.sort_order
    , m.code
    , ' + CASE WHEN @data_language IS NULL THEN 'm.name' ELSE 'COALESCE(tr.name, m.name) AS name' END + '
    , CASE WHEN h.parent_id IS NULL AND m.is_active = 1 THEN 1 ELSE 0 END AS is_in_budget_request
    , CAST(NULL AS char(1)) AS data_format'
    + CASE WHEN @columns IS NULL THEN '' ELSE '
    , ' + @columns END + '
    , CAST(NULL AS char(1)) AS last_format_column
FROM
    dbo27.members m'
    + CASE WHEN @columns IS NULL THEN '' ELSE '
    LEFT OUTER JOIN (
        SELECT
            mp.member_id
            , mp.username
            , mp.permission AS value
        FROM
            dbo27.member_permissions mp
    ) s PIVOT (
        MAX(value) FOR username IN (' + @columns + ')
    ) p ON p.member_id = m.id' END + '
    LEFT OUTER JOIN (
        SELECT
            DISTINCT
            p.parent_id
        FROM
            dbo27.members p
    ) h ON h.parent_id = m.id'
    + CASE WHEN @data_language IS NULL THEN '' ELSE '
    LEFT OUTER JOIN dbo27.translations tr ON tr.company_id = m.company_id AND tr.table_id = 1 AND tr.member_id = m.id AND tr.language = ''' + @data_language + ''''
        END    + '
WHERE
    m.company_id = ' + COALESCE(CAST(@company_id AS nvarchar), 'NULL')
    + CASE WHEN @dimension_id IS NULL THEN '' ELSE + '
    AND m.dimension_id = ' + CAST(@dimension_id AS nvarchar) END
    + '
ORDER BY
    m.dimension_id
    , m.sort_order
    , m.code
'

-- PRINT @sql

EXEC (@sql)

END


