-- Author:      Gartle LLC
-- Release:     3.0, 2022-07-05
-- Description: Cell change event handler for usp_member_permissions
-- =============================================

CREATE PROCEDURE [xls27].[usp_member_permissions_change]
    @column_name nvarchar(128) = NULL
    , @cell_value nvarchar(255) = NULL
    , @id int = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

DECLARE @message nvarchar(max)

-- Protecting non-data columns

IF @column_name IN ('data_format')
    BEGIN
    SET @message = dbo27.get_translated_string('The column ''%s'' is not updatable', @data_language)
    RAISERROR (@message, 11, 0, @column_name)
    RETURN
    END

DECLARE @company_id int

SET @company_id = (SELECT TOP 1 u.company_id FROM dbo27.members t INNER JOIN dbo27.users u ON u.company_id = t.company_id WHERE t.id = @id AND u.username = USER_NAME() AND u.is_active = 1)

IF @company_id IS NULL
    RETURN

IF NOT EXISTS (SELECT id FROM dbo27.members WHERE id = @id)
    BEGIN
    SET @message = dbo27.get_translated_string('Cannot find member_id %s', @data_language)
    RAISERROR (@message, 11, 0, @id)
    RETURN
    END

DECLARE @username nvarchar(128) = @column_name

IF NOT EXISTS (SELECT u.username FROM dbo27.users u WHERE u.company_id = @company_id AND u.username = @username)
    BEGIN
    SET @message = dbo27.get_translated_string('Cannot find the principal ''%s'', because it does not exist or you do not have permission.', @data_language)
    RAISERROR (@message, 11, 0, @username)
    RETURN
    END

-- Getting permissions from the cell value

DECLARE @select_permission bit = NULL
DECLARE @update_permission bit = NULL

IF @cell_value IS NULL
    BEGIN
    DELETE FROM dbo27.member_permissions WHERE member_id = @id AND username = @column_name
    RETURN
    END

IF UPPER(@cell_value) = 'D'
    BEGIN
    SET @select_permission = 0
    SET @update_permission = 0
    END
ELSE IF UPPER(@cell_value) = 'R'
    BEGIN
    SET @select_permission = 1
    SET @update_permission = 0
    END
ELSE IF UPPER(@cell_value) = 'W'
    BEGIN
    SET @select_permission = 1
    SET @update_permission = 1
    END
ELSE
    BEGIN
    SET @message = dbo27.get_translated_string('Unknown option: %s (Use R, W, D)', @data_language)
    RAISERROR (@message, 11, 0, @cell_value)
    RETURN
    END

SET NOCOUNT OFF

UPDATE dbo27.member_permissions
SET
    select_permission = @select_permission
    , update_permission = @update_permission
    , is_inherited = 0
WHERE
    member_id = @id
    AND username = @column_name

IF @@ROWCOUNT = 0
    INSERT INTO dbo27.member_permissions (member_id, username, select_permission, update_permission, is_inherited)
    VALUES (@id, @username, @select_permission, @update_permission, 0)

END


