CREATE PROCEDURE [xls27].[usp_subsidiary_accounts]
    @company_id int = NULL
    , @subsidiary_group_id int = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE username = USER_NAME() AND is_active = 1 AND is_default = 1)
ELSE
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

SELECT
    m.id
    , m.code
    , COALESCE(tr.name, m.name) AS name
    , m.unit_id AS currency_id
    , p.[1] AS group1
    , p.[2] AS group2
    , p.[3] AS group3
FROM
    dbo27.members m
    LEFT OUTER JOIN (
        SELECT
            h.member_id
            , h.group_id
            , ROW_NUMBER() OVER(PARTITION BY h.member_id ORDER BY g.code, g.id) AS R
        FROM
            dbo27.member_groups h
            INNER JOIN dbo27.subsidiary_groups g ON g.id = h.group_id
    ) s PIVOT (
        MAX(group_id) FOR R IN ([1], [2], [3])
    ) p ON p.member_id = m.id
    LEFT OUTER JOIN dbo27.subsidiary_groups g1 ON g1.id = p.[1]
    LEFT OUTER JOIN dbo27.subsidiary_groups g2 ON g2.id = p.[2]
    LEFT OUTER JOIN dbo27.subsidiary_groups g3 ON g3.id = p.[3]
    LEFT OUTER JOIN dbo27.translations tr ON tr.company_id = m.company_id AND tr.table_id = 1 AND tr.member_id = m.id AND tr.language = @data_language
WHERE
    m.company_id = @company_id
    AND (@subsidiary_group_id IS NULL OR m.id IN (SELECT member_id FROM dbo27.member_groups WHERE group_id = @subsidiary_group_id))
    AND m.dimension_id = 7
ORDER BY
    g1.code
    , g2.code
    , g3.code
    , m.code
    , m.id

END


