-- =============================================
-- Author:      Gartle LLC
-- Release:     3.0, 2022-07-05
-- Description: Update procedure for usp_subsidiary_accounts
-- =============================================

CREATE PROCEDURE [xls27].[usp_subsidiary_accounts_update]
      @id int = NULL
    , @code nvarchar(50) = NULL
    , @name nvarchar(100) = NULL
    , @currency_id tinyint = NULL
    , @group1 tinyint = NULL
    , @group2 tinyint = NULL
    , @group3 tinyint = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

IF NOT EXISTS (SELECT TOP 1 u.company_id FROM dbo27.members t INNER JOIN dbo27.users u ON u.company_id = t.company_id  WHERE t.id = @id AND u.username = USER_NAME() AND u.is_active = 1)
    RETURN

SET @code = LTRIM(RTRIM(@code))
SET @name = LTRIM(RTRIM(@name))

DECLARE @message nvarchar(max)

IF CHARINDEX(@code, ' ') > 0 OR CHARINDEX(@code, '''') > 0 OR CHARINDEX(@code, '"') > 0 OR CHARINDEX(@code, '[') > 0 OR CHARINDEX(@code, ']') > 0
    BEGIN
    SET @message = dbo27.get_translated_string('The code ''%s'' contains prohibited symbols (space, '', ", [ or ])', @data_language)
    RAISERROR (@message, 11, 0, @code)
    RETURN
    END

DECLARE @prev_currency_id int = (SELECT unit_id FROM dbo27.members WHERE id = @id)

IF NOT COALESCE(@prev_currency_id, 0) = COALESCE(@currency_id, 0)
    BEGIN
    IF EXISTS(
        SELECT
            TOP 1 j.id
        FROM
            dbo27.journals j
            INNER JOIN dbo27.actual_closed_periods p ON p.date = j.month
        WHERE
            (j.debit_subsidiary_account_id = @id OR j.credit_subsidiary_account_id = @id)
            AND p.is_closed = 1
        )
        BEGIN
        SET @message = dbo27.get_translated_string('The period is closed for changes', @data_language)
        RAISERROR (@message, 11, 0)
        RETURN
        END

    IF COALESCE(@currency_id, 0) = 0
        BEGIN
        UPDATE dbo27.journals
        SET
            amount$ = amount
        FROM
            dbo27.journals j
        WHERE
            (j.debit_subsidiary_account_id = @id OR j.credit_subsidiary_account_id = @id)
            AND j.auto_type_id = 0
        END
    ELSE
        BEGIN
        DECLARE @is_base_currency bit = (SELECT is_base_currency FROM dbo27.units WHERE id = @currency_id)

        UPDATE dbo27.journals
        SET
            amount$ = ROUND(amount * CASE WHEN @is_base_currency = 0 OR r.rate = 0 THEN COALESCE(r.rate, 1.0) ELSE 1.0 / COALESCE(r.rate, 1.0) END, 2)
        FROM
            dbo27.journals j
            INNER JOIN dbo27.actual_currency_rates r ON r.currency_id = @currency_id AND r.date = j.date
        WHERE
            (j.debit_subsidiary_account_id = @id OR j.credit_subsidiary_account_id = @id)
            AND j.auto_type_id = 0
        END
    END

UPDATE dbo27.members
SET
    code = @code
    , name = @name
    , unit_id = @currency_id
WHERE
    id = @id
    AND NOT (
        COALESCE(code, '-1') = COALESCE(@code, '-2')
        AND COALESCE(name, '-1') = COALESCE(@name, '-2')
        AND COALESCE(unit_id, -1) = COALESCE(@currency_id, -2)
        )

MERGE dbo27.member_groups t
USING
    (
        SELECT * FROM (VALUES (@id, @group1), (@id, @group2), (@id, @group3)) v(member_id, group_id) WHERE group_id IS NOT NULL
    ) s ON t.member_id = s.member_id AND t.group_id = s.group_id
WHEN NOT MATCHED THEN
    INSERT (member_id, group_id) VALUES (member_id, group_id)
WHEN NOT MATCHED BY SOURCE AND t.member_id = @id THEN
    DELETE;

END


