-- Description: Selects translations
-- =============================================

CREATE PROCEDURE [xls27].[usp_translations]
    @company_id int = NULL
    , @is_translatable bit = NULL
    , @is_complete bit = NULL
WITH EXECUTE AS OWNER
AS
BEGIN

SET NOCOUNT ON;

EXECUTE AS CALLER

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE username = USER_NAME() AND is_active = 1 AND is_default = 1)
ELSE
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

REVERT

IF @company_id IS NULL
    BEGIN
    SELECT
        t.table_id
        , t.member_id
        , t.name AS table_name
        , 0 AS is_translatable
        , 0 AS is_complete
        , t.name
        , t.name AS [en]
    FROM
        dbo27.translations t
    WHERE
        t.company_id IS NULL

    RETURN
    END

DECLARE @company_sid nvarchar(max) = CAST(@company_id AS nvarchar(max))
DECLARE @sql nvarchar(max)
DECLARE @languages nvarchar(max)
DECLARE @complete nvarchar(max)

SELECT @languages = STUFF((
    SELECT
        t.name
    FROM
        (
        SELECT
            DISTINCT
            ', [' + t.language + ']' AS name
            , CASE
                WHEN t.language = 'en' THEN '1'
                WHEN t.language = 'fr' THEN '2'
                WHEN t.language = 'it' THEN '3'
                WHEN t.language = 'es' THEN '4'
                WHEN t.language = 'pt' THEN '5'
                WHEN t.language = 'de' THEN '6'
                WHEN t.language = 'ru' THEN '7'
                ELSE t.language
                END AS sort_order
        FROM
            dbo27.translations t
        WHERE
            t.company_id = @company_id
        ) t
    ORDER BY
        t.sort_order
    FOR XML PATH(''), TYPE).value('.', 'nvarchar(MAX)'), 1, 2, '')

IF @languages IS NULL SET @languages = '[en]'

DECLARE @default_language varchar(10) = (SELECT default_language FROM dbo27.companies WHERE id = @company_id)

IF @default_language IS NULL SET @languages = '  '

SELECT @complete = STUFF((
    SELECT
        t.name
    FROM
        (
        SELECT
            DISTINCT
            ' OR [' + t.language + '] IS NULL' AS name
            , CASE
                WHEN t.language = 'en' THEN '1'
                WHEN t.language = 'fr' THEN '2'
                WHEN t.language = 'it' THEN '3'
                WHEN t.language = 'es' THEN '4'
                WHEN t.language = 'pt' THEN '5'
                WHEN t.language = 'de' THEN '6'
                WHEN t.language = 'ru' THEN '7'
                ELSE t.language
                END AS sort_order
        FROM
            dbo27.translations t
        WHERE
            t.company_id = @company_id
            AND NOT t.language = @default_language
        ) t
    ORDER BY
        t.sort_order
    FOR XML PATH(''), TYPE).value('.', 'nvarchar(MAX)'), 1, 4, '')

IF @complete IS NULL SET @complete = '[en] IS NULL'

SET @sql = 'SELECT
    t.table_id
    , t.member_id
    , tt.name AS table_name
    , t.is_translatable
    , CASE WHEN t.is_translatable = 0 THEN 1 WHEN ' + @complete + ' THEN 0 ELSE 1 END AS is_complete
    , t.name
    , ' + @languages + '
FROM
    (
        SELECT
            m.sort_order
            , m.table_id
            , m.member_id
            , m.is_translatable
            , m.name
            , t.language
            , t.name AS translated_name
        FROM
            (
                SELECT 2 AS sort_order, 2 AS table_id, t.id AS member_id, t.name, 1 AS is_translatable FROM dbo27.companies t WHERE t.id = ' + @company_sid + '
                UNION ALL SELECT 3 AS sort_order, 3 AS table_id, t.id AS member_id, t.name, t.is_translatable FROM dbo27.units t WHERE t.company_id = ' + @company_sid + '
                UNION ALL SELECT 4 AS sort_order, 4 AS table_id, t.id AS member_id, t.name, t.is_translatable FROM dbo27.vat_rates t WHERE t.company_id = ' + @company_sid + '
                UNION ALL SELECT 5 AS sort_order, 5 AS table_id, t.id AS member_id, t.name, 1 AS is_translatable FROM dbo27.dimensions t
                UNION ALL SELECT 6 AS sort_order, 9 AS table_id, t.id AS member_id, t.description, 1 AS is_translatable FROM dbo27.report_sections t WHERE t.company_id = ' + @company_sid + '
                UNION ALL SELECT 7 AS sort_order, 10 AS table_id, t.id AS member_id, t.name, 1 AS is_translatable FROM dbo27.subsidiary_groups t WHERE t.company_id = ' + @company_sid + '
                UNION ALL SELECT 8 AS sort_order, 11 AS table_id, t.id AS member_id, t.name, 1 AS is_translatable FROM dbo27.ledger_accounts t WHERE t.company_id = ' + @company_sid + '
                UNION ALL SELECT 9 AS sort_order, 12 AS table_id, t.id AS member_id, t.name, 1 AS is_translatable FROM dbo27.saved_report_types t WHERE t.company_id = ' + @company_sid + '
                UNION ALL SELECT 10 AS sort_order, 1 AS table_id, t.id AS member_id, t.name, t.is_translatable FROM dbo27.members t WHERE t.company_id = ' + @company_sid + '
            ) m
            LEFT OUTER JOIN dbo27.translations t ON t.company_id = ' + @company_sid + ' AND t.table_id = m.table_id AND t.member_id = m.member_id
        WHERE
            m.name IS NOT NULL
    ) s PIVOT (
        MAX(translated_name) FOR language IN (' + @languages + ')
    ) t
    INNER JOIN dbo27.translated_tables tt ON tt.id = t.table_id'
    + CASE WHEN @is_complete IS NULL AND @is_translatable IS NULL THEN '' ELSE '
WHERE'
        + CASE WHEN @is_complete IS NULL THEN '' WHEN @is_complete = 1 THEN '
    NOT (' + @complete + ')'
            ELSE '
    (' + @complete + ')'
            END
        + CASE WHEN @is_translatable IS NULL THEN '' ELSE '
    '        + CASE WHEN @is_complete IS NULL THEN '' ELSE 'AND ' END + 't.is_translatable = ' + CASE WHEN @is_translatable = 1 THEN '1' ELSE '0' END
            END
        END    + '
ORDER BY
    t.sort_order
    , t.name'

-- PRINT @sql

EXEC (@sql)

END


