    SELECT TOP 1 @company_id = u.company_id FROM dbo27.units t INNER JOIN dbo27.users u ON u.company_id = t.company_id  WHERE t.id = @member_id AND u.username = USER_NAME() AND u.is_active = 1
ELSE IF @table_id = 4
    SELECT TOP 1 @company_id = u.company_id FROM dbo27.vat_rates t INNER JOIN dbo27.users u ON u.company_id = t.company_id  WHERE t.id = @member_id AND u.username = USER_NAME() AND u.is_active = 1
ELSE IF @table_id = 5
    SELECT TOP 1 @company_id = u.company_id FROM dbo27.companies t INNER JOIN dbo27.users u ON u.company_id = t.id  WHERE t.id = @company_id AND u.username = USER_NAME() AND u.is_active = 1
ELSE IF @table_id = 6
    SELECT TOP 1 @company_id = u.company_id FROM dbo27.companies t INNER JOIN dbo27.users u ON u.company_id = t.id  WHERE t.id = @company_id AND u.username = USER_NAME() AND u.is_active = 1
ELSE IF @table_id = 9
    SELECT TOP 1 @company_id = u.company_id FROM dbo27.report_sections t INNER JOIN dbo27.users u ON u.company_id = t.company_id  WHERE t.id = @member_id AND u.username = USER_NAME() AND u.is_active = 1
ELSE IF @table_id = 10
    SELECT TOP 1 @company_id = u.company_id FROM dbo27.subsidiary_groups t INNER JOIN dbo27.users u ON u.company_id = t.company_id  WHERE t.id = @member_id AND u.username = USER_NAME() AND u.is_active = 1
