    SELECT TOP 1 @company_id = u.company_id FROM dbo27.saved_report_types t INNER JOIN dbo27.users u ON u.company_id = t.company_id  WHERE t.id = @member_id AND u.username = USER_NAME() AND u.is_active = 1
