    RETURN

IF @company_id IS NULL OR @company_id1 IS NOT NULL AND NOT @company_id1 = @company_id
    RETURN

IF @column_name = 'is_translatable'
    BEGIN
    IF NOT @cell_number_value IN (0, 1) AND NOT @cell_number_value IS NULL
