-- Release:     3.0, 2022-07-05
-- Description: Selects common elements with translations from xls.translations
-- =============================================

CREATE PROCEDURE [xls27].[usp_translations_common]
    @is_complete bit = NULL
WITH EXECUTE AS OWNER
AS
BEGIN

SET NOCOUNT ON;

DECLARE @sql nvarchar(max)
DECLARE @languages nvarchar(max)
DECLARE @complete nvarchar(max)

SELECT @languages = STUFF((
    SELECT
        t.name
    FROM
        (
        SELECT
            DISTINCT
            ', [' + t.LANGUAGE_NAME + ']' AS name
            , CASE
                WHEN t.LANGUAGE_NAME = 'en' THEN '1'
                WHEN t.LANGUAGE_NAME = 'fr' THEN '2'
                WHEN t.LANGUAGE_NAME = 'it' THEN '3'
                WHEN t.LANGUAGE_NAME = 'es' THEN '4'
                WHEN t.LANGUAGE_NAME = 'pt' THEN '5'
                WHEN t.LANGUAGE_NAME = 'de' THEN '6'
                WHEN t.LANGUAGE_NAME = 'ru' THEN '7'
                ELSE t.LANGUAGE_NAME
                END AS sort_order
        FROM
            xls.translations t
        ) t
    ORDER BY
        t.sort_order
    FOR XML PATH(''), TYPE).value('.', 'nvarchar(MAX)'), 1, 2, '')

IF @languages IS NULL SET @languages = '[en]'

SELECT @complete = STUFF((
    SELECT
        t.name
    FROM
        (
        SELECT
            DISTINCT
            ' OR [' + t.LANGUAGE_NAME + '] IS NULL' AS name
            , CASE
                WHEN t.LANGUAGE_NAME = 'en' THEN '1'
                WHEN t.LANGUAGE_NAME = 'fr' THEN '2'
                WHEN t.LANGUAGE_NAME = 'it' THEN '3'
                WHEN t.LANGUAGE_NAME = 'es' THEN '4'
                WHEN t.LANGUAGE_NAME = 'pt' THEN '5'
                WHEN t.LANGUAGE_NAME = 'de' THEN '6'
                WHEN t.LANGUAGE_NAME = 'ru' THEN '7'
                ELSE t.LANGUAGE_NAME
                END AS sort_order
        FROM
            xls.translations t
        ) t
    ORDER BY
        t.sort_order
    FOR XML PATH(''), TYPE).value('.', 'nvarchar(MAX)'), 1, 4, '')

IF @complete IS NULL SET @complete = '[en] IS NULL'

SET @sql = 'SELECT
    t.TABLE_SCHEMA
    , t.TABLE_NAME
    , CASE WHEN ' + @complete + ' THEN 0 ELSE 1 END AS is_complete
    , t.COLUMN_NAME AS [COLUMN]
    , ' + @languages + '
FROM
    (
        SELECT
            t.sort_order
            , t.TABLE_SCHEMA
            , t.TABLE_NAME
            , t.COLUMN_NAME
            , tr.LANGUAGE_NAME
            , tr.TRANSLATED_NAME AS name
        FROM
            (
                SELECT 1 AS sort_order, code AS COLUMN_NAME, ''dbo27'' AS TABLE_SCHEMA, ''report_types_code'' AS TABLE_NAME FROM dbo27.report_types
                UNION ALL SELECT 2 AS sort_order, name AS COLUMN_NAME, ''dbo27'' AS TABLE_SCHEMA, ''report_types_name'' AS TABLE_NAME FROM dbo27.report_types
                UNION ALL SELECT 3 AS sort_order, name AS COLUMN_NAME, ''dbo27'' AS TABLE_SCHEMA, ''report_section_types_name'' AS TABLE_NAME FROM dbo27.report_section_types
                UNION ALL SELECT 4 AS sort_order, name AS COLUMN_NAME, ''dbo27'' AS TABLE_SCHEMA, ''report_section_value_types_name'' AS TABLE_NAME FROM dbo27.report_section_value_types
                UNION ALL SELECT 5 AS sort_order, name AS COLUMN_NAME, ''dbo27'' AS TABLE_SCHEMA, ''report_section_total_types_name'' AS TABLE_NAME FROM dbo27.report_section_total_types
                UNION ALL SELECT 6 AS sort_order, name AS COLUMN_NAME, ''dbo27'' AS TABLE_SCHEMA, ''entry_sides_name'' AS TABLE_NAME FROM dbo27.entry_sides
                UNION ALL SELECT 7 AS sort_order, name AS COLUMN_NAME, ''dbo27'' AS TABLE_SCHEMA, ''ledger_account_types_name'' AS TABLE_NAME FROM dbo27.ledger_account_types
                UNION ALL SELECT 8 AS sort_order, name AS COLUMN_NAME, ''dbo27'' AS TABLE_SCHEMA, ''ledger_account_special_types_name'' AS TABLE_NAME FROM dbo27.ledger_account_special_types
                UNION ALL SELECT 9 AS sort_order, name AS COLUMN_NAME, ''dbo27'' AS TABLE_SCHEMA, ''member_types_name'' AS TABLE_NAME FROM dbo27.member_types
                UNION ALL SELECT * FROM (
                    SELECT
                        11 AS sort_order
                        , NULL AS COLUMN_NAME
                        , c.TABLE_SCHEMA
                        , c.TABLE_NAME
                    FROM
                        INFORMATION_SCHEMA.TABLES c
                    WHERE
                        c.TABLE_SCHEMA = ''xls27''
                    UNION ALL
                    SELECT
                        11 AS sort_order
                        , NULL AS COLUMN_NAME
                        , p.SPECIFIC_SCHEMA AS TABLE_SCHEMA
                        , p.SPECIFIC_NAME AS TABLE_NAME
                    FROM
                        INFORMATION_SCHEMA.ROUTINES p
                    WHERE
                        p.SPECIFIC_SCHEMA = ''xls27''
                        AND p.ROUTINE_TYPE = ''PROCEDURE''
                        AND NOT p.SPECIFIC_NAME LIKE ''%_insert''
                        AND NOT p.SPECIFIC_NAME LIKE ''%_update''
                        AND NOT p.SPECIFIC_NAME LIKE ''%_delete''
                        AND NOT p.SPECIFIC_NAME LIKE ''%_change''
                        AND NOT p.SPECIFIC_NAME LIKE ''%_merge''
                        AND NOT p.SPECIFIC_NAME LIKE ''usp_import_%''
                        AND NOT p.SPECIFIC_NAME LIKE ''usp_export_%''
                        AND NOT p.SPECIFIC_NAME LIKE ''xl_parameter_values_%''
                        AND NOT p.SPECIFIC_NAME LIKE ''xl_validation_list_%''
                        AND NOT p.SPECIFIC_NAME LIKE ''usp_update_table_format''
                    ) c' + '
                UNION ALL SELECT * FROM (
                    SELECT
                        12 AS sort_order
                        , c.COLUMN_NAME
                        , ''xls27'' AS TABLE_SCHEMA
                        , NULL AS TABLE_NAME
                    FROM
                        INFORMATION_SCHEMA.COLUMNS c
                    WHERE
                        c.TABLE_SCHEMA = ''dbo27''
                        AND NOT c.TABLE_NAME IN (''xl_automatic_handlers'', ''xl_online_help_handlers'')
                        AND NOT c.TABLE_NAME LIKE ''view_query_list%''
                    UNION
                    SELECT
                        12 AS sort_order
                        , SUBSTRING(p.PARAMETER_NAME, 2, 127) AS COLUMN_NAME
                        , p.SPECIFIC_SCHEMA AS TABLE_SCHEMA
                        , NULL AS TABLE_NAME
                    FROM
                        INFORMATION_SCHEMA.PARAMETERS p
                        INNER JOIN INFORMATION_SCHEMA.ROUTINES r ON r.SPECIFIC_SCHEMA = p.SPECIFIC_SCHEMA AND r.SPECIFIC_NAME = p.SPECIFIC_NAME
                    WHERE
                        p.SPECIFIC_SCHEMA = ''xls27''
                        AND r.ROUTINE_TYPE = ''PROCEDURE''
                        AND p.ORDINAL_POSITION > 0
                        AND NOT p.SPECIFIC_NAME LIKE ''%_insert''
                        AND NOT p.SPECIFIC_NAME LIKE ''%_update''
                        AND NOT p.SPECIFIC_NAME LIKE ''%_delete''
                        AND NOT p.SPECIFIC_NAME LIKE ''%_change''
                        AND NOT p.SPECIFIC_NAME LIKE ''%_merge''
                        AND NOT p.SPECIFIC_NAME LIKE ''usp_import_%''
                        AND NOT p.SPECIFIC_NAME LIKE ''usp_export_%''
                        AND NOT p.SPECIFIC_NAME LIKE ''xl_parameter_values_%''
                        AND NOT p.SPECIFIC_NAME LIKE ''xl_validation_list_%''
                        AND NOT p.SPECIFIC_NAME LIKE ''usp_update_table_format''
                        AND NOT p.PARAMETER_NAME IN (''@data_language'')
                    ) c
            ) t
            LEFT OUTER JOIN xls.translations tr ON
                tr.TABLE_SCHEMA = t.TABLE_SCHEMA
                AND (tr.TABLE_NAME = t.TABLE_NAME OR tr.TABLE_NAME IS NULL AND t.TABLE_NAME IS NULL)
                AND (tr.COLUMN_NAME = t.COLUMN_NAME OR tr.COLUMN_NAME IS NULL AND t.COLUMN_NAME IS NULL)
    ) s' + ' PIVOT (
        MAX(name) FOR LANGUAGE_NAME IN (' + @languages + ')
    ) t'
    + CASE WHEN @is_complete IS NULL THEN '' WHEN @is_complete = 1 THEN '
WHERE
    NOT (' + @complete + ')'
    ELSE '
WHERE
    ' + @complete END + '
ORDER BY
    t.sort_order
    , t.TABLE_SCHEMA
    , t.TABLE_NAME
    , t.COLUMN_NAME'

DECLARE @print int = 0

IF @print = 1
    BEGIN
    PRINT SUBSTRING(@sql, 1, 4000)
    IF LEN(@sql) > 4000  PRINT SUBSTRING(@sql, 4001, 4000)
    IF LEN(@sql) > 8000  PRINT SUBSTRING(@sql, 8001, 4000)
    IF LEN(@sql) > 12000 PRINT SUBSTRING(@sql, 12001, 4000)
    END

EXEC (@sql)

END


GO

