CREATE PROCEDURE [xls27].[usp_units_insert]
    @company_id int = NULL
    , @code nvarchar(50)
    , @name nvarchar(100)
    , @translated_name nvarchar(100) = NULL
    , @sort_order int = NULL
    , @factor float = NULL
    , @is_currency bit = NULL
    , @is_functional_currency bit = NULL
    , @is_base_currency bit = NULL
    , @is_active bit = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

IF @company_id IS NULL
    RETURN

SET @code = LTRIM(RTRIM(@code))
SET @name = LTRIM(RTRIM(@name))

SET NOCOUNT OFF

INSERT INTO dbo27.units
    ( company_id
    , code
    , name
    , sort_order
    , factor
    , is_currency
    , is_functional_currency
    , is_base_currency
    , is_active
    )
VALUES
    ( @company_id
    , @code
    , @name
    , COALESCE(@sort_order, 0)
    , COALESCE(@factor, 1)
    , COALESCE(@is_currency, 1)
    , COALESCE(@is_functional_currency, 1)
