-- =============================================
-- Author:      Gartle LLC
-- Release:     3.0, 2022-07-05
-- Description: The procedure selects report cell data to an Excel window
-- =============================================

CREATE PROCEDURE [xls27].[xl_actions_actual_report_cell_data]
    @company_id int = NULL
    , @year int = NULL
    , @entity_id int = NULL
    , @report int = NULL
    , @ref_number int = NULL
    , @account nvarchar(50) = NULL
    , @code1 nvarchar(50) = NULL
    , @code2 nvarchar(50) = NULL
    , @column_name nvarchar(128) = NULL
    , @data_language varchar(10) = NULL
WITH EXECUTE AS OWNER
AS
BEGIN

SET NOCOUNT ON

IF @year IS NULL SET @year = YEAR(GETDATE())

-- Checking the company

EXECUTE AS CALLER

SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

REVERT

-- Getting account_id and member_id using code1 and code2

DECLARE @report_type_id int, @account_id int, @member_id int

IF @report IS NOT NULL SELECT @report_type_id = id FROM dbo27.report_types WHERE id = @report

IF @account IS NOT NULL SELECT @account_id = id FROM dbo27.ledger_accounts WHERE company_id = @company_id AND code = @account

IF @code1 IS NOT NULL SELECT @member_id = id FROM dbo27.members WHERE company_id = @company_id AND code = @code1
IF @code2 IS NOT NULL SELECT @member_id = id FROM dbo27.members WHERE company_id = @company_id AND code = @code2

-- If any required value is NULL then return an empty result immediately

IF @company_id IS NULL OR @report_type_id IS NULL
    OR @account_id IS NULL AND @ref_number IS NULL
    BEGIN
    SELECT
        j.id
        , j.date
        , d.code AS debit
        , c.code AS credit
        , j.amount$ AS amount
        , COALESCE(users.name, j.modified_by) AS last_user
        , j.modified_on AS last_update
    FROM
        dbo27.journals j
        INNER JOIN dbo27.ledger_accounts d ON d.id = j.debit_account_id
        INNER JOIN dbo27.ledger_accounts c ON c.id = j.credit_account_id
        LEFT OUTER JOIN dbo27.users users ON users.company_id = j.company_id AND users.username = j.modified_by
    WHERE
        j.id IS NULL
    RETURN
    END

DECLARE @start_date date
DECLARE @end_date date

DECLARE @index int

SET @index = CHARINDEX(@column_name, '       data01, data02, data03, data04, data05, data06, data07, data08, data09, data10, data11')

IF @index > 0
    BEGIN
    SET @start_date = DATEFROMPARTS(@year, @index / 8, 1)
    SET @end_date = DATEADD(D, -1, DATEFROMPARTS(@year, @index / 8 + 1, 1))
    END
ELSE IF @column_name = 'data12'
    BEGIN
    SET @start_date = DATEFROMPARTS(@year, 12, 1)
    SET @end_date = DATEFROMPARTS(@year, 12, 31)
    END
ELSE
    BEGIN
    SET @start_date = DATEFROMPARTS(@year, 1, 1)
    SET @end_date = DATEFROMPARTS(@year, 12, 31)
    END

IF @account_id IS NOT NULL
    BEGIN
    SELECT
        j.id
        , CAST(j.date AS datetime) AS date
        , d.code AS debit
        , c.code AS credit
        , CAST(j.amount AS float) AS amount
        , ds.name AS debit_subsidiary_account
        , dc.name AS credit_subsidiary_account
        , j.description
        , COALESCE(dc.name, cc.name) AS currency
        , cr.rate
        , CAST(j.amount$ AS float) AS amount$
        , m4.code AS entity
        , m1.code AS account
        , m5.code AS region
        , m6.code AS product
        , COALESCE(users.name, j.modified_by) AS last_user
        , j.modified_on AS last_update
    FROM
        dbo27.journals j
        INNER JOIN dbo27.ledger_accounts d ON d.id = j.debit_account_id
        INNER JOIN dbo27.ledger_accounts c ON c.id = j.credit_account_id
        LEFT OUTER JOIN dbo27.members ds ON ds.id = j.debit_subsidiary_account_id
        LEFT OUTER JOIN dbo27.members cs ON cs.id = j.credit_subsidiary_account_id
        LEFT OUTER JOIN dbo27.units dc ON dc.id = ds.id AND dc.is_functional_currency = 0
        LEFT OUTER JOIN dbo27.units cc ON cc.id = cs.id AND cc.is_functional_currency = 0
        LEFT OUTER JOIN dbo27.actual_currency_rates cr ON cr.currency_id = COALESCE(dc.id, cc.id) AND cr.date = j.date
        INNER JOIN dbo27.report_section_accounts rsa ON rsa.account_type_id = d.account_type_id AND rsa.entry_side_id = 1 AND (rsa.pair_account_type_id IS NULL OR rsa.pair_account_type_id = c.account_type_id)
        INNER JOIN dbo27.report_sections rs ON rs.company_id = j.company_id AND rs.section_type_id = rsa.section_type_id
        INNER JOIN dbo27.report_section_types rst ON rst.id = rs.section_type_id
        LEFT OUTER JOIN dbo27.members m1 ON m1.id = j.account_id
        LEFT OUTER JOIN dbo27.members m4 ON m4.id = j.[entity_id]
        LEFT OUTER JOIN dbo27.members m5 ON m5.id = j.region_id
        LEFT OUTER JOIN dbo27.members m6 ON m6.id = j.product_id
        LEFT OUTER JOIN dbo27.members m7 ON m7.id = j.debit_subsidiary_account_id
        LEFT OUTER JOIN dbo27.users users ON users.company_id = m1.company_id AND users.username = j.modified_by
    WHERE
        rs.company_id = @company_id
        AND rst.report_type_id = @report_type_id
        AND d.id = @account_id
        AND rs.ref_number + COALESCE(d.ref_number, 0) = @ref_number
        AND COALESCE(j.entity_id, -1) = COALESCE(@entity_id, j.entity_id, -1)
        AND j.date <= @end_date
        AND (rsa.value_type_id = 1 AND j.date >= @start_date OR NOT rsa.value_type_id = 1)
        AND NOT c.account_type_id = 150
    UNION ALL
    SELECT
        j.id
        , CAST(j.date AS datetime) AS date
        , d.code AS debit
        , c.code AS credit
        , CAST(j.amount AS float) AS amount
        , ds.name AS debit_subsidiary_account
        , dc.name AS credit_subsidiary_account
        , j.description
        , COALESCE(dc.name, cc.name) AS currency
        , cr.rate
        , CAST(j.amount$ AS float) AS amount$
        , m4.code AS entity
        , m1.code AS account
        , m5.code AS region
        , m6.code AS product
        , COALESCE(users.name, j.modified_by) AS last_user
        , j.modified_on AS last_update
    FROM
        dbo27.journals j
        INNER JOIN dbo27.ledger_accounts d ON d.id = j.debit_account_id
        INNER JOIN dbo27.ledger_accounts c ON c.id = j.credit_account_id
        LEFT OUTER JOIN dbo27.members ds ON ds.id = j.debit_subsidiary_account_id
        LEFT OUTER JOIN dbo27.members cs ON cs.id = j.credit_subsidiary_account_id
        LEFT OUTER JOIN dbo27.units dc ON dc.id = ds.id AND dc.is_functional_currency = 0
        LEFT OUTER JOIN dbo27.units cc ON cc.id = cs.id AND cc.is_functional_currency = 0
        LEFT OUTER JOIN dbo27.actual_currency_rates cr ON cr.currency_id = COALESCE(dc.id, cc.id) AND cr.date = j.date
        INNER JOIN dbo27.report_section_accounts rsa ON rsa.account_type_id = c.account_type_id AND rsa.entry_side_id = 2 AND (rsa.pair_account_type_id IS NULL OR rsa.pair_account_type_id = d.account_type_id)
        INNER JOIN dbo27.report_sections rs ON rs.company_id = j.company_id AND rs.section_type_id = rsa.section_type_id
        INNER JOIN dbo27.report_section_types rst ON rst.id = rs.section_type_id
        LEFT OUTER JOIN dbo27.members m1 ON m1.id = j.account_id
        LEFT OUTER JOIN dbo27.members m4 ON m4.id = j.[entity_id]
        LEFT OUTER JOIN dbo27.members m5 ON m5.id = j.region_id
        LEFT OUTER JOIN dbo27.members m6 ON m6.id = j.product_id
        LEFT OUTER JOIN dbo27.members m7 ON m7.id = j.debit_subsidiary_account_id
        LEFT OUTER JOIN dbo27.users users ON users.company_id = m1.company_id AND users.username = j.modified_by
    WHERE
        rs.company_id = @company_id
        AND rst.report_type_id = @report_type_id
        AND c.id = @account_id
        AND rs.ref_number + COALESCE(c.ref_number, 0) = @ref_number
        AND COALESCE(j.entity_id, -1) = COALESCE(@entity_id, j.entity_id, -1)
        AND j.date <= @end_date
        AND (rsa.value_type_id = 1 AND j.date >= @start_date OR NOT rsa.value_type_id = 1)
        AND NOT d.account_type_id = 150
    UNION ALL
    SELECT
        NULL AS id
        , NULL AS date
        , CASE WHEN s.id = 1 THEN a.code ELSE NULL END AS debit
        , CASE WHEN s.id = 1 THEN NULL ELSE a.code END AS credit
        , CAST(CASE WHEN s.id = 1 AND r.balance > 0 THEN r.balance WHEN s.id = 2 AND r.balance < 0 THEN -r.balance ELSE NULL END AS float) AS amount
        , CASE WHEN s.id = 1 THEN sa.name ELSE NULL END AS debit_subsidiary_account
        , CASE WHEN s.id = 1 THEN NULL ELSE sa.name END AS credit_subsidiary_account
        , 'Opening Balance' AS description
        , c.name AS currency
        , cr.rate
        , CAST(CASE WHEN s.id = 1 AND r.balance$ > 0 THEN r.balance$ WHEN s.id = 2 AND r.balance$ < 0 THEN -r.balance$ ELSE NULL END AS float) AS amount$
        , NULL AS entity
        , NULL AS account
        , NULL AS region
        , NULL AS product
        , COALESCE(users.name, r.modified_by) AS last_user
        , r.modified_on AS last_update
    FROM
        dbo27.actual_balances r
        INNER JOIN dbo27.ledger_accounts a ON a.id = r.account_id
        LEFT OUTER JOIN dbo27.members sa ON sa.id = r.subsidiary_account_id
        LEFT OUTER JOIN dbo27.units c ON c.id = sa.id AND c.is_functional_currency = 0
        CROSS JOIN dbo27.entry_sides s
        INNER JOIN dbo27.report_section_accounts rsa ON rsa.account_type_id = a.account_type_id AND rsa.entry_side_id = s.id AND rsa.pair_account_type_id IS NULL
        INNER JOIN dbo27.report_sections rs ON rs.company_id = a.company_id AND rs.section_type_id = rsa.section_type_id
        LEFT OUTER JOIN dbo27.users users ON users.company_id = a.company_id AND users.username = r.modified_by
        LEFT OUTER JOIN (SELECT currency_id, MIN(date) AS date FROM dbo27.actual_currency_rates GROUP BY currency_id) cd ON cd.currency_id = c.id
        LEFT OUTER JOIN dbo27.actual_currency_rates cr ON cr.currency_id = c.id AND cr.date = cd.date
    WHERE
        a.company_id = @company_id
        AND (r.debit IS NOT NULL OR r.credit IS NOT NULL)
        AND rsa.value_type_id IN (2, 3, 4)
        AND rs.ref_number + COALESCE(a.ref_number, 0) = @ref_number
        AND (s.id = 1 AND r.balance$ > 0 OR s.id = 2 AND r.balance$ < 0)
    ORDER BY
        date
        , id
        , debit
        , credit
    END
ELSE
    BEGIN
    WITH factors (id, parent_id, is_parent) AS (
        SELECT
            rs.id
            , rs.id AS parent_id
            , COALESCE(rs.hide_items, 0) AS is_parent
        FROM
            dbo27.report_sections rs
        WHERE
            rs.company_id = @company_id
            AND rs.report_type_id = @report_type_id
        UNION ALL
        SELECT
            rs.id
            , rs.parent2_id
            , 1 AS is_parent
        FROM
            dbo27.report_sections rs
        WHERE
            rs.company_id = @company_id
            AND rs.report_type_id = @report_type_id
            AND rs.parent2_id IS NOT NULL
        UNION ALL
        SELECT
            f.id
            , rs.parent_id
            , 1 AS is_parent
        FROM
            factors f
            INNER JOIN dbo27.report_sections rs ON rs.id = f.parent_id
        WHERE
            rs.parent_id IS NOT NULL
            AND NOT f.id = rs.parent_id
    )
    SELECT
        j.id
        , CAST(j.date AS datetime) AS date
        , d.code AS debit
        , c.code AS credit
        , CAST(j.amount AS float) AS amount
        , ds.name AS debit_subsidiary_account
        , dc.name AS credit_subsidiary_account
        , j.description
        , COALESCE(dc.name, cc.name) AS currency
        , cr.rate
        , CAST(j.amount$ AS float) AS amount$
        , m4.code AS entity
        , m1.code AS account
        , m5.code AS region
        , m6.code AS product
        , COALESCE(users.name, j.modified_by) AS last_user
        , j.modified_on AS last_update
    FROM
        dbo27.journals j
        INNER JOIN dbo27.ledger_accounts d ON d.id = j.debit_account_id
        INNER JOIN dbo27.ledger_accounts c ON c.id = j.credit_account_id
        LEFT OUTER JOIN dbo27.members ds ON ds.id = j.debit_subsidiary_account_id
        LEFT OUTER JOIN dbo27.members cs ON cs.id = j.credit_subsidiary_account_id
        LEFT OUTER JOIN dbo27.units dc ON dc.id = ds.id AND dc.is_functional_currency = 0
        LEFT OUTER JOIN dbo27.units cc ON cc.id = cs.id AND cc.is_functional_currency = 0
        LEFT OUTER JOIN dbo27.actual_currency_rates cr ON cr.currency_id = COALESCE(dc.id, cc.id) AND cr.date = j.date
        INNER JOIN dbo27.report_section_accounts rsa ON rsa.account_type_id = d.account_type_id AND rsa.entry_side_id = 1 AND (rsa.pair_account_type_id IS NULL OR rsa.pair_account_type_id = c.account_type_id)
        INNER JOIN dbo27.report_sections rs ON rs.company_id = j.company_id AND rs.section_type_id = rsa.section_type_id
        INNER JOIN factors f ON f.id = rs.id
        INNER JOIN dbo27.report_sections frs ON frs.id = f.parent_id AND COALESCE(frs.show_line, 1) = 1
        INNER JOIN dbo27.report_section_types rst ON rst.id = rs.section_type_id
        LEFT OUTER JOIN dbo27.members m1 ON m1.id = j.account_id
        LEFT OUTER JOIN dbo27.members m4 ON m4.id = j.[entity_id]
        LEFT OUTER JOIN dbo27.members m5 ON m5.id = j.region_id
        LEFT OUTER JOIN dbo27.members m6 ON m6.id = j.product_id
        LEFT OUTER JOIN dbo27.members m7 ON m7.id = j.debit_subsidiary_account_id
        LEFT OUTER JOIN dbo27.users users ON users.company_id = m1.company_id AND users.username = j.modified_by
    WHERE
        rs.company_id = @company_id
        AND rst.report_type_id = @report_type_id
        AND frs.ref_number = @ref_number
        AND COALESCE(j.entity_id, -1) = COALESCE(@entity_id, j.entity_id, -1)
        AND j.date <= @end_date
        AND (rsa.value_type_id = 1 AND j.date >= @start_date OR NOT rsa.value_type_id = 1)
        AND NOT (rst.total_type_id = 2 AND @column_name = 'data00')
        AND NOT c.account_type_id = 150
    UNION ALL
    SELECT
        j.id
        , CAST(j.date AS datetime) AS date
        , d.code AS debit
        , c.code AS credit
        , CAST(j.amount AS float) AS amount
        , ds.name AS debit_subsidiary_account
        , dc.name AS credit_subsidiary_account
        , j.description
        , COALESCE(dc.name, cc.name) AS currency
        , cr.rate
        , CAST(j.amount$ AS float) AS amount$
        , m4.code AS entity
        , m1.code AS account
        , m5.code AS region
        , m6.code AS product
        , COALESCE(users.name, j.modified_by) AS last_user
        , j.modified_on AS last_update
    FROM
        dbo27.journals j
        INNER JOIN dbo27.ledger_accounts d ON d.id = j.debit_account_id
        INNER JOIN dbo27.ledger_accounts c ON c.id = j.credit_account_id
        LEFT OUTER JOIN dbo27.members ds ON ds.id = j.debit_subsidiary_account_id
        LEFT OUTER JOIN dbo27.members cs ON cs.id = j.credit_subsidiary_account_id
        LEFT OUTER JOIN dbo27.units dc ON dc.id = ds.id AND dc.is_functional_currency = 0
        LEFT OUTER JOIN dbo27.units cc ON cc.id = cs.id AND cc.is_functional_currency = 0
        LEFT OUTER JOIN dbo27.actual_currency_rates cr ON cr.currency_id = COALESCE(dc.id, cc.id) AND cr.date = j.date
        INNER JOIN dbo27.report_section_accounts rsa ON rsa.account_type_id = c.account_type_id AND rsa.entry_side_id = 2 AND (rsa.pair_account_type_id IS NULL OR rsa.pair_account_type_id = d.account_type_id)
        INNER JOIN dbo27.report_sections rs ON rs.company_id = j.company_id AND rs.section_type_id = rsa.section_type_id
        INNER JOIN factors f ON f.id = rs.id
        INNER JOIN dbo27.report_sections frs ON frs.id = f.parent_id AND COALESCE(frs.show_line, 1) = 1
        INNER JOIN dbo27.report_section_types rst ON rst.id = rs.section_type_id
        LEFT OUTER JOIN dbo27.members m1 ON m1.id = j.account_id
        LEFT OUTER JOIN dbo27.members m4 ON m4.id = j.[entity_id]
        LEFT OUTER JOIN dbo27.members m5 ON m5.id = j.region_id
        LEFT OUTER JOIN dbo27.members m6 ON m6.id = j.product_id
        LEFT OUTER JOIN dbo27.members m7 ON m7.id = j.debit_subsidiary_account_id
        LEFT OUTER JOIN dbo27.users users ON users.company_id = m1.company_id AND users.username = j.modified_by
    WHERE
        rs.company_id = @company_id
        AND rst.report_type_id = @report_type_id
        AND frs.ref_number = @ref_number
        AND COALESCE(j.entity_id, -1) = COALESCE(@entity_id, j.entity_id, -1)
        AND j.date <= @end_date
        AND (rsa.value_type_id = 1 AND j.date >= @start_date OR NOT rsa.value_type_id = 1)
        AND NOT (rst.total_type_id = 2 AND @column_name = 'data00')
        AND NOT d.account_type_id = 150
    UNION ALL
    SELECT
        NULL AS id
        , NULL AS date
        , CASE WHEN s.id = 1 THEN a.code ELSE NULL END AS debit
        , CASE WHEN s.id = 1 THEN NULL ELSE a.code END AS credit
        , CAST(CASE WHEN s.id = 1 AND r.balance > 0 THEN r.balance WHEN s.id = 2 AND r.balance < 0 THEN -r.balance ELSE NULL END AS float) AS amount
        , CASE WHEN s.id = 1 THEN sa.name ELSE NULL END AS debit_subsidiary_account
        , CASE WHEN s.id = 1 THEN NULL ELSE sa.name END AS credit_subsidiary_account
        , 'Opening Balance' AS description
        , c.name AS currency
        , cr.rate
        , CAST(CASE WHEN s.id = 1 AND r.balance$ > 0 THEN r.balance$ WHEN s.id = 2 AND r.balance$ < 0 THEN -r.balance$ ELSE NULL END AS float) AS amount$
        , NULL AS entity
        , NULL AS account
        , NULL AS region
        , NULL AS product
        , COALESCE(users.name, r.modified_by) AS last_user
        , r.modified_on AS last_update
    FROM
        dbo27.actual_balances r
        INNER JOIN dbo27.ledger_accounts a ON a.id = r.account_id
        LEFT OUTER JOIN dbo27.members sa ON sa.id = r.subsidiary_account_id
        LEFT OUTER JOIN dbo27.units c ON c.id = sa.id AND c.is_functional_currency = 0
        CROSS JOIN dbo27.entry_sides s
        INNER JOIN dbo27.report_section_accounts rsa ON rsa.account_type_id = a.account_type_id AND rsa.entry_side_id = s.id AND rsa.pair_account_type_id IS NULL
        INNER JOIN dbo27.report_sections rs ON rs.company_id = a.company_id AND rs.section_type_id = rsa.section_type_id
        INNER JOIN factors f ON f.id = rs.id
        INNER JOIN dbo27.report_sections frs ON frs.id = f.parent_id
        LEFT OUTER JOIN dbo27.users users ON users.company_id = a.company_id AND users.username = r.modified_by
        LEFT OUTER JOIN (SELECT currency_id, MIN(date) AS date FROM dbo27.actual_currency_rates GROUP BY currency_id) cd ON cd.currency_id = c.id
        LEFT OUTER JOIN dbo27.actual_currency_rates cr ON cr.currency_id = c.id AND cr.date = cd.date
    WHERE
        a.company_id = @company_id
        AND (r.debit IS NOT NULL OR r.credit IS NOT NULL)
        AND rsa.value_type_id IN (2, 3, 4)
        AND frs.ref_number = @ref_number
        AND COALESCE(frs.show_line, 1) = 1
        AND (s.id = 1 AND r.balance$ > 0 OR s.id = 2 AND r.balance$ < 0)
    ORDER BY
        date
        , id
        , debit
        , credit
    END

END


