-- Author:      Gartle LLC
-- Release:     3.0, 2022-07-05
-- Description: Closes account actual_balances
-- =============================================

CREATE PROCEDURE [xls27].[xl_actions_auto_close_accounts]
    @company_id int = NULL
AS
BEGIN

SET NOCOUNT ON

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE username = USER_NAME() AND is_active = 1 AND is_default = 1)
ELSE
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

IF @company_id IS NULL
    RETURN

-- balances by accounts and months

;WITH balances AS (
    SELECT
        j.account_id
        , date
        , SUM(debit) - SUM(credit) AS balance
    FROM
        (
            SELECT
                j.debit_account_id AS account_id
                , DATEADD(M, 1, DATEADD(D, -DATEPART(D, j.date), j.date)) AS date
                , j.amount$ AS debit
                , 0 AS credit
            FROM
                dbo27.journals j
            WHERE
                j.company_id = @company_id
                AND NOT j.auto_type_id = 30
            UNION ALL
            SELECT
                j.credit_account_id AS account_id
                , DATEADD(M, 1, DATEADD(D, -DATEPART(D, j.date), j.date)) AS date
                , 0 AS debit
                , j.amount$ AS credit
            FROM
                dbo27.journals j
            WHERE
                j.company_id = @company_id
                AND NOT j.auto_type_id = 30
        ) j
    GROUP BY
        account_id
        , date
)

-- Account-level entries

, entries1 AS (
    SELECT
        CASE WHEN balance < 0 AND a.closing_credit_account_id IS NOT NULL THEN a.id ELSE a.closing_debit_account_id END AS debit_account_id
        , CASE WHEN balance < 0 AND a.closing_credit_account_id IS NOT NULL THEN a.closing_credit_account_id ELSE a.id END AS credit_account_id
        , date
        , ROUND(CASE WHEN balance < 0 AND a.closing_credit_account_id IS NOT NULL THEN -balance ELSE balance END, 2) AS amount
    FROM
        balances b
        INNER JOIN dbo27.ledger_accounts a ON a.id = b.account_id
        LEFT OUTER JOIN dbo27.ledger_accounts d ON d.id = a.closing_debit_account_id
        LEFT OUTER JOIN dbo27.ledger_accounts c ON c.id = a.closing_credit_account_id
    WHERE
        (a.closing_debit_account_id IS NOT NULL OR a.closing_credit_account_id IS NOT NULL)
        AND a.subsidiary_group_id IS NULL
)

-- SELECT * FROM entries1

-- Account parents

, parents (id, parent_id) AS (
    SELECT
        a.id
        , a.parent_id
    FROM
        dbo27.ledger_accounts a
    WHERE
        a.parent_id IS NOT NULL

    UNION ALL
    SELECT
        p.id
        , a.parent_id
    FROM
        parents p
        INNER JOIN dbo27.ledger_accounts a ON a.id = p.parent_id
    WHERE
        a.parent_id IS NOT NULL
        AND NOT p.id = a.parent_id
)

-- SELECT * FROM parents

, entries (debit_account_id, credit_account_id, date, amount) AS (

    -- Account-level entries

    SELECT
        debit_account_id, credit_account_id, date, amount
    FROM
        entries1

    UNION ALL

    -- Parent-level entries

    SELECT
        CASE WHEN balance < 0 AND a.closing_credit_account_id IS NOT NULL THEN h.id ELSE a.closing_debit_account_id END AS debit_account_id
        , CASE WHEN balance < 0 AND a.closing_credit_account_id IS NOT NULL THEN a.closing_credit_account_id ELSE h.id END AS credit_account_id
        , date
        , ROUND(CASE WHEN balance < 0 AND a.closing_credit_account_id IS NOT NULL THEN -balance ELSE balance END, 2) AS amount
    FROM
        (
            SELECT
                account_id
                , date
                , SUM(balance) AS balance
            FROM
                (
                    -- Balances as is

                    SELECT
                        p.parent_id AS account_id
                        , date
                        , balance
                    FROM
                        balances b
                        INNER JOIN parents p ON p.id = b.account_id

                    -- Automatic entries on debit

                    UNION ALL
                    SELECT
                        p.parent_id AS account_id
                        , date
                        , amount
                    FROM
                        entries1 e
                        INNER JOIN parents p ON p.id = e.debit_account_id

                    -- Automatic entries on credit

                    UNION ALL
                    SELECT
                        p.parent_id AS account_id
                        , date
                        , -amount
                    FROM
                        entries1 e
                        INNER JOIN parents p ON p.id = e.credit_account_id
                ) p
            GROUP BY
                account_id
                , date
        ) b
        INNER JOIN dbo27.ledger_accounts a ON a.id = b.account_id
        INNER JOIN dbo27.ledger_accounts h ON h.id = a.closing_child_account_id
        LEFT OUTER JOIN dbo27.ledger_accounts d ON d.id = a.closing_debit_account_id
        LEFT OUTER JOIN dbo27.ledger_accounts c ON c.id = a.closing_credit_account_id
    WHERE
        (a.closing_debit_account_id IS NOT NULL OR a.closing_credit_account_id IS NOT NULL)
)

-- Testing entries
-- SELECT t.id, s.date, s.debit_account_id, s.credit_account_id, s.amount FROM entries s
-- LEFT OUTER JOIN dbo27.journals t ON t.debit_account_id = s.debit_account_id AND t.credit_account_id = s.credit_account_id AND t.date = s.date AND t.auto_type_id = 30

-- Merge entries

MERGE dbo27.journals t
USING
    (
        SELECT e.date, e.debit_account_id, e.credit_account_id, SUM(e.amount) AS amount FROM entries e GROUP BY e.date, e.debit_account_id, e.credit_account_id
    ) s ON t.auto_type_id = 30
        AND t.date = s.date
        AND t.debit_account_id = s.debit_account_id
        AND t.credit_account_id = s.credit_account_id
WHEN MATCHED AND (NOT COALESCE(t.amount, 0) = s.amount OR NOT t.modified_by = 'auto') THEN
    UPDATE SET amount = s.amount, amount$ = s.amount, t.modified_by = 'auto'
WHEN NOT MATCHED THEN
    INSERT (company_id, date, amount, amount$, debit_account_id, credit_account_id, auto_type_id, created_by, created_on)
    VALUES (@company_id, date, amount, amount, debit_account_id, credit_account_id, 30, 'auto', GETDATE())
WHEN NOT MATCHED BY SOURCE AND company_id = @company_id AND auto_type_id = 30 THEN
    DELETE
    ;

END


GO

