-- Author:      Gartle LLC
-- Release:     3.0, 2022-07-05
-- Description: Calculates gains and losses from currency transactions
--
-- Dr    Cr    Amount Amount$    Dr    Cr    Amount Amount$
-- Conv  Cash  1000   1100        Conv  Cash  1000   1100
-- Cash  Conv  1120   1120        Cash  Conv  1080   1080
-- Conv  Gain     0     20        Loss  Conv     0     20
-- =============================================

CREATE PROCEDURE [xls27].[xl_actions_auto_currency_transactions]
    @company_id int = NULL
AS
BEGIN

SET NOCOUNT ON

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE username = USER_NAME() AND is_active = 1 AND is_default = 1)
ELSE
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

IF @company_id IS NULL
    RETURN

DECLARE @gain_account_id int
DECLARE @loss_account_id int

SELECT TOP 1 @gain_account_id = a.id FROM dbo27.ledger_accounts a INNER JOIN dbo27.ledger_account_types t ON t.id = a.account_type_id WHERE a.company_id = @company_id AND t.specific_type_id = 5
SELECT TOP 1 @loss_account_id = a.id FROM dbo27.ledger_accounts a INNER JOIN dbo27.ledger_account_types t ON t.id = a.account_type_id WHERE a.company_id = @company_id AND t.specific_type_id = 6

IF @gain_account_id IS NULL OR @loss_account_id IS NULL
    RETURN

;WITH entries AS (
SELECT
    p.id AS ref_record_id
    , j.date

    -- Dr    Cr    Amount Amount$    Dr    Cr    Amount Amount$
    -- Conv  Cash  1000   1100        Conv  Cash  1000   1100        p.amount$
    -- Cash  Conv  1120   1120        Cash  Conv  1080   1080        j.amount$
    -- Conv  Gain     0     20        Loss  Conv     0     20

    , CASE WHEN p.amount$ < j.amount$ THEN p.debit_account_id ELSE @loss_account_id END AS debit_account_id
    , CASE WHEN p.amount$ < j.amount$ THEN @gain_account_id ELSE j.credit_account_id END AS credit_account_id
    , CASE WHEN p.amount$ < j.amount$ THEN p.debit_subsidiary_account_id ELSE NULL END AS debit_subsidiary_account_id
    , CASE WHEN p.amount$ < j.amount$ THEN NULL ELSE j.credit_subsidiary_account_id END AS credit_subsidiary_account_id
    , CASE WHEN p.amount$ < j.amount$ THEN j.amount$ - p.amount$ ELSE p.amount$ - j.amount$ END AS amount
FROM
    dbo27.journals j
    INNER JOIN dbo27.journals p ON p.id = j.id - 1 AND p.debit_account_id = j.credit_account_id
    INNER JOIN dbo27.members cs ON cs.id = p.credit_subsidiary_account_id
    INNER JOIN dbo27.members ds ON ds.id = j.debit_subsidiary_account_id
WHERE
    j.company_id = @company_id
    AND NOT cs.unit_id = ds.unit_id
)
-- SELECT * FROM entries

MERGE dbo27.journals t
USING
    (
        SELECT * FROM entries e
    ) s ON t.ref_record_id = s.ref_record_id
        AND t.auto_type_id = 10
        AND t.date = s.date
        AND t.debit_account_id = s.debit_account_id
        AND t.credit_account_id = s.credit_account_id
        AND COALESCE(t.debit_subsidiary_account_id, -1) = COALESCE(s.debit_subsidiary_account_id, -1)
        AND COALESCE(t.credit_subsidiary_account_id, -1) = COALESCE(s.credit_subsidiary_account_id, -1)
WHEN MATCHED AND NOT COALESCE(t.amount, 0) = s.amount THEN
    UPDATE SET amount = s.amount, amount$ = s.amount
WHEN NOT MATCHED THEN
    INSERT (company_id, date, amount, amount$, debit_account_id, credit_account_id, ref_record_id, auto_type_id, created_by, created_on)
    VALUES (@company_id, date, amount, amount,  debit_account_id, credit_account_id, ref_record_id, 10, 'auto', GETDATE())
WHEN NOT MATCHED BY SOURCE AND company_id = @company_id AND t.auto_type_id = 10 THEN
    DELETE;

END


GO

