-- Author:      Gartle LLC
-- Release:     3.0, 2022-07-05
-- Description: Calculates gains and losses from currency translation
-- =============================================

CREATE PROCEDURE [xls27].[xl_actions_auto_currency_translation]
    @company_id int = NULL
AS
BEGIN

SET NOCOUNT ON

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE username = USER_NAME() AND is_active = 1 AND is_default = 1)
ELSE
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

IF @company_id IS NULL
    RETURN

DECLARE @gain_account_id int
DECLARE @loss_account_id int

SELECT TOP 1 @gain_account_id = a.id FROM dbo27.ledger_accounts a INNER JOIN dbo27.ledger_account_types t ON t.id = a.account_type_id WHERE a.company_id = @company_id AND t.specific_type_id = 7
SELECT TOP 1 @loss_account_id = a.id FROM dbo27.ledger_accounts a INNER JOIN dbo27.ledger_account_types t ON t.id = a.account_type_id WHERE a.company_id = @company_id AND t.specific_type_id = 8

IF @gain_account_id IS NULL OR @loss_account_id IS NULL
    RETURN

-- Balance and turnover by accounts and months

;WITH balances (account_id, subsidiary_account_id, date, rate, balance, balance$, new_balance$, delta$) AS (
    SELECT
        account_id
        , subsidiary_account_id
        , date
        , MAX(rate) AS rate
        , SUM(debit) - SUM(credit) AS balance
        , SUM(debit$) - SUM(credit$) AS balance$
        , ROUND((SUM(debit) - SUM(credit)) * MAX(rate), 2) AS new_balance$
        , ROUND((SUM(debit) - SUM(credit)) * MAX(rate), 2) - ROUND((SUM(debit$) - SUM(credit$)), 2) AS delta$
    FROM
        (
            SELECT
                b.account_id
                , b.subsidiary_account_id
                , r.date
                , r.rate
                , b.balance AS debit
                , b.balance$ AS debit$
                , 0 AS credit
                , 0 AS credit$
            FROM
                dbo27.actual_balances b
                INNER JOIN dbo27.members s ON s.id = b.subsidiary_account_id
                INNER JOIN dbo27.units u ON u.id = s.unit_id
                INNER JOIN dbo27.actual_currency_rates r ON r.currency_id = s.unit_id AND r.is_calc_date = 1
            WHERE
                s.company_id = @company_id
                AND NOT u.is_functional_currency = 1

            UNION ALL
            SELECT
                j.debit_account_id AS account_id
                , j.debit_subsidiary_account_id AS subsidiary_account_id
                , r.date
                , r.rate
                , j.amount AS debit
                , j.amount$ AS debit$
                , 0 AS credit
                , 0 AS credit$
            FROM
                dbo27.journals j
                INNER JOIN dbo27.members ds ON ds.id = j.debit_subsidiary_account_id
                INNER JOIN dbo27.units u ON u.id = ds.unit_id
                INNER JOIN dbo27.actual_currency_rates r ON r.currency_id = ds.unit_id AND r.is_calc_date = 1 AND r.date >= j.date
            WHERE
                j.company_id = @company_id
                AND NOT u.is_functional_currency = 1
                AND NOT j.auto_type_id = 20

            UNION ALL
            SELECT
                j.credit_account_id AS account_id
                , j.credit_subsidiary_account_id AS subsidiary_account_id
                , r.date
                , r.rate
                , 0 AS debit
                , 0 AS debit$
                , j.amount AS credit
                , j.amount$ AS credit$
            FROM
                dbo27.journals j
                INNER JOIN dbo27.members cs ON cs.id = j.credit_subsidiary_account_id
                INNER JOIN dbo27.units u ON u.id = cs.unit_id
                INNER JOIN dbo27.actual_currency_rates r ON r.currency_id = cs.unit_id AND r.is_calc_date = 1 AND r.date >= j.date
            WHERE
                j.company_id = @company_id
                AND NOT u.is_functional_currency = 1
                AND NOT j.auto_type_id = 20
        ) j
    GROUP BY
        account_id
        , subsidiary_account_id
        , date
)

-- SELECT * FROM balances

, balances2 (account_id, subsidiary_account_id, date, rate, balance, balance$, new_balance$, delta$, amount$) AS (
    SELECT
        account_id
        , subsidiary_account_id
        , date
        , rate
        , balance
        , balance$
        , new_balance$
        , delta$
        , delta$ - LAG(delta$, 1, 0) OVER (PARTITION BY account_id, subsidiary_account_id ORDER BY date) AS amount$
    FROM
        balances b
)

-- SELECT * FROM balances2

, entries (date, debit_account_id, debit_subsidiary_account_id, credit_account_id, credit_subsidiary_account_id, amount, amount$) AS (
    SELECT
        b.date
        , CASE WHEN amount$ < 0 THEN @loss_account_id ELSE b.account_id END AS debit_account_id
        , CASE WHEN amount$ < 0 THEN NULL ELSE b.subsidiary_account_id END AS debit_subsidiary_account_id
        , CASE WHEN amount$ < 0 THEN b.account_id ELSE @gain_account_id END AS credit_account_id
        , CASE WHEN amount$ < 0 THEN b.subsidiary_account_id ELSE NULL END AS credit_subsidiary_account_id
        , 0
        , CASE WHEN amount$ < 0 THEN ROUND(-amount$, 2) ELSE ROUND(amount$, 2) END AS amount$
    FROM
        balances2 b
        INNER JOIN dbo27.members s ON s.id = b.subsidiary_account_id
        INNER JOIN dbo27.actual_currency_rates r ON r.currency_id = s.unit_id AND r.date = b.date
    WHERE
        NOT b.amount$ = 0
)

-- SELECT * FROM entries

MERGE dbo27.journals t
USING
    (
        SELECT * FROM entries e
    ) s ON t.auto_type_id = 20
        AND t.date = s.date
        AND t.debit_account_id = s.debit_account_id
        AND t.credit_account_id = s.credit_account_id
        AND (t.debit_account_id = s.debit_account_id OR t.credit_account_id = s.credit_account_id)
WHEN MATCHED AND NOT COALESCE(t.amount$, 0) = s.amount$ THEN
    UPDATE SET amount$ = s.amount$
WHEN NOT MATCHED THEN
    INSERT (company_id, date, amount, amount$, debit_account_id, credit_account_id, debit_subsidiary_account_id, credit_subsidiary_account_id, auto_type_id, created_by, created_on)
    VALUES (@company_id, date, amount, amount$, debit_account_id, credit_account_id, debit_subsidiary_account_id, credit_subsidiary_account_id, 20, 'auto', GETDATE())
WHEN NOT MATCHED BY SOURCE AND company_id = @company_id AND t.auto_type_id = 20 THEN
    DELETE;

END


