-- Release:     3.0, 2022-07-05
-- Description: The procedure selects report cell data to an Excel window
-- =============================================

CREATE PROCEDURE [xls27].[xl_actions_budget_report_cell_data]
    @company_id int = NULL
    , @category_id int = NULL
    , @time_id int = NULL
    , @entity_id int = NULL
    , @year int = NULL
    , @report int = NULL
    , @ref_number int = NULL
    , @account nvarchar(50) = NULL
    , @code1 nvarchar(50) = NULL
    , @code2 nvarchar(50) = NULL
    , @column_name nvarchar(128) = NULL
    , @data_language varchar(10) = NULL
WITH EXECUTE AS OWNER
AS
BEGIN

SET NOCOUNT ON

-- Checking the company

EXECUTE AS CALLER

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 u.company_id FROM dbo27.members t INNER JOIN dbo27.users u ON u.company_id = t.company_id  WHERE t.id = @category_id AND u.username = USER_NAME() AND u.is_active = 1)
ELSE
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

REVERT

-- Replace values calculated by Excel formulas

IF @ref_number = 0 SET @ref_number = NULL
IF @entity_id = 0 SET @entity_id = NULL
IF @account IN ('0', '') SET @account = NULL
IF @code1 IN ('0', '') SET @code1 = NULL
IF @code2 IN ('0', '') SET @code2 = NULL

-- Getting account_id and member_id using code1 and code2

DECLARE @report_type_id int, @account_id int, @member_id1 int, @member_id2 int

IF @report IS NOT NULL SELECT @report_type_id = id FROM dbo27.report_types WHERE id = @report

IF @account IS NOT NULL SELECT @account_id = id FROM dbo27.ledger_accounts WHERE company_id = @company_id AND code = @account

IF @code1 IS NOT NULL SELECT @member_id1 = id FROM dbo27.members WHERE company_id = @company_id AND code = @code1
IF @code2 IS NOT NULL SELECT @member_id2 = id FROM dbo27.members WHERE company_id = @company_id AND code = @code2

DECLARE @search_by_account bit = CASE WHEN @ref_number IS NOT NULL OR @account IS NOT NULL OR @report_type_id NOT IN (1, 4) THEN 1 ELSE 0 END

-- Find max value and total types

DECLARE @value_type_id tinyint = NULL
DECLARE @total_type_id tinyint = NULL

IF @search_by_account = 0
    BEGIN
    SET @value_type_id = 1
    SET @total_type_id = 1
    END
ELSE IF @account_id IS NOT NULL
    BEGIN
    SELECT
        @value_type_id = MAX(rsa.value_type_id)
        , @total_type_id = MAX(rst.total_type_id)
    FROM
        dbo27.report_sections rs
        INNER JOIN dbo27.report_section_accounts rsa ON rsa.section_type_id = rs.section_type_id
        INNER JOIN dbo27.ledger_accounts a ON a.company_id = rs.company_id AND a.account_type_id = rsa.account_type_id
        INNER JOIN dbo27.report_section_types rst ON rst.id = rs.section_type_id
    WHERE
        rs.company_id = @company_id
        AND a.id = @account_id
        AND rs.ref_number + COALESCE(a.ref_number, 0) = @ref_number
    END
ELSE IF @ref_number IS NOT NULL
    BEGIN
    WITH factors (id, parent_id, is_parent) AS (
        SELECT

            rs.id
            , rs.id AS parent_id
            , COALESCE(rs.hide_items, 0) AS is_parent
        FROM
            dbo27.report_sections rs
        WHERE
            rs.company_id = @company_id
            AND rs.report_type_id = @report_type_id
        UNION ALL
        SELECT
            rs.id
            , rs.parent2_id
            , 1 AS is_parent
        FROM
            dbo27.report_sections rs
        WHERE
            rs.company_id = @company_id
            AND rs.report_type_id = @report_type_id
            AND rs.parent2_id IS NOT NULL
        UNION ALL
        SELECT
            f.id
            , rs.parent_id
            , 1 AS is_parent
        FROM
            factors f
            INNER JOIN dbo27.report_sections rs ON rs.id = f.parent_id
        WHERE
            rs.parent_id IS NOT NULL
            AND NOT f.id = rs.parent_id
    )
    SELECT
        @value_type_id = MAX(rsa.value_type_id)
        , @total_type_id = MAX(rst.total_type_id)
    FROM
        dbo27.report_sections rs
        INNER JOIN factors f ON f.id = rs.id
        INNER JOIN dbo27.report_sections frs ON frs.id = f.parent_id
        INNER JOIN dbo27.report_section_accounts rsa ON rsa.section_type_id = rs.section_type_id
        INNER JOIN dbo27.report_section_types rst ON rst.id = rs.section_type_id
    WHERE
        rs.company_id = @company_id
        AND frs.ref_number = @ref_number
    END

-- If any required value is NULL then return an empty result immediately

IF @company_id IS NULL OR @report_type_id IS NULL OR @category_id IS NULL OR @time_id IS NULL
    OR @account_id IS NULL AND @ref_number IS NULL AND @member_id1 IS NULL
    OR @value_type_id IS NULL OR @total_type_id IS NULL
    BEGIN
    SELECT
        CAST(NULL AS nvarchar(50)) AS entity
        , CAST(NULL AS nvarchar(50)) AS description
        , CAST(NULL AS nvarchar(50)) AS account
        , CAST(NULL AS nvarchar(50)) AS subaccount
        , CAST(NULL AS nvarchar(50)) AS region
        , CAST(NULL AS nvarchar(50)) AS product
        , CAST(NULL AS nvarchar(50)) AS vat
        , CAST(NULL AS nvarchar(50)) AS unit
    FROM
        dbo27.budget_requests
    WHERE
        id IS NULL
    RETURN
    END

-- String identifiers

DECLARE @company_sid nvarchar(50) = CAST(@company_id AS nvarchar(50))
DECLARE @report_type_sid nvarchar(50) = CAST(@report_type_id AS nvarchar(50))
DECLARE @category_sid nvarchar(50) = CAST(@category_id AS nvarchar(50))
DECLARE @time_sid nvarchar(50) = CAST(@time_id AS nvarchar(50))
DECLARE @entity_sid nvarchar(50) = COALESCE(CAST(@entity_id AS nvarchar(50)), 'NULL')
DECLARE @account_sid nvarchar(50) = COALESCE(CAST(@account_id AS nvarchar(50)), 'NULL')
DECLARE @member_sid1 nvarchar(50) = COALESCE(CAST(@member_id1 AS nvarchar(50)), 'NULL')
DECLARE @member_sid2 nvarchar(50) = COALESCE(CAST(@member_id2 AS nvarchar(50)), 'NULL')

DECLARE @sql nvarchar(max) = ''

-- Select rows of all requests of the specified category, period, and entity

IF @search_by_account = 0
    SET @sql = @sql + 'WITH members (id, parent_id) AS (
    SELECT
        m.id
        , m.id AS parent_id
    FROM
        dbo27.members m
    WHERE
        m.company_id = ' + @company_sid + '
        AND m.dimension_id = 1
    UNION ALL
    SELECT
        f.id
        , m.parent_id
    FROM
        members f
        INNER JOIN dbo27.members m ON m.id = f.parent_id
    WHERE
        m.parent_id IS NOT NULL
        AND NOT f.id = m.parent_id
)
, '
    ELSE
    SET @sql = @sql + 'WITH '

SET @sql = @sql + 'e1 AS (
    SELECT
        r.id
        , m1.is_charge_net
        , m1.payment_debit_id, m1.payment_credit_id, m1.charge_debit_id, m1.charge_credit_id, m1.vat_debit_id, m1.vat_credit_id
        , r.subsidiary_account_id
        , t.cf_sign, t.pl_sign, t.vat_sign
    FROM
        dbo27.budget_requests r
        INNER JOIN dbo27.members m1 ON m1.id = r.account_id
        INNER JOIN dbo27.member_types t ON t.id = m1.account_type_id'
        + CASE WHEN @search_by_account = 1 THEN '' ELSE '
        INNER JOIN members f ON f.id = r.account_id'
            END + '
    WHERE
        r.category_id = ' + @category_sid + '
        AND r.time_id = ' + @time_sid + '
        AND m1.company_id = ' + @company_sid + CASE WHEN @entity_id IS NULL THEN '' ELSE '
        AND r.[entity_id] = ' + @entity_sid END
        + CASE WHEN @search_by_account = 1 THEN '' ELSE '
        AND f.parent_id = ' + @member_sid1
            END + '
)'

-- Transform three pair of accounts into six records

IF @search_by_account = 1
    SET @sql = @sql + '
, e2 AS (
    SELECT id, is_charge_net, 1 AS entry_id, 1 AS entry_side_id, payment_debit_id AS ledger_account_id, subsidiary_account_id, payment_credit_id AS pair_account_id FROM e1 WHERE payment_debit_id IS NOT NULL AND payment_credit_id IS NOT NULL
    UNION ALL
    SELECT id, is_charge_net, 1 AS entry_id, 2 AS entry_side_id, payment_credit_id AS ledger_account_id, subsidiary_account_id, payment_debit_id AS pair_account_id FROM e1 WHERE payment_debit_id IS NOT NULL AND payment_credit_id IS NOT NULL
    UNION ALL
    SELECT id, is_charge_net, 2 AS entry_id, 1 AS entry_side_id, charge_debit_id AS ledger_account_id, subsidiary_account_id, charge_credit_id AS pair_account_id FROM e1 WHERE charge_debit_id IS NOT NULL AND charge_credit_id IS NOT NULL
    UNION ALL
    SELECT id, is_charge_net, 2 AS entry_id, 2 AS entry_side_id, charge_credit_id AS ledger_account_id, subsidiary_account_id, charge_debit_id AS pair_account_id FROM e1 WHERE charge_debit_id IS NOT NULL AND charge_credit_id IS NOT NULL
    UNION ALL
    SELECT id, is_charge_net, 3 AS entry_id, 1 AS entry_side_id, vat_debit_id AS ledger_account_id, subsidiary_account_id, vat_credit_id AS pair_account_id FROM e1 WHERE vat_debit_id IS NOT NULL AND vat_credit_id IS NOT NULL
    UNION ALL
    SELECT id, is_charge_net, 3 AS entry_id, 2 AS entry_side_id, vat_credit_id AS ledger_account_id, subsidiary_account_id, vat_debit_id AS pair_account_id FROM e1 WHERE vat_debit_id IS NOT NULL AND vat_credit_id IS NOT NULL
)'
    ELSE IF @report_type_id = 4
    SET @sql = @sql + '
, e2 AS (
    SELECT id, is_charge_net, 1 AS entry_id, 1 AS entry_side_id, payment_debit_id AS ledger_account_id, subsidiary_account_id, payment_credit_id AS pair_account_id, 1 AS value_sign FROM e1 WHERE cf_sign IN (0, 1)
    UNION ALL
    SELECT id, is_charge_net, 1 AS entry_id, 2 AS entry_side_id, payment_credit_id AS ledger_account_id, subsidiary_account_id, payment_debit_id AS pair_account_id, -1 AS value_sign FROM e1 WHERE cf_sign = -1
)'
    ELSE
    SET @sql = @sql + '
, e2 AS (
    SELECT id, is_charge_net, 2 AS entry_id, 1 AS entry_side_id, charge_debit_id AS ledger_account_id, subsidiary_account_id, charge_credit_id AS pair_account_id, 1 AS value_sign FROM e1 WHERE cf_sign IN (0, 1)
    UNION ALL
    SELECT id, is_charge_net, 2 AS entry_id, 2 AS entry_side_id, charge_credit_id AS ledger_account_id, subsidiary_account_id, charge_debit_id AS pair_account_id, -1 AS value_sign FROM e1 WHERE cf_sign = -1
    UNION ALL
    SELECT id, is_charge_net, 3 AS entry_id, 1 AS entry_side_id, vat_debit_id AS ledger_account_id, subsidiary_account_id, vat_credit_id AS pair_account_id, -1 AS value_sign FROM e1 WHERE vat_sign = 1 AND is_charge_net = 0
    UNION ALL
    SELECT id, is_charge_net, 3 AS entry_id, 2 AS entry_side_id, vat_credit_id AS ledger_account_id, subsidiary_account_id, vat_debit_id AS pair_account_id, 1 AS value_sign FROM e1 WHERE vat_sign = -1 AND is_charge_net = 0
)'

-- Expand rows to section types

IF @search_by_account = 1
    SET @sql = @sql + '
, e3 AS (
    SELECT
        r.id
        , r.is_charge_net
        , r.entry_id
        , r.entry_side_id
        , r.ledger_account_id
        , r.subsidiary_account_id
        , pair_account_id
        , rsa.section_type_id
        , rsa.value_type_id
        , rsa.value_sign
        , a.ref_number
    FROM
        e2 r
        INNER JOIN dbo27.ledger_accounts a ON a.id = r.ledger_account_id
        INNER JOIN dbo27.report_section_accounts rsa ON rsa.account_type_id = a.account_type_id AND rsa.entry_side_id = r.entry_side_id
        INNER JOIN dbo27.report_section_types rst ON rst.id = rsa.section_type_id
        LEFT OUTER JOIN dbo27.ledger_accounts c ON c.id = r.pair_account_id
    WHERE
        rst.report_type_id = ' + @report_type_sid + '
        AND rsa.pair_account_type_id IS NULL OR rsa.pair_account_type_id = c.account_type_id
)'

-- Select report sections id->parent_id to get aggregated values

IF @search_by_account = 1
    SET @sql = @sql + '
, factors (id, parent_id, is_parent) AS (
    SELECT
        rs.id
        , rs.id AS parent_id
        , COALESCE(rs.hide_items, 0) AS is_parent
    FROM
        dbo27.report_sections rs
    WHERE
        rs.company_id = ' + @company_sid + '
        AND rs.report_type_id = ' + @report_type_sid + '
    UNION ALL
    SELECT
        rs.id
        , rs.parent2_id
        , 1 AS is_parent
    FROM
        dbo27.report_sections rs
    WHERE
        rs.company_id = ' + @company_sid + '
        AND rs.report_type_id = ' + @report_type_sid + '
        AND rs.parent2_id IS NOT NULL
    UNION ALL
    SELECT
        f.id
        , rs.parent_id
        , 1 AS is_parent
    FROM
        factors f
        INNER JOIN dbo27.report_sections rs ON rs.id = f.parent_id
    WHERE
        rs.parent_id IS NOT NULL
        AND NOT f.id = rs.parent_id
)'

-- Filter rows of the row to get details by ref_number or ref_number and account

IF @search_by_account = 1
    SET @sql = @sql + '
, e4 AS (
    SELECT
        r.id
        , r.is_charge_net
        , r.entry_id
        , r.entry_side_id
        , r.ledger_account_id
        , r.subsidiary_account_id
        , r.pair_account_id
        , r.section_type_id
        , r.value_type_id
        , r.value_sign
        , r.ref_number
        , rst.total_type_id
    FROM
        e3 r
        INNER JOIN dbo27.report_sections rs ON rs.section_type_id = r.section_type_id
        INNER JOIN dbo27.report_section_types rst ON rst.id = rs.section_type_id'
        + CASE WHEN @account_id IS NOT NULL THEN '' ELSE '
        INNER JOIN factors f ON f.id = rs.id
        INNER JOIN dbo27.report_sections frs ON frs.id = f.parent_id AND COALESCE(frs.show_line, 1) = 1'
            END + '
    WHERE
        rs.company_id = ' + @company_sid + '
        AND rs.report_type_id = ' + @report_type_sid
        + CASE WHEN @account_id IS NOT NULL THEN '
        AND r.ledger_account_id = ' + @account_sid + '
        AND rs.ref_number + COALESCE(r.ref_number, 0) = ' + CAST(@ref_number AS nvarchar(50))
            ELSE '
        AND frs.ref_number = ' + CAST(@ref_number AS nvarchar(50))
            END + '
)'
ELSE
    SET @sql = @sql + '
, e4 AS (
    SELECT
        r.id
        , r.is_charge_net
        , r.entry_id
        , r.entry_side_id
        , r.ledger_account_id
        , r.subsidiary_account_id
        , r.pair_account_id
        , NULL AS section_type_id
        , 1 AS value_type_id
        , r.value_sign
        , NULL AS ref_number
        , 1 AS total_type_id
    FROM
        e2 r
)'

-- Calculated payments, charges, and VAT for selected rows of budget requests

SET @sql = @sql + '
, a1 AS (

    SELECT
        s.id
        , s.is_charge_net
        , s.entry_id
        , s.entry_side_id
        , s.ledger_account_id
        , s.subsidiary_account_id
        , s.pair_account_id
        , s.section_type_id
        , s.value_type_id
        , s.value_sign
        , s.ref_number
        , s.total_type_id

        , COALESCE(CAST(r.balance00 * COALESCE(cr.balance00, 1) AS numeric(15,2)), 0) AS balance00
        ' + '
        , COALESCE(CAST(r.payments01 * COALESCE(cr.period01, 1) AS numeric(15,2)), 0) AS payments01
        , COALESCE(CAST(r.payments02 * COALESCE(cr.period02, 1) AS numeric(15,2)), 0) AS payments02
        , COALESCE(CAST(r.payments03 * COALESCE(cr.period03, 1) AS numeric(15,2)), 0) AS payments03
        , COALESCE(CAST(r.payments04 * COALESCE(cr.period04, 1) AS numeric(15,2)), 0) AS payments04
        , COALESCE(CAST(r.payments05 * COALESCE(cr.period05, 1) AS numeric(15,2)), 0) AS payments05
        , COALESCE(CAST(r.payments06 * COALESCE(cr.period06, 1) AS numeric(15,2)), 0) AS payments06
        , COALESCE(CAST(r.payments07 * COALESCE(cr.period07, 1) AS numeric(15,2)), 0) AS payments07
        , COALESCE(CAST(r.payments08 * COALESCE(cr.period08, 1) AS numeric(15,2)), 0) AS payments08
        , COALESCE(CAST(r.payments09 * COALESCE(cr.period09, 1) AS numeric(15,2)), 0) AS payments09
        , COALESCE(CAST(r.payments10 * COALESCE(cr.period10, 1) AS numeric(15,2)), 0) AS payments10
        , COALESCE(CAST(r.payments11 * COALESCE(cr.period11, 1) AS numeric(15,2)), 0) AS payments11
        , COALESCE(CAST(r.payments12 * COALESCE(cr.period12, 1) AS numeric(15,2)), 0) AS payments12
        ' + '
        , COALESCE(CAST(r.charges01 * COALESCE(cr.period01, 1) AS numeric(15,2)), 0) AS charges01
        , COALESCE(CAST(r.charges02 * COALESCE(cr.period02, 1) AS numeric(15,2)), 0) AS charges02
        , COALESCE(CAST(r.charges03 * COALESCE(cr.period03, 1) AS numeric(15,2)), 0) AS charges03
        , COALESCE(CAST(r.charges04 * COALESCE(cr.period04, 1) AS numeric(15,2)), 0) AS charges04
        , COALESCE(CAST(r.charges05 * COALESCE(cr.period05, 1) AS numeric(15,2)), 0) AS charges05
        , COALESCE(CAST(r.charges06 * COALESCE(cr.period06, 1) AS numeric(15,2)), 0) AS charges06
        , COALESCE(CAST(r.charges07 * COALESCE(cr.period07, 1) AS numeric(15,2)), 0) AS charges07
        , COALESCE(CAST(r.charges08 * COALESCE(cr.period08, 1) AS numeric(15,2)), 0) AS charges08
        , COALESCE(CAST(r.charges09 * COALESCE(cr.period09, 1) AS numeric(15,2)), 0) AS charges09
        , COALESCE(CAST(r.charges10 * COALESCE(cr.period10, 1) AS numeric(15,2)), 0) AS charges10
        , COALESCE(CAST(r.charges11 * COALESCE(cr.period11, 1) AS numeric(15,2)), 0) AS charges11
        , COALESCE(CAST(r.charges12 * COALESCE(cr.period12, 1) AS numeric(15,2)), 0) AS charges12
        ' + '
        , COALESCE(CAST(r.charges01 * COALESCE(cr.period01, 1) * COALESCE(vr.factor, 0) AS numeric(15,2)), 0) AS vat01
        , COALESCE(CAST(r.charges02 * COALESCE(cr.period02, 1) * COALESCE(vr.factor, 0) AS numeric(15,2)), 0) AS vat02
        , COALESCE(CAST(r.charges03 * COALESCE(cr.period03, 1) * COALESCE(vr.factor, 0) AS numeric(15,2)), 0) AS vat03
        , COALESCE(CAST(r.charges04 * COALESCE(cr.period04, 1) * COALESCE(vr.factor, 0) AS numeric(15,2)), 0) AS vat04
        , COALESCE(CAST(r.charges05 * COALESCE(cr.period05, 1) * COALESCE(vr.factor, 0) AS numeric(15,2)), 0) AS vat05
        , COALESCE(CAST(r.charges06 * COALESCE(cr.period06, 1) * COALESCE(vr.factor, 0) AS numeric(15,2)), 0) AS vat06
        , COALESCE(CAST(r.charges07 * COALESCE(cr.period07, 1) * COALESCE(vr.factor, 0) AS numeric(15,2)), 0) AS vat07
        , COALESCE(CAST(r.charges08 * COALESCE(cr.period08, 1) * COALESCE(vr.factor, 0) AS numeric(15,2)), 0) AS vat08
        , COALESCE(CAST(r.charges09 * COALESCE(cr.period09, 1) * COALESCE(vr.factor, 0) AS numeric(15,2)), 0) AS vat09
        , COALESCE(CAST(r.charges10 * COALESCE(cr.period10, 1) * COALESCE(vr.factor, 0) AS numeric(15,2)), 0) AS vat10
        , COALESCE(CAST(r.charges11 * COALESCE(cr.period11, 1) * COALESCE(vr.factor, 0) AS numeric(15,2)), 0) AS vat11
        , COALESCE(CAST(r.charges12 * COALESCE(cr.period12, 1) * COALESCE(vr.factor, 0) AS numeric(15,2)), 0) AS vat12
    FROM
        e4 s
        INNER JOIN dbo27.budget_requests r ON r.id = s.id
        INNER JOIN dbo27.members m1 ON m1.id = r.account_id
        LEFT OUTER JOIN dbo27.vat_rates vr ON vr.id = r.vat_rate_id
        LEFT OUTER JOIN dbo27.budget_currency_rates cr ON cr.category_id = r.category_id AND cr.time_id = r.time_id AND cr.unit_id = r.unit_id
)'

-- Transform payments, charges, and VAT to data01 - data12 depending on entry_id

SET @sql = @sql + '
, b1 AS (
    SELECT
