        , CASE WHEN entry_id = 1 THEN payments01 WHEN entry_id = 3 THEN vat01 ELSE charges01 - is_charge_net * vat01 END * value_sign AS data01
        , CASE WHEN entry_id = 1 THEN payments02 WHEN entry_id = 3 THEN vat02 ELSE charges02 - is_charge_net * vat02 END * value_sign AS data02
        , CASE WHEN entry_id = 1 THEN payments03 WHEN entry_id = 3 THEN vat03 ELSE charges03 - is_charge_net * vat03 END * value_sign AS data03
        , CASE WHEN entry_id = 1 THEN payments04 WHEN entry_id = 3 THEN vat04 ELSE charges04 - is_charge_net * vat04 END * value_sign AS data04
        , CASE WHEN entry_id = 1 THEN payments05 WHEN entry_id = 3 THEN vat05 ELSE charges05 - is_charge_net * vat05 END * value_sign AS data05
        , CASE WHEN entry_id = 1 THEN payments06 WHEN entry_id = 3 THEN vat06 ELSE charges06 - is_charge_net * vat06 END * value_sign AS data06
        , CASE WHEN entry_id = 1 THEN payments07 WHEN entry_id = 3 THEN vat07 ELSE charges07 - is_charge_net * vat07 END * value_sign AS data07
        , CASE WHEN entry_id = 1 THEN payments08 WHEN entry_id = 3 THEN vat08 ELSE charges08 - is_charge_net * vat08 END * value_sign AS data08
        , CASE WHEN entry_id = 1 THEN payments09 WHEN entry_id = 3 THEN vat09 ELSE charges09 - is_charge_net * vat09 END * value_sign AS data09
        , CASE WHEN entry_id = 1 THEN payments10 WHEN entry_id = 3 THEN vat10 ELSE charges10 - is_charge_net * vat10 END * value_sign AS data10
        , CASE WHEN entry_id = 1 THEN payments11 WHEN entry_id = 3 THEN vat11 ELSE charges11 - is_charge_net * vat11 END * value_sign AS data11
        , CASE WHEN entry_id = 1 THEN payments12 WHEN entry_id = 3 THEN vat12 ELSE charges12 - is_charge_net * vat12 END * value_sign AS data12
    FROM
        a1 s
)'

SET @sql = @sql + '
, b2 AS (
    SELECT
        s.id
        , m4.code AS entity
        , r.description
        , m1.code AS account
        , m7.name AS sub_account
        , m5.code AS region
        , m6.code AS product
        , t.name AS vat
        , u.name AS unit
        , CASE WHEN s.entry_side_id = 1 THEN a.code ELSE c.code END AS debit
        , CASE WHEN s.entry_side_id = 1 THEN c.code ELSE a.code END AS credit
        , CASE s.entry_id WHEN 1 THEN ''Payments'' WHEN 2 THEN ''Charges'' WHEN 3 THEN ''VAT'' ELSE NULL END AS section
        , data00, data01, data02, data03, data04, data05, data06, data07, data08, data09, data10, data11, data12
        , COALESCE(users.name, r.modified_by) AS last_user
        , r.modified_on AS last_update
        , m4.sort_order AS entity_sort_order
        , r.row_index
        , s.entry_id
        , s.entry_side_id
    FROM
        b1 s
        INNER JOIN dbo27.budget_requests r ON r.id = s.id
        INNER JOIN dbo27.ledger_accounts a ON a.id = s.ledger_account_id
        INNER JOIN dbo27.ledger_accounts c ON c.id = s.pair_account_id
        INNER JOIN dbo27.members m1 ON m1.id = r.account_id
        INNER JOIN dbo27.members m4 ON m4.id = r.[entity_id]
        LEFT OUTER JOIN dbo27.members m5 ON m5.id = r.region_id
        LEFT OUTER JOIN dbo27.members m6 ON m6.id = r.product_id
        LEFT OUTER JOIN dbo27.members m7 ON m7.id = r.subsidiary_account_id
        LEFT OUTER JOIN dbo27.vat_rates t ON t.id = r.vat_rate_id
        LEFT OUTER JOIN dbo27.units u ON u.id = r.unit_id
        LEFT OUTER JOIN dbo27.users users ON users.company_id = m1.company_id AND users.username = r.modified_by
)'

IF NOT (@value_type_id = 1 AND @total_type_id = 1) AND @entity_id IS NULL
    SET @sql = @sql + '
, f1 AS (
    SELECT
        NULL AS id
        , NULL AS entity
        , ''' + N'Opening Balance' + ''' AS description
        , NULL AS account
        , NULL AS sub_account
        , NULL AS region
        , NULL AS product
        , NULL AS vat
        , NULL AS unit
        , CASE WHEN s.id = 1 THEN a.code ELSE NULL END AS debit
        , CASE WHEN s.id = 1 THEN NULL ELSE a.code END AS credit
        , NULL AS section
        , CASE WHEN s.id = 1 THEN r.debit ELSE r.credit END AS data00
        , NULL AS data01, NULL AS data02, NULL AS data03, NULL AS data04, NULL AS data05, NULL AS data06, NULL AS data07, NULL AS data08, NULL AS data09, NULL AS data10, NULL AS data11, NULL AS data12
        , COALESCE(users.name, r.modified_by) AS last_user
        , r.modified_on AS last_update
