    IF @index = 0
        SET @sql = @sql + '
        NOT COALESCE(data00, 0) = 0 OR NOT COALESCE(data01, 0) = 0 OR NOT COALESCE(data02, 0) = 0 OR NOT COALESCE(data03, 0) = 0 OR NOT COALESCE(data04, 0) = 0 OR NOT COALESCE(data05, 0) = 0 OR NOT COALESCE(data06, 0) = 0 OR NOT COALESCE(data07, 0) = 0 OR NOT COALESCE(data08, 0) = 0 OR NOT COALESCE(data09, 0) = 0 OR NOT COALESCE(data10, 0) = 0 OR NOT COALESCE(data11, 0) = 0 OR NOT COALESCE(data12, 0) = 0'
    ELSE
        SET @sql = @sql + '
        ' + SUBSTRING('NOT COALESCE(data00, 0) = 0 OR NOT COALESCE(data01, 0) = 0 OR NOT COALESCE(data02, 0) = 0 OR NOT COALESCE(data03, 0) = 0 OR NOT COALESCE(data04, 0) = 0 OR NOT COALESCE(data05, 0) = 0 OR NOT COALESCE(data06, 0) = 0 OR NOT COALESCE(data07, 0) = 0 OR NOT COALESCE(data08, 0) = 0 OR NOT COALESCE(data09, 0) = 0 OR NOT COALESCE(data10, 0) = 0 OR NOT COALESCE(data11, 0) = 0 OR NOT COALESCE(data12, 0) = 0', 1, @index + 13)
    END

-- Build the ORDER BY clause

SET @sql = @sql + '
