CREATE PROCEDURE [xls27].[xl_actions_select_members]
    @company_id int = NULL
    , @data_language varchar(10) = NULL
AS
BEGIN

SET NOCOUNT ON

IF @company_id IS NULL
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE username = USER_NAME() AND is_active = 1 AND is_default = 1)
ELSE
    SET @company_id = (SELECT TOP 1 company_id FROM dbo27.users WHERE company_id = @company_id AND username = USER_NAME() AND is_active = 1)

SELECT
    d.name AS dimension
    , m.code
    , COALESCE(tr.name, m.name) AS name
FROM
    dbo27.members m
    INNER JOIN dbo27.dimensions d ON d.id = m.dimension_id
    LEFT OUTER JOIN dbo27.member_permissions mp ON mp.member_id = m.id AND mp.username = USER_NAME()
    LEFT OUTER JOIN dbo27.translations tr ON tr.company_id = m.company_id AND tr.table_id = 2 AND tr.member_id = m.id AND tr.language = @data_language
WHERE
    m.is_active = 1
    AND m.company_id = @company_id
    AND (COALESCE(mp.update_permission, 0) = 1 OR COALESCE(mp.select_permission, 0) = 1 OR NOT EXISTS (
        SELECT
            p.name
        FROM
            sys.database_principals p
            INNER JOIN sys.database_role_members rm ON rm.member_principal_id = p.principal_id
        WHERE
            p.name = USER_NAME()
            AND rm.role_principal_id = USER_ID('budgeting_app_users')
        ))
ORDER BY
    d.id
    , m.sort_order
    , m.code
    , m.name

END


